struct ray {
    vec3 origin;
    vec3 direction;
};

struct material {
    vec3 color;
    int mat_type;
    float mat_dep_var;
    float fuzz;
    float ir;
};

struct hitRecord{
    vec3 point;
    vec3 normal;
    float t;
    bool front;
    material mat;
};

bool zero(vec3 direction){
    float minNum = 1e-8;
    if (abs(direction.x) < minNum && abs(direction.y) < minNum && abs(direction.z) < minNum){
        return true;
    }
    return false;
}

bool lambertian(ray r, hitRecord rec, out vec3 attenuation, out ray scatteredRay, material mat){
    vec3 scatteredDir= rec.normal + normalize(random_in_unit_sphere(g_seed));

    if (zero(scatteredDir)){
        scatteredDir = rec.normal;    
    }
    scatteredRay = ray(rec.point, scatteredDir);
    attenuation = mat.color;
    return true;
}

vec3 reflect(vec3 v, vec3 n) {
    return v - 2.*dot(v,n)*n;
}

vec3 refract(vec3 uv, vec3 n, float etai_over_etat){
    float cosTheta = min(dot(uv*-1.0, n), 1.0);
    vec3 rOutPerp =  etai_over_etat * (uv + cosTheta*n);
    vec3 rOutParallel = -sqrt(abs(1.0 - length(rOutPerp)*length(rOutPerp))) * n;
    return rOutPerp + rOutParallel;
}

float reflectance(float cosine, float refIdx){
    float r0 = (1.-refIdx) / (1.+refIdx);
    float r = r0 * r0;
    return r + (1.-r)*pow((1.-cosine),5.);
}

bool dielectric(material m, ray r_in, hitRecord rec, out vec3 attenuation, out ray scattered){
    attenuation = vec3(1.,1.,1.);
    float refractionRatio = m.ir;
    if (rec.front){
        refractionRatio = 1.0 / m.ir;
    }
    vec3 unitDirection = normalize(r_in.direction);

    float cosTheta = min(dot(-unitDirection, rec.normal), 1.0);
    float sinTheta = sqrt(1.0 - cosTheta*cosTheta);
    bool cannotRefract = refractionRatio * sinTheta > 1.0;
    vec3 direction;
    if (cannotRefract || reflectance(cosTheta, refractionRatio) > rand1(g_seed)){
        direction = reflect(unitDirection, rec.normal);
    }
    else{
        direction = refract(unitDirection, rec.normal, refractionRatio);
    }
    scattered = ray(rec.point, direction);
    return true;
}

struct camera{
    float aspect_ratio;
    float viewport_height;
    float viewport_width;
    float focal_length;

    vec3 origin;
    vec3 horizontal;
    vec3 vertical;
    vec3 lower_left;
};

struct sphereStruct {
    vec3 center;
    float radius;
    material mat;
};

bool metal(material mat, ray rIn, hitRecord rec, out vec3 attenuation, out ray scattered) {
    float f = min(mat.fuzz, 1.0);
    vec3 reflected = reflect(normalize(rIn.direction), rec.normal);
    scattered = ray(rec.point, reflected + f*random_in_unit_sphere(g_seed));
    attenuation = mat.color;
    bool test = (dot(scattered.direction, rec.normal) > 0.);
    return test;
}

bool materialScatter(material mat, ray rIn, hitRecord rec, out vec3 attenuation, out ray scattered) {
    if (mat.mat_type == 8){
        return lambertian(rIn, rec, attenuation, scattered, mat);
    }
    else if (mat.mat_type == 9){
        return metal(mat, rIn, rec, attenuation, scattered);
    }
    else if (mat.mat_type == 10){
        return dielectric(mat, rIn, rec, attenuation, scattered);
    } 
    else{
        return false;
    }
}

ray getRay(float u, float v, camera c){
    return ray(c.origin, c.lower_left + u*c.horizontal + v*c.vertical - c.origin);
}

void setFaceNormal(const ray r, vec3 normalOut, out hitRecord rec) {
    rec.front = dot(r.direction, normalOut) < 0.;
    if (rec.front){
        rec.normal = normalOut;
    }
    else{
        rec.normal = -normalOut;
    }
}
vec3 pointAt(float t, ray r)  {
    return r.origin + t*r.direction;
}


bool sphere(ray r, float min_t, float max_t, out hitRecord temp, sphereStruct s) {
    vec3 oc = r.origin - s.center;
    float dir2 = length(r.direction)*length(r.direction);
    float oc_dir =dot(oc, r.direction);
    float oc_rad = length(oc) * length(oc) - s.radius*s.radius;
    float discriminant = oc_dir*oc_dir - dir2*oc_rad;

    if (discriminant < 0.) return false;

    float sqrtd = sqrt(discriminant);

    float root = (-oc_dir - sqrtd) / dir2;
    if (root < min_t || max_t < root){
       return false;
    }

    temp.t = root;
    temp.point = pointAt(temp.t,r);
    temp.normal = (temp.point - s.center)/s.radius;
    vec3 outward_normal = (temp.point - s.center) / s.radius;
    setFaceNormal(r, outward_normal, temp);
    temp.mat = s.mat;
    return true;

}

bool hit(ray r, float minT, float maxT, inout hitRecord rec, sphereStruct s[9], int spheres) {
   hitRecord temp;
   bool hitCheck = false;
   float closest = maxT;

   for (int i=0; i < spheres; i++){
      if (sphere(r, minT, closest, temp, s[i])){
          hitCheck = true;
          closest = temp.t;
          rec = temp;
       }
   }
   return hitCheck;
}

bool rayColorHelper(out ray r, int depth, out vec3 result, out vec3 attenTotal) {
    if (depth <= 0){
        result = vec3(0., 0., 0.);
        return false;
    }
    const int spheres = 9;
    sphereStruct[spheres] sphereArray;
    sphereArray[0] = sphereStruct(vec3(0., -100.5, -1.), 100., material(vec3(0.1, 0.1, 0.1), 8, 0., 0., 0.));   
    sphereArray[1] = sphereStruct(vec3(-101.5, 0.0, -1.), 100., material(vec3(0.0, 0.9, 0.9), 8, 0., 0., 0.));   
    sphereArray[2] = sphereStruct(vec3(101.5, 0.0, -1.), 100., material(vec3(0., 0.9, 0.9), 8, 0., 0., 0.));
    sphereArray[3] = sphereStruct(vec3(0.0, 2.0, 120.), 100., material(vec3(0.,0.9,0.9), 8, 0., 0., 0.));
sphereArray[4] = sphereStruct(vec3(0.19,1.07,3.0),0.09, material(vec3(0.8, 0.8, 0.8),9, 0., 0.0, 1.)); 
sphereArray[5] = sphereStruct(vec3(-0.16,1.19,3.0),0.12, material(vec3(0.8, 0.8, 0.8),9, 0., 0.0, 1.)); 
sphereArray[6] = sphereStruct(vec3(0.19,1.17,3.0),0.18, material(vec3(0.8, 0.8, 0.8),9, 0., 0.0, 1.)); 
sphereArray[7] = sphereStruct(vec3(-0.42,1.12,3.0),0.19, material(vec3(0.8, 0.8, 0.8),9, 0., 0.0, 1.)); 
sphereArray[8] = sphereStruct(vec3(-0.18,0.7,3.0),0.14, material(vec3(0.8, 0.8, 0.8),9, 0., 0.0, 1.));

    hitRecord rec;
    if (hit(r, 0.001, 1e5, rec, sphereArray, spheres)){
        ray scattered;
        vec3 attenuation;
        if (materialScatter(rec.mat, r, rec, attenuation, scattered)){
            attenTotal *= attenuation;
            r = scattered;
            return true;
        }
        result = vec3(0.,0.,0.);
        return true;
    }

    vec3 dir = r.direction;
    float t = 0.5 *(dir.y + 1.);
    result = (1. - t)*vec3(1., 1., 1.) + t*vec3(0.5, 0.7, 1.);

    return false;

}

vec3 rayColor(ray r, int depth){
    vec3 result;
    float count = 0.;
    int d = depth;
    vec3 attenTotal = vec3(1.,1.,1.);

    for (;rayColorHelper(r, d, result, attenTotal); d--);
    return attenTotal*result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 lookfrom = vec3(0.0,0.5,-4.);
    vec3 lookat = vec3(0.,0.4,0.);
    vec3 vup = vec3(0.,1.,0.);

    float vertFieldOfView = 20.;
    float rads = vertFieldOfView * 3.14159265/180.;
    float h = tan(rads/2.0);

    camera c;
    c.aspect_ratio = 16.0 / 9.0;
    c.viewport_height = 2.0 * h;
    c.viewport_width = c.aspect_ratio * c.viewport_height;
    c.focal_length = 1.0;

    vec3 w = normalize(lookfrom - lookat);
    vec3 u = normalize(cross(vup,w));
    vec3 v = cross(w,u);


    c.origin = lookfrom;
    c.horizontal = c.viewport_width * u;
    c.vertical = c.viewport_height * v;
    c.lower_left = c.origin - (c.horizontal/2.) - (c.vertical/2.) - w;

    int samples_per_pixel = 40;
    const int max_depth = 20;
    vec3 finColor = vec3(0., 0., 0.);
    init_rand(fragCoord, iTime);

    for (int s = 0; s < samples_per_pixel; s++){
        float x = (fragCoord.x + rand1(g_seed))/ (float(iResolution.x) - 1.);
        float y = (fragCoord.y + rand1(g_seed))/ (float(iResolution.y) - 1.);
        ray r = getRay(x, y, c);
        vec3 color = rayColor(r, max_depth);
        finColor += color * (1./float(samples_per_pixel));
    }
    fragColor = vec4(sqrt(finColor), 1.0);
}

