// O.r : current image error
// O.b : current image best
// O.g : current image delta proposal
// O.a : integral error

#define hash(p)  fract(sin((p)* vec2(12.9898, 78.233 )) * 43758.5453)

void mainImage( out vec4 O, vec2 u )
{
    O = T(u);                      // restore previous state
    vec2  U = u/R.y;
    float m = textureLod(iChannel0, vec2(.5), 99.).r, // total image difference
          m0= O.a;                 // previous image difference
    if ( m < m0 ) O.b += O.g,      // if last draw improved, keep it.
                  O.a = m;         // store new image difference
    O.g = 0.;                      // proposal: modification to prev best
    for( float s,t,i = 0.; i < 1.; i++ ) {           // simulatenous tests (not independant: whole lot ok or not)
        t = iTime -7.1*i;                            // seed
        vec2 p = R/R.y*hash(t), r = hash(t+1.), d;   // random rectangle
        r = .2*r*r;                                  // size distribution
        s =  2.*hash(t+.5).y -1.;                    // random signed weight
                                                     // proposal splat:
     // d = r - abs(U-p); O.g += s* step( 0.,min(d.x,d.y)); //  rectangle
     // O.g += s* step( length(U-p) , r.x );         //         disc
        O.g += s* max(0., r.x - length(U-p) );       //         cone
     // O.g += s* smoothstep(r.x,0.,length(U-p) );   //         blob
    }
    O.r = abs ( O.b+O.g - texture(iChannel1,u/R).b );// error compare to reference
                                                     // top MIPmap LOD will integrate    
    if (iFrame<1) O = vec4(1e9, .5,0, 1e9); 
}

