#define MAX_STEPS 50
#define MAX_DIST 10.
#define SURF_DIST .005
#define TAU 6.283185
#define PI 3.141592
#define S smoothstep
#define T iTime

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }

float opU( float d1, float d2 )
{
	return min(d1, d2);
}

float opSub( float d1, float d2 ) { return max(-d1,d2); }

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float rounding( in float d, in float h )
{
    return d - h;
}

float sdBox2( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdCappedTorus(in vec3 p, in vec2 sc, in float ra, in float rb)
{
  p.x = abs(p.x);
  float k = (sc.y*p.x>sc.x*p.y) ? dot(p.xy,sc) : length(p.xy);
  return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

float sdCappedCylinder(vec3 p, vec3 a, vec3 b, float r)
{
  vec3  ba = b - a;
  vec3  pa = p - a;
  float baba = dot(ba,ba);
  float paba = dot(pa,ba);
  float x = length(pa*baba-ba*paba) - r*baba;
  float y = abs(paba-baba*0.5)-baba*0.5;
  float x2 = x*x;
  float y2 = y*y*baba;
  float d = (max(x,y)<0.0)?-min(x2,y2):(((x>0.0)?x2:0.0)+((y>0.0)?y2:0.0));
  return sign(d)*sqrt(abs(d))/baba;
}

float udTriangle( in vec3 v1, in vec3 v2, in vec3 v3, in vec3 p )
{
    vec3 v21 = v2 - v1; vec3 p1 = p - v1;
    vec3 v32 = v3 - v2; vec3 p2 = p - v2;
    vec3 v13 = v1 - v3; vec3 p3 = p - v3;
    vec3 nor = cross( v21, v13 );

    return sqrt( (sign(dot(cross(v21,nor),p1)) + 
                  sign(dot(cross(v32,nor),p2)) + 
                  sign(dot(cross(v13,nor),p3))<2.0) 
                  ?
                  min( min( 
                  dot2(v21*clamp(dot(v21,p1)/dot2(v21),0.0,1.0)-p1), 
                  dot2(v32*clamp(dot(v32,p2)/dot2(v32),0.0,1.0)-p2) ), 
                  dot2(v13*clamp(dot(v13,p3)/dot2(v13),0.0,1.0)-p3) )
                  :
                  dot(nor,p1)*dot(nor,p1)/dot2(nor) );
}

float GetDist(vec3 p, out float col) {
    const float hr = 0.707; // heart raduis
    const float tr = 0.07; // torus radius

    col = 1.;

    float isBeat = max(0., mod(floor(iTime*1.7),3.)-1.);
    float beat = abs(sin(iTime*TAU*1.7));
    float scale = 1. + isBeat * beat * .2;

    //float d = sdBox(p, vec3(1));
    // Top right arc
    vec3 p1 = p;
    p1.x += .5 * scale;
    p1.y -= .5 * scale;
    p1.xy *= Rot(-0.785);
    vec2 a = vec2(sin(1.57), cos(1.57));
    //a = vec2(sin(3.14), cos(3.14));
    float d1 = sdCappedTorus(p1, a, hr, tr);
    d1 = opU(d1, sdCapsule(p1, vec3(-hr, 0., 0.), vec3(hr, 0., 0.), tr));

    // Mid right triangle
    vec3 p2 = p;
    p2.x += 1. * scale;
    p2.y += (scale - 1.) * .5;
    float d2 = sdCapsule(p2, vec3(0), vec3(1, 0, 0), tr);
    d2 = opU(d2, sdCapsule(p2, vec3(0), vec3(1, 1, 0), tr));
    d2 = opU(d2, sdCapsule(p2, vec3(1, 1, 0), vec3(1, 0, 0), tr));

    // Bottom right triangle
    vec3 p3 = p;
    p3.x += 1. * scale;
    p3.y += 0. + scale - 1.;
    float d3 = sdCapsule(p3, vec3(0), vec3(1, 0, 0), tr);
    d3 = opU(d3, sdCapsule(p3, vec3(0), vec3(1, -1, 0), tr));
    d3 = opU(d3, sdCapsule(p3, vec3(1, -1, 0), vec3(1, 0, 0), tr));

    float rightHalf = opU(opU(d1, d2), d3);

    // Upper left pie
    vec3 p4 = p;
    p4.x -= .5 * scale;
    p4.y -= .5 * scale;
    p4.xy *= Rot(.785);
    float d4 = sdCappedCylinder(p4, vec3(0, 0, .001), vec3(0, 0, -.001), hr);
    d4 = opSub(sdBox2(p4+vec3(-0,.5,0), vec3(1, .5, .5)), d4);
    d4 = rounding(d4, tr-.005);

    // Lower left triangle
    vec3 p5 = p;
    p5 -= vec3(scale-1.,-scale+1., 0);
    float d5 = udTriangle(vec3(0), vec3(1, 0, 0), vec3(0, -1, 0), p5) - .001;
    d5 = rounding(d5, tr-.006);

    float leftHalf = opU(d4, d5);

    float d = min(rightHalf, leftHalf);

    if (d == d4) col = 2.;
    if (d == d5) col = 3.;

    return d;
}

float RayMarch(vec3 ro, vec3 rd, out float col) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p, col);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float col;
	float d = GetDist(p, col);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy, col),
        GetDist(p-e.yxy, col),
        GetDist(p-e.yyx, col)
    );

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

vec3 Render(inout vec3 ro, inout vec3 rd, inout float ref) {
    // Doing weird things to move the skybox to nice starting reflection and tint.
    // Straight skybox woud be texture(iChannel0, rd).rgb, tuned to not over expose color.
    vec3 skybox = texture(iChannel0, vec3(rd.xz*Rot(PI*-.25), rd.y)).rbg * vec3(.3, .6, .5);
    // background tint + slight skybox
    vec3 col = vec3(.2, .266, .694) + skybox;

    // Get distance and color, TODO: move color to vec2 return type
    float colIndex = 0.;
    float d = RayMarch(ro, rd, colIndex);

    // Make a nice color if we hit
    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        // Super basic lighting
        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;

        // Color and reflectiveness
        if (colIndex == 1.) {
            col = vec3(.8)*dif;
            ref = .35;
        }
        else if (colIndex == 2.) {
            col = vec3(.96, .756, .737)*dif;
            ref = .35;
        }
        else if (colIndex == 3.) {
            col = vec3(.615, .5, .341)*dif;
            ref = .25;
        }

        // Adjust direction to reflect
        ro = p+n*SURF_DIST*3.;
        rd = r;
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;
    vec3 ro = vec3(0, 3.2, 3);
    if (iMouse.z > 0. && (m.x > 0. || m.y > 0.)) {
        m -= .5;
        m.y *= -1.;
        ro.yz *= Rot(-m.y*PI+1.);
        ro.xz *= Rot(-m.x*TAU);
    } else {
        ro.y = -1.4;
        ro.xz *= Rot(iTime*.3);
    }
    ro = (normalize(ro)*6.);

    uv.y += .03;

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), 2.);

    float ref = 0.;
    vec3 col = Render(ro, rd, ref);
    vec3 bounce = ref * Render(ro, rd, ref);
    col += bounce;

    col = pow(col, vec3(1.3));	// gamma correction

    fragColor = vec4(col,1.0);
}

//Creating the map of the whole world of SDFs
float map(vec3 p, settings setts)
{
    return world_sdf(p, iTime, setts);
}


// Computing the normals
vec3 computeNormal(vec3 p, settings setts)
{
    const float h = 1e-4;
    float dist = world_sdf(p, iTime, setts);  
    float distx = world_sdf(p+vec3(h,0.,0.), iTime, setts) - dist;
    float disty = world_sdf(p+vec3(0.,h,0.), iTime, setts) - dist;
    float distz = world_sdf(p+vec3(0.,0.,h), iTime, setts) - dist;
    vec3 un_norm = vec3(distx, disty, distz);
    return normalize(un_norm);
}

//Sphere tracing
bool sphere_tracing(ray r,
               		int max_iter,
               		settings setts,
               		out vec3 hit_loc,
               		out int iters)
{
    hit_loc = r.origin + r.direction * (-r.origin.y / r.direction.y);
    iters = 1;

    float step_size;
    vec3 old_loc = r.origin;
    int curr_iters = 1;
    while (curr_iters < max_iter){
        step_size = world_sdf(old_loc, iTime, setts);
        vec3 curr_loc = old_loc + step_size*r.direction;
        if (step_size < 1e-4){
            hit_loc = curr_loc;
            iters = curr_iters;
            return true;
        }
        old_loc = curr_loc;
        curr_iters += 1;
    }
    iters = max_iter;
    return false;
}


vec3 shade(ray r, int iters, settings setts)
{
    vec3 p = r.origin;
    vec3 d = r.direction;

    if (setts.shade_mode == GRID)
    {
    	float res = 0.2;
    	float one = abs(mod(p.x, res) - res / 2.0);
    	float two = abs(mod(p.y, res) - res / 2.0);
    	float three = abs(mod(p.z, res) - res / 2.0);
    	float interp = min(one, min(two, three)) / res;

        return mix( vec3(0.2, 0.5, 1.0), vec3(0.1, 0.1, 0.1), smoothstep(0.0,0.05,abs(interp)) );
    }
    else if (setts.shade_mode == COST)
    {
        return vec3(float(iters) / float(cost_norm));
    }
    else if (setts.shade_mode == NORMAL)
    {
        vec3 normal = computeNormal(p, setts);
        vec3 calc = vec3((normal.x+1.0)/2.0, (normal.y+1.0)/2.0, (normal.z+1.0)/2.0);
        return calc;
    }
    else if (setts.shade_mode == DIFFUSE_POINT)
    {
        float r = abs(cos(iTime * 0.2));
        float g = abs(sin(iTime * 0.2));
        float b = abs(tan(iTime * 0.2));

        //static light source
        vec3 light_pos = vec3(0.0, 7., -2.);

        //moving light source
        //vec3 light_pos = vec3(r, 4. + g, b);

        vec3 light_intensity = vec3(20.0);
        vec3 surface_color = vec3(0.0,0.8,0.9);

        //Changing color
        //vec3 surface_color = vec3(r, g, b);

        vec3 normal = computeNormal(p, setts);
        vec3 Li = light_intensity/dot(light_pos-p, light_pos-p);
        vec3 color = surface_color*max(0.0, dot(normalize(light_pos-p), normalize(normal)))*Li;
        return color;
    }
    else
    {
        return vec3(0.0);
    }

    return vec3(0.0);
}


vec3 render(settings setts, vec2 fragCoord)
{
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    if (p.y < -0.95)
    {
        float val = cos(iTime * anim_speed);
