// pre-defined constants
#define EPSILON 1e-4
#define PI 3.1415926535897932384626433832795


// shade mode
#define GRID 0
#define COST 1
#define NORMAL 2
#define AMBIENT 3
#define DIFFUSE_POINT 4
#define ENVIRONMENT_MAP 5


// Render Settings

struct settings
{
    int shade_mode;
};

settings render_settings = settings(NORMAL);

float anim_speed = 0.35;
int cost_norm = 200;

vec3 two_tone_color_a = vec3(0.6 * 1.0, 0.6 * 1.0, 0.0 * 1.0);
vec3 two_tone_color_b = vec3(0.2 * 1.0, 0.1 * 1.0, 0.9 * 1.0);
vec3 two_tone_dir = vec3(1.0, 1.0, 0.0);

//SDFs -- All reused from previous assignments

// returns the signed distance to a sphere from position p
float sdSphere(vec3 p, float r)
{
 	return length(p) - r;
}

// returns the signed distance to a box from position p
float sdBox( vec3 p, vec3 half_bounds )
{
    vec3 dist = abs(p) - half_bounds;
    float max_dist = max(dist.x,max(dist.y,dist.z));
    float inside = min(max_dist, 0.0);
    float outside = length(max(dist, 0.0));
    return inside + outside;
}

// returns the signed distance to a cylinder from position p
float sdCylinder(vec3 p, vec3 a, vec3 b, float r)
{
    vec3  b_minus_a = b - a;
    vec3  p_minus_a = p - a;
    float ba_ba = dot(b_minus_a,b_minus_a);
    float pa_ba = dot(p_minus_a,b_minus_a);
    float x = length(p_minus_a*ba_ba-b_minus_a*pa_ba) - r*ba_ba;
    float y = abs(pa_ba-ba_ba*0.5)-ba_ba*0.5;
    float x2 = x*x;
    float y2_ba = y*y*ba_ba;
    float dist = 0.0;
    if (max(x,y)<0.0){
        dist = -min(x2,y2_ba);
    }
    else{
        if (x>0.0){
            dist = x2;
        }
        if (y>0.0){
            dist += y2_ba;
        }
    }
    return sign(dist)*sqrt(abs(dist))/ba_ba;
}

// returns the signed distance to a cone from position p
float sdCone(vec3 p, vec3 a, vec3 b, float ra, float rb)
{
    vec3  b_minus_a = b - a;
    vec3  p_minus_a = p - a;
    float rb_minus_ra  = rb-ra;
    float ba_dot = dot(b_minus_a,b_minus_a);
    float pa_dot = dot(p_minus_a,p_minus_a);
    float pa_ba = dot(p_minus_a,b_minus_a)/ba_dot;
    float x = sqrt( pa_dot - pa_ba*pa_ba*ba_dot );

    float radius = 0.0;
    if (pa_ba<0.5){
        radius = ra;
    }
    else{
        radius = rb;
    }
    float x_mag = max(0.0,x-radius);
    float y_mag = abs(pa_ba-0.5)-0.5;
    float k = rb_minus_ra*rb_minus_ra + ba_dot;
    float f = max((rb_minus_ra*(x-ra)+pa_ba*ba_dot)/k, 0.0);
    f = min(f,1.0);
    float x_mag1 = x-ra - f*rb_minus_ra;
    float y_mag1 = pa_ba - f;
    float ori = 0.0;
    if (x_mag1<0.0 && y_mag<0.0){
        ori = -1.0;
    }
    else{
        ori = 1.0;
    }
    float xx = x_mag*x_mag;
    float yy = y_mag*y_mag;
    float xx1 = x_mag1*x_mag1;
    float yy1 = y_mag1*y_mag1;

    float squared = min(xx + yy*ba_dot, xx1 + yy1*ba_dot);
    return ori*sqrt(squared);
}

//Boolean Set Operations - All reused from previous assignments

float opSmoothUnion(float d1, float d2, float k)
{
    float h = max(k-abs(d1-d2), 0.0);
    float s_union = min(d1, d2) - (h*h)/(4.0*k);
    return s_union;
}

float opSmoothSubtraction(float d1, float d2, float k)
{
    float h = max(k-abs(-d1-d2), 0.0);
    float s_subtraction = max(-d1, d2) + (h*h)/(4.0*k);
    return s_subtraction;
}

float opSmoothIntersection( float d1, float d2, float k )
{
    float h = max(k-abs(d1-d2), 0.0);
    float s_intersection = max(d1, d2) + (h*h)/(4.0*k);
    return s_intersection;
}

float sdTorus( vec3 p, vec2 t )
{
  float len = length(p.xz)-t.x;
  vec2 q = vec2(len,p.y);
  return length(q)-t.y;
}


