////////////////////////////////////////////////////////////////////////////////
//
// Lattice of Forking Paths
// Matthew Arcus, mla, 2022
//
// Based on https://www.shadertoy.com/view/tsXBzs, but instead of every face
// leading back to the same cube, every face leads to a completely different
// cube, with each path leading to a different cube.
//
// We need to keep track of the path through the lattice, this is done with
// an iterated hash (so we're using blockchain technology!). The hash is a
// simple Feistel network so moving through a face one way undoes the effect
// of moving through the other way.
//
// Navigation is just moving on a line. Mouse changes view direction.
// Repeats every 60 seconds to avoid excessive time computing the start
// cube (I should store this in a texture).
//
// Inspired by https://www.shadertoy.com/view/7dlyW7 by SchnitzelKiller.
//
////////////////////////////////////////////////////////////////////////////////

const float PI = 3.1415927;
float maxdist = 50.0;
int maxiterations = 100;

bool alert = false;
void assert(bool b) {
  if (!b) alert = true;
}

float stepsize(vec3 p, vec3 r) {
  // Want least k such that p + kr has coordinate 1 in some dimension
  // eg. p.x+kr.x = 1 => k = (1-p.x)/r.x
  // eg. p.x+kr.x = 0 => k = -p.x/r.x
  // Want k positive, so assuming p.x is 0 < p.x < 1, depends on r.
  float k = 1e8;
  if (r.x > 0.0) k = min(k,(1.0-p.x)/r.x);
  else if (r.x < 0.0) k = min(k,-p.x/r.x);
  if (r.y > 0.0) k = min(k,(1.0-p.y)/r.y);
  else if (r.y < 0.0) k = min(k,-p.y/r.y);
  if (r.z > 0.0) k = min(k,(1.0-p.z)/r.z);
  else if (r.z < 0.0) k = min(k,-p.z/r.z);
  return k;
}

// Having moved to a face & just beyond, work out which face
// we have hit. What happens if we are close to the edge & in
// fact have passed through 2 faces?
int gethitface(vec3 p) {
  if (p.x > 1.0) return 0;
  if (p.x < 0.0) return 1;
  if (p.y > 1.0) return 2;
  if (p.y < 0.0) return 3;
  if (p.z > 1.0) return 4;
  if (p.z < 0.0) return 5;
  return -1;
}

void nextpos(int hitface, inout vec3 p) {
  if (hitface == 0) p.x -= 1.0;
  if (hitface == 1) p.x += 1.0;
  if (hitface == 2) p.y -= 1.0;
  if (hitface == 3) p.y += 1.0;
  if (hitface == 4) p.z -= 1.0;
  if (hitface == 5) p.z += 1.0;
}

// From Chris Wellons: https://nullprogram.com/blog/2018/07/31/
uint ihash(uint x) {
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return x;
}

uvec2 update(uvec2 type, int hitface) {
  uint k = uint((hitface>>1)+1);
  uint n0 = type[0];
  uint n1 = type[1];
  if ((hitface&1) == 0) {
    n0 ^= ihash(n1^k);
    n1 ^= ihash(n0^k);
  } else {
    n1 ^= ihash(n0^k);
    n0 ^= ihash(n1^k);
  }
  return uvec2(n0,n1);
}

bool traceray(inout vec3 p, inout vec3 r, out int hitface, inout uvec2 type, out float totaldist) {
  totaldist = 0.0;
  vec3 p0 = p;
  for (int i = 0; i < maxiterations; i++) {
    assert(i+1 < maxiterations);
    float eps = 1e-3;
    float k = stepsize(p,r)+eps;
    p += k*r;
    totaldist += k;
    if (totaldist > maxdist) return false;
    // determine the hit face
    hitface = gethitface(p);
    type = update(type,hitface);
    vec3 border = min(p,1.0-p);
    // Have we hit the face frames?
    float framewidth = 0.05;
    if (true) {
      if (hitface/2 == 0 && min(border.y,border.z) < framewidth) return true;
      if (hitface/2 == 1 && min(border.z,border.x) < framewidth) return true;
      if (hitface/2 == 2 && min(border.x,border.y) < framewidth) return true;
    }
    // Advance p
    nextpos(hitface,p);
  }
  assert(false); // We should have enough iterations to get to maxdist
  return false;
}

void moveforward(inout vec3 p, vec3 dir, float t, inout uvec2 type) {
  for (int i = 0; i < 50; i++) {
    assert(i < 49);
    float k = stepsize(p,dir) ;
    float eps = 1e-3;
    if (t <= k) {
      p += t*dir;
      return;
    }
    k += eps;
    p += k*dir;
    t -= k;
    int hitface = gethitface(p);
    type = update(type,hitface);
    nextpos(hitface,p);
  }
  assert(false);
}

vec3 bgcol = vec3(1,1,0.5);

vec3 colors[] =
  vec3[](vec3(1,0,0),
         vec3(0,1,0),
         vec3(0,0,1),
         vec3(1,1,0),
         vec3(1,0,1),
         vec3(0,1,1));

vec3 getcolor(uint type) {
  type = ihash(type);
  return colors[type%uint(colors.length())];
}

vec3 normal(int hitface) {
  vec3 n = vec3(0);
  n[hitface>>1] = 1.0;
  return n;
}

vec3 raycolor(vec3 p, vec3 r, uvec2 type) {
  float totaldist;
  int hitface;
  if (!traceray(p,r,hitface,type,totaldist)) return bgcol;
  vec3 color = 0.8*getcolor(type[0]^type[1]);
  vec3 light = normalize(vec3(0,1,-0.5));
  vec3 n = normal(hitface);
  if (dot(n,r) > 0.0) n = -n;
  color *= 0.4+0.4*clamp(dot(light,n),0.0,1.0);
  color = mix(color,bgcol,totaldist/maxdist);
  return color;
}

vec2 rotate(vec2 p, float t) {
  return mat2(cos(t),sin(t),-sin(t),cos(t))*p;
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = -(2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = -(2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (false) {
    p.zx = rotate(p.zx,iTime * 0.2);
    p.xy = rotate(p.xy,iTime * 0.125);
  }
  return p;
}

