#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define TAU 6.283185
#define PI 3.141592
#define S smoothstep
#define T iTime

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }

float opU( float d1, float d2 )
{
	return min(d1, d2);
}

float opSub( float d1, float d2 ) { return max(-d1,d2); }

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float rounding( in float d, in float h )
{
    return d - h;
}

float sdBox2( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdCappedTorus(in vec3 p, in vec2 sc, in float ra, in float rb)
{
  p.x = abs(p.x);
  float k = (sc.y*p.x>sc.x*p.y) ? dot(p.xy,sc) : length(p.xy);
  return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

float sdCappedCylinder(vec3 p, vec3 a, vec3 b, float r)
{
  vec3  ba = b - a;
  vec3  pa = p - a;
  float baba = dot(ba,ba);
  float paba = dot(pa,ba);
  float x = length(pa*baba-ba*paba) - r*baba;
  float y = abs(paba-baba*0.5)-baba*0.5;
  float x2 = x*x;
  float y2 = y*y*baba;
  float d = (max(x,y)<0.0)?-min(x2,y2):(((x>0.0)?x2:0.0)+((y>0.0)?y2:0.0));
  return sign(d)*sqrt(abs(d))/baba;
}

float udTriangle( in vec3 v1, in vec3 v2, in vec3 v3, in vec3 p )
{
    vec3 v21 = v2 - v1; vec3 p1 = p - v1;
    vec3 v32 = v3 - v2; vec3 p2 = p - v2;
    vec3 v13 = v1 - v3; vec3 p3 = p - v3;
    vec3 nor = cross( v21, v13 );

    return sqrt( (sign(dot(cross(v21,nor),p1)) + 
                  sign(dot(cross(v32,nor),p2)) + 
                  sign(dot(cross(v13,nor),p3))<2.0) 
                  ?
                  min( min( 
                  dot2(v21*clamp(dot(v21,p1)/dot2(v21),0.0,1.0)-p1), 
                  dot2(v32*clamp(dot(v32,p2)/dot2(v32),0.0,1.0)-p2) ), 
                  dot2(v13*clamp(dot(v13,p3)/dot2(v13),0.0,1.0)-p3) )
                  :
                  dot(nor,p1)*dot(nor,p1)/dot2(nor) );
}

float GetDist(vec3 p, out float col) {
    const float hr = 0.707; // heart raduis
    const float tr = 0.07; // torus radius

    col = 1.;

    //float d = sdBox(p, vec3(1));
    // Top right arc
    vec3 p1 = p;
    p1.x += .5;
    p1.y -= .5;
    p1.xy *= Rot(-0.785);
    vec2 a = vec2(sin(1.57), cos(1.57));
    //a = vec2(sin(3.14), cos(3.14));
    float d1 = sdCappedTorus(p1, a, hr, tr);
    d1 = opU(d1, sdCapsule(p1, vec3(-hr, 0., 0.), vec3(hr, 0., 0.), tr));

    // Mid right triangle
    vec3 p2 = p;
    p2.x += 1.;
    p2.y += 0.;
    float d2 = sdCapsule(p2, vec3(0), vec3(1, 0, 0), tr);
    d2 = opU(d2, sdCapsule(p2, vec3(0), vec3(1, 1, 0), tr));
    d2 = opU(d2, sdCapsule(p2, vec3(1, 1, 0), vec3(1, 0, 0), tr));


    // Bottom right triangle
    vec3 p3 = p;
    p3.x += 1.;
    p3.y += 0.;
    float d3 = sdCapsule(p3, vec3(0), vec3(1, 0, 0), tr);
    d3 = opU(d3, sdCapsule(p2, vec3(0), vec3(1, -1, 0), tr));
    d3 = opU(d3, sdCapsule(p2, vec3(1, -1, 0), vec3(1, 0, 0), tr));

    float rightHalf = opU(opU(d1, d2), d3);

    // Upper left pie
    vec3 p4 = p;
    p4.x -= .5;
    p4.y -= .5;
    p4.xy *= Rot(.785);
    float d4 = sdCappedCylinder(p4, vec3(0, 0, .001), vec3(0, 0, -.001), hr);
    d4 = opSub(sdBox2(p4+vec3(-0,.5,0), vec3(1, .5, .5)), d4);
    d4 = rounding(d4, tr-.005);

    // Lower left triangle
    vec3 p5 = p;
    float d5 = udTriangle(vec3(0), vec3(1, 0, 0), vec3(0, -1, 0), p5) - .001;
    d5 = rounding(d5, tr-.006);

    float leftHalf = opU(d4, d5);

    float d = min(rightHalf, leftHalf);

    if (d == d4) col = 2.;
    if (d == d5) col = 3.;

    return d;
}

float RayMarch(vec3 ro, vec3 rd, out float col) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p, col);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float col;
	float d = GetDist(p, col);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy, col),
        GetDist(p-e.yxy, col),
        GetDist(p-e.yyx, col)
    );

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;
    if (m.x > 0. || m.y > 0.) {
        m -= .5;
        m.y *= -1.;
    }

    vec3 ro = vec3(0, 2, 2);
    ro.yz *= Rot(-m.y*PI+1.);
    ro.xz *= Rot(-m.x*TAU);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), 1.);
    vec3 col = vec3(.2, .266, .694);

    float colIndex = 0.;
    float d = RayMarch(ro, rd, colIndex);

    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;

        if (colIndex == 1.)
            col = vec3(colIndex);
        else if (colIndex == 2.)
            col = vec3(.96, .756, .737);
        else if (colIndex == 3.)
            col = vec3(.615, .5, .341);

        col = col * dif;
    }

    //col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

