// License CC0: Hexler hexes
//  I like Hexler's kodelife. When I go to their homepage they often
//  have an image of repeated hexes. 
//  This is the result of short hack to try to recreate it


#define TIME        iTime
#define RESOLUTION  iResolution
#define PI          3.141592654
#define TAU         (2.0*PI)

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}
// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
//  Macro version of above to enable compile-time constants
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

// License: Unknown, author: Unknown, found: don't remember
float hash(float co) {
  return fract(sin(co*12.9898) * 13758.5453);
}

// License: MIT OR CC-BY-NC-4.0, author: mercury, found: https://mercury.sexy/hg_sdf/
float mod1(inout float p, float size) {
  float halfsize = size*0.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize, size) - halfsize;
  return c;
}

// License: CC0, author: Mårten Rånge, found: https://github.com/mrange/glsl-snippets
vec2 toPolar(vec2 p) {
  return vec2(length(p), atan(p.y, p.x));
}

// License: CC0, author: Mårten Rånge, found: https://github.com/mrange/glsl-snippets
vec2 toRect(vec2 p) {
  return vec2(p.x*cos(p.y), p.x*sin(p.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  float aa = 2.0/RESOLUTION.y;

  vec2 pp = toPolar(p);
  float a = pp.y;
  float hn = mod1(pp.y, TAU/6.0);
  vec2 hp = toRect(pp);

  float x = hp.x;
  float w = mix(0.03, 0.1, 0.5+0.5*cos(sqrt(0.5)*0.125*TIME));
  float n = mod1(x, w);
  float d = abs(x)-(0.5*w-aa);

  float h0 = hash(n+1.234);
  float h1 = hash(n+2.345);
  float cut = mix(-0.5, 0.999, 0.5+0.5*sin(TIME+TAU*h0));
  const float coln = 6.0;
  vec3 col = hsv2rgb(vec3(0.1*TIME+floor(h0*coln)/coln, 0.9, 1.0))*smoothstep(aa, -aa, d)*smoothstep(cut, cut-0.005, sin(a+2.0*(h1-0.5)*TIME+h1*TAU));

  col = sqrt(col);

  fragColor = vec4(col, 1.0);
}

vec2 opU(vec2 d1, vec2 d2) {
    return (d1.x < d2.x) ? d1 : d2;
}

vec3 pallete(float t) {
    vec3 a = vec3(0.2);
    vec3 b = vec3(0.5);
    vec3 c = vec3(1.0);
    vec3 d = vec3(0.0, 0.33, 0.67);
    return a + b * cos(6.28318 * (c*t + d));
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}


vec2 map(vec3 p) {
    vec2 d = vec2(length(p-vec3(0.0, 0.2 + cos(iTime) * 0.05, 0.0 + sin(iTime)*0.02)) - 0.2, 0.05);
    d = opU(d, vec2(length(p-vec3(0.5, 0.05 + sin(1.4*iTime+2.0)*0.03, 0.0 + cos(iTime)*0.03)) - 0.1, 2.2));
    d = opU(d, vec2(length(p-vec3(-0.4 + cos(iTime) * 0.02, 0.1 + sin(iTime)*0.01, 0.1* sin(3.0*iTime)*0.3)) - 0.1, 0.8));

    d = opU(d, vec2(sdBox(p - vec3(0.0, 0.0, 0.5), vec3(2.1, 3.1, 0.1)), 0.7));
    d = opU(d, vec2(sdBox(p - vec3(0.0, -0.3, 0.0), vec3(2.1, 0.1, 2.1)), 0.6));

    return d;
}

float shadow(vec3 p, vec3 light) {
    vec3 ro = p;
    vec3 rd = normalize(light-p);

    float tmin = 0.21;
    float t = tmin;
    int maxSteps = 700;

    float res = 1.0;
    for (int i=0; i<maxSteps; i++) {
            vec3 p = ro + t*rd;
            vec2 h = map(p);
            if (h.x < 0.0001) {
                return 0.0;
            }
            res = min(res, 32.0*h.x/t);
            t += h.x;
    }
    return res;

}

vec3 calcNormal(vec3 p) {
    vec3 d = vec3(0.0001, 0.0, 0.0);

    float dx = (map(p + d.xyy) - map(p - d.xyy)).x;
    float dy = (map(p + d.yxy) - map(p - d.yxy)).x;
    float dz = (map(p + d.yyx) - map(p - d.yyx)).x;

    return normalize(vec3(dx, dy, dz));
}

mat3 setUpCamera(vec3 camera_pos, vec3 camera_target, float angle) {
	vec3 up = vec3(sin(angle), cos(angle), 0.0);
	vec3 cz = normalize(camera_target - camera_pos);
	vec3 cx = normalize(cross(up, cz));
	vec3 cy = normalize(cross(cz, cx));
	return mat3(cx, cy, cz);
}

vec2 rayCast(vec3 ro, vec3 rd) {
    float tmin = 0.1;
    float tmax = 200.0;
    float t = tmin;
    int maxSteps = 70;

    vec2 res = vec2(-1.0, -7.0);

    for (int i=0; i<maxSteps && t<tmax; i++) {
            vec3 p = ro + t*rd;
            vec2 h = map(p);
            if (h.x < 0.0001) {
                res = vec2(t, h.y);
                break;
            }
            t += h.x;
    }
    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 ro = vec3(0.0, 0.1, -1.0);
    vec3 ta = vec3(0.0, 0.1, 0.0);
    mat3 camera = setUpCamera(ro, ta, 0.0);
    float fo = 2.5;

    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec3 rd = camera * normalize(vec3(uv, fo));

    vec2 h = rayCast(ro, rd);
    if (h.y < 0.0) {
        fragColor = vec4(0.0);
        return;
     }

     vec3 light = vec3(0.5, 0.5, -1.0);
     vec3 point = ro + rd * h.x;
     vec3 ld = normalize(light-point);
     vec3 normal = calcNormal(point);

     vec3 color = pallete(h.y) * max(0.0, dot(normal, ld));

     // specular
     vec3 hf = normalize(normal + ld);
     color += vec3(0.5) * pow(dot(normal, hf), 1000.0);

     color = shadow(point, light) * color;

     // gamma correction
     color = pow( color, vec3(0.4545) );

     fragColor = vec4(color, 0.0);
}

