//////////////////////////////////////////////////////////////
#define time iTime*1.
#define pi 3.14159265
#define deg pi/180.

mat2 r2d(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

float sdBox(vec3 p ,vec3 s) {
    return max(abs(p.z)-s.z,max(abs(p.y)-s.y,abs(p.x)-s.x));
}

vec3 lpos(void) {
    vec3 l = vec3(0.,1.5,5.);
    l.x += sin(time/5.)*1.2;
    l.z += cos(time/5.)*1.2;
    return l;
}

float sdTorus(vec3 p, vec2 s) {
    return length(vec2(length(p.xy),p.z+s.x)-s.x)-s.y;
}

vec4 map(vec3 p) {
    vec3 o = p;
    p.z -= 5.;
    p.yz *= r2d(sin(time/5.)*0.1);
    vec3 tp = p;
    float tpf = 2.;
    vec3 tpb = tp;
    tp.y = (fract(tp.y*tpf)-0.5)/tpf;
    tp.xz *= r2d(time);
    tp.yz *= r2d(deg*60.*(abs(fract(floor(tpb.y*tpf)/tpf)-1.)*2.));
    float d = sdTorus(p,vec2(0.1,0.01));
    d = min(d,sdTorus(tp,vec2(0.48,0.01)));
    d = min(d,p.y+1.);
    return vec4(p,d);
}

vec2 RM(vec3 ro, vec3 rd,float s) {
    float dO = 0.;
    float ii = 0.;
    for (int i=0;i<99;i++) {
        vec3 p = ro+rd*dO;
        float dS = map(p).w*s;
        dO += dS*0.5;
        ii += 0.01;
        if (dO > 100. || dS< 0.001) {break;}
    }
    return vec2(dO,ii);
}

vec3 calcNorm(vec3 p) {
    vec2 h = vec2(0.001,0.);
    return normalize(vec3(
        map(p-h.xyy).w-map(p+h.xyy).w,
        map(p-h.yxy).w-map(p+h.yxy).w,
        map(p-h.yyx).w-map(p+h.yyx).w
    ));
}

vec3 sh(vec3 p, vec3 n) {
    vec3 l = lpos();
    vec3 rd = normalize(l-p);
    vec2 d = RM(p-n*0.003,rd,1.);
    float s = 0.;
    float dif = clamp(dot(rd,n),0.,1.);
    if (length(l-p) > d.x) {
        s = 1.;
    }
    return vec3(s,dif,d.x);
}

vec3 colo(vec3 p , vec3 n ,vec2 d, vec3 rd) {
    vec3 col = 1.-d.xxx*0.1;
    vec3 s = sh(p,n);
    col *= 0.5;
    col.rg += n.yz*r2d(0.2)*0.2;
    col.b += n.x;
    col -= s.x*0.5;
    col += (1.-s.y*1.)*0.4;
    vec3 l = lpos();
    float l1 = clamp(length(l-p),0.,1.);
    float l2 = pow(1.-length(l.xy-rd.xy*l.z),3.);
    col = clamp(col,0.,1.);
    col += clamp((l1*l2),0.,1.);
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 R = iResolution.xy;
    float ar = R.x/R.y;
    uv -= 0.5;
    uv.x *= ar;
    vec3 col = vec3(0.);
    vec3 ro = vec3(0.);
    vec3 rd = normalize(vec3(uv,2.));
    vec2 d = RM(ro,rd,1.);
    vec3 p = ro+rd*d.x;
    vec3 n = calcNorm(p);
    col += colo(p,n,d,rd);
    float rl = 1.;
    for (int i=0;i<3;i++) {
        ro = p-n*0.003;
        rd = reflect(rd,n);
        d = RM(ro,rd,1.);
        p = ro+rd*d.x;
        n = calcNorm(p);
        col += colo(p,n,d,rd)*rl*0.2;
    }
    fragColor = vec4(col,1.0);
}

