#define S(r,v) smoothstep(9./iResolution.y,0.,abs(v-(r)))

const vec2 s = vec2(1, 1.7320508); // 1.7320508 = sqrt(3)
const vec3 baseCol = vec3(.05098, .25098, .2784);
const float borderThickness = .02;
const float isolineOffset = .4;
const float isolineOffset2 = .325;

float calcHexDistance(vec2 p)
{
    p = abs(p);
    return max(dot(p, s * .5), p.x);
}

float random(vec2 co)
{
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec4 calcHexInfo(vec2 uv)
{
    vec4 hexCenter = round(vec4(uv, uv - vec2(.5, 1.)) / s.xyxy);
    vec4 offset = vec4(uv - hexCenter.xy * s, uv - (hexCenter.zw + .5) * s);
    return dot(offset.xy, offset.xy) < dot(offset.zw, offset.zw) ? vec4(offset.xy, hexCenter.xy) : vec4(offset.zw, hexCenter.zw);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 3. * (2. * fragCoord - iResolution.xy) / iResolution.y;
    uv.x += iTime * .25;

    vec4 hexInfo = calcHexInfo(uv);
    float totalDist = calcHexDistance(hexInfo.xy) + borderThickness;
    float rand = random(hexInfo.zw);

    float angle = atan(hexInfo.y, hexInfo.x) + rand * 5. + iTime;

    vec3 isoline = S(isolineOffset, totalDist) * baseCol * step(3. + rand * .5, mod(angle, 6.28))
        + S(isolineOffset2, totalDist)
                    * baseCol * step(4. + rand * 1.5, mod(angle + rand * 2., 6.28));

    float sinOffset = sin(iTime + rand * 8.);
    float aa = 5. / iResolution.y;

    fragColor.rgb = (smoothstep(.51, .51 - aa, totalDist) 
    + pow(1. - max(0., .5 - totalDist), 20.) * 1.5) * (baseCol + rand * vec3(0., .1, .09)
    )
    + isoline 
    //+ baseCol * smoothstep(.2 + sinOffset, .2 + sinOffset - aa, totalDist)
    ;    
}

float map(vec3 p, settings setts)
{
    return world_sdf(p, iTime, setts);
}


vec3 computeNormal(vec3 p, settings setts)
{
    float h = 1e-4;
    float sdp = world_sdf(p, iTime, setts);
    return normalize(vec3(world_sdf(p + vec3(h, 0, 0), iTime, setts) - sdp,
                          world_sdf(p + vec3(0, h, 0), iTime, setts) - sdp,
                          world_sdf(p + vec3(0, 0, h), iTime, setts) - sdp));


}


///////////////////////////////////////////
/////// SPHERE TRACING CODE HERE:
///////////////////////////////////////////

bool sphere_tracing(ray r,
               		int max_iter,
               		settings setts,
               		out vec3 hit_loc,
               		out int iters)
{
    hit_loc = r.origin + r.direction * (-r.origin.y / r.direction.y);
    iters = 1;
    float t = 0.;
    for (int i = 0; i < max_iter; i++) {
        vec3 p = r.origin + t * r.direction;
        float dist = world_sdf(p, iTime, setts);
        if (dist < EPSILON) {
            hit_loc = p;
            iters = i;
            return true;
        }

        t += dist;

    }

    iters = max_iter;
    return false;

}

///////////////////////////////////////////

vec3 shade(ray r, int iters, settings setts)
{
    vec3 p = r.origin;
    vec3 d = r.direction;

    if (setts.shade_mode == GRID)
    {
    	float res = 0.2;
    	float one = abs(mod(p.x, res) - res / 2.0);
    	float two = abs(mod(p.y, res) - res / 2.0);
    	float three = abs(mod(p.z, res) - res / 2.0);
    	float interp = min(one, min(two, three)) / res;

        return mix( vec3(0.2, 0.5, 1.0), vec3(0.1, 0.1, 0.1), smoothstep(0.0,0.05,abs(interp)) );
    }
    else if (setts.shade_mode == COST)
    {
        return vec3(float(iters) / float(cost_norm));
    }
    else if (setts.shade_mode == NORMAL)
    {
        vec3 normal = computeNormal(p, setts);
        vec3 white = vec3(1., 1., 1.);
        return (normal + white) / 2.;

    }
    else if (setts.shade_mode == DIFFUSE_POINT)
    {
        vec3 light_pos = vec3(0.0, 5.0, 0.0);
        vec3 light_intensity = vec3(5.0);
        vec3 surface_color = vec3(0.5);

        vec3 lightDirection = normalize(light_pos - p);
        vec3 normal = computeNormal(p, setts);
        vec3 Li = light_intensity / pow(distance(light_pos, p), 2.0);
        return surface_color * max(0.0, dot(normal, lightDirection)) * Li;
    }
    else if (setts.shade_mode == ENVIRONMENT_MAP)
    {
        vec3 normal = computeNormal(p, setts);
        vec3 reflection = reflect(d, normal);
        //return vec3(texture(iChannel0, reflection));
    }
    else
    {
        return vec3(0.0);
    }

    return vec3(0.0);
}

