const int keyW = 87;
const int keyA = 65;
const int keyS = 83;
const int keyD = 68;

const int keySpace = 16;
const int keyShift = 32;



float keyPressed(int keyCode)
{
    return texelFetch(iChannel0, ivec2(keyCode, 0), 0).x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 keyVec = texture(iChannel1, uv);

    if(iTime < 0.1)
    {
        keyVec = vec4(0.0, 0.0, -0.3, 0.0);
    }

    keyVec.x += (keyPressed(keyW) - keyPressed(keyS)) * iTimeDelta;
    keyVec.y += (keyPressed(keyD) - keyPressed(keyA)) * iTimeDelta;
    keyVec.z += (keyPressed(keySpace) - keyPressed(keyShift)) * iTimeDelta;

    fragColor = keyVec;
}

