vec3 skyTex(vec3 ray, vec3 sun)
{
    ray.y = abs(ray.y);
    float raySun = dot(ray,sun);

    vec3 col = exp2(-(ray.y-raySun*0.5)/vec3(.1,.3,.6)); // Base gradient
    col *= sqrt(max(0.00001, dot(vec3(sun.x, sun.y+0.3, sun.z), vec3(0.0, 1.0, 0.0)))); //Darken the sky the lower the sun is
    return mix(col, vec3(10.0, 3.0, 0.2), smoothstep(0.9995, 1.0, max(0.0, raySun))) * sqrt(max(0.0, (dot(abs(ray+0.03), vec3(0.0, 1.0, 0.0))))); // Sun disc
}

