  if (iFrame < 2 && coord.x < TSIZE && coord.y < TSIZE)
  {

    vec4 data, n;
    ivec2 co = ivec2(floor(coord));

    float a = derivHash(co);
    float b = derivHash((co+ivec2(1,0)));
    float c = derivHash((co+ivec2(0,1)));
    float d = derivHash((co+ivec2(1,1)));

// Pre-calc all we can...
