    vec4 tt = cell(px + ivec2(0,2));
    vec4 ll = cell(px - ivec2(2,0));
    vec4 rr = cell(px + ivec2(2,0));  

    vec4 sum = b + t + l + r;
    vec4 diag = bl + br + tl + tr;
    vec4 dif = abs(b-t) * abs(l-r);
    vec4 dif2 = (t + b) * (l + r);
    vec4 diDif = (tl + br) * (tr + bl);

    if (frame < 10) {
        col.g = 0.3 * diag.g + 0.2 * sum.g - e.g;
    } else if (frame == 10) {
        col.g = 1. - step(0.1, e.g);
    } else if (frame == 11 && diag.g == 0.) {
       if (b.g+t.g==2. && l.g+r.g==0. && bb.g+tt.g>=1.)  
           col.g = 1.;
       else if (l.g+r.g==2. && b.g+t.g==0. && ll.g+rr.g>=1.)  
           col.g = 1.;         
    } else if (frame <= 13) {
       if (e.g == 1. && dif2.g == 1.)
           col.g = 0.;
    } else if (frame == 14) {
       if (f.x > dim.x + 1. || f.y > dim.y)
           col *= 0.;
       else if (f.x < 0.5 * dim.x + 1. && abs(f.y - 0.5 * dim.y) < 4.) //could replace 4 with random val
           col.r = 1.;
    } else if (frame < 36) {
        if (e.g == 1. && (sum+diag).r > 0.)
            col.r = 1.;
    } else if (frame < 36 + h) {
        col = l;
    } else if (frame == 36 + h) { // :)
        float h2 = mod(floor(float(iFrame) / 120.), 2.) - 0.5;

        float s = step(abs(f.x - 0.5 * dim.x - 2.5), 0.5);
