vec4 tunnel(vec2 uv) {
    vec2 tunnel = vec2(atan(uv.y/uv.x)/PI, 1.0/length(uv)+iTime/2.0);
    if (uv.x > 0.0) {
        tunnel.x += 0.5;
    }
    tunnel *= 10.0;
    tunnel = vec2(tunnel.x + tunnel.y, tunnel.x - tunnel.y);

    bool flip = rand(floor(tunnel)/10.0) < 0.5;
    return abs(mod(tunnel.x, 1.0)-mod(flip ? 1.0-tunnel.y : tunnel.y, 1.0)) < 0.1 ? vec4(0.3, 0.9, 1.0, 1.0) : vec4(0.05, 0.05, 0.2, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
