vec3 mix4ColorGradient(float ratio, vec3 start, vec3 mid1, vec3 mid2, vec3 end){
    return
    mix(
        mix(
            mix(start, mid1, ratio/.33), mix(mid1, mid2, (ratio - .33)/.66), 
            step(.33, ratio)), 
