void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = fragCoord / iResolution.xy;
    fragColor = texture(iChannel0, uv);

    const float range = 0.1; // Length of glow streaks
    const float steps = 0.005; // Number of texture samples / 2
    const float threshold = 0.6; // Color key threshold (0-1)
    const float brightness = 5.0; // Glow brightness

    for (float i = -range; i < range; i += steps) {

        float falloff = 1.0 - abs(i / range);

        vec4 blur = texture(iChannel0, uv + i);
        if (blur.r + blur.g + blur.b > threshold * 3.0) {
            fragColor += blur * falloff * steps * brightness;
        }

        blur = texture(iChannel0, uv + vec2(i, -i));
        if (blur.r + blur.g + blur.b > threshold * 3.0) {
            fragColor += blur * falloff * steps * brightness;
        }
    }
}

vec4 cell(ivec2 f) {
    return texelFetch(iChannel0, f, 0);
}

void mainImage(out vec4 col, in vec2 f)
{
    col = vec4(0.0);
    int frame = iFrame % 120;

    // Translate and zoom
    f -= 0.5 * iResolution.xy;
    f *= 1.1 * dim.y/iResolution.y; 
    f += 0.5 * dim;
    // s = 1. if right side, 0. if left
    float s = step(floor(f.x), 0.5 * dim.x);   

    // Mirrored coords 
    ivec2 ff = ivec2(s * dim.x + (1. - f.x) * (2. * s - 1.), f.y);
    col = cell(ff);
    vec4 col2 = cell(ff + ivec2(0,1));
    vec4 col3 = cell(ff + ivec2(0,2));

    float g = col.g;
    float g2 = col2.g;

    if (frame > 60)
        col.rgb = col.rrr;
    if (f.x <= 0. || f.x > dim.x + 2. || f.y > dim.y)
           col *= 0.;            

    float v = step(floor(0.5 * dim.x) + 1., f.x);
    float o = 2. * h21(vec2(floor(float(iFrame) / 120.), 132.));  
    o += 0.1 * v;
    float y = f.y / dim.y;
    col *= 0.5 + 0.5 * v;
    col *= 0.75 + 0.5 * y;
    col.rgb *= pal(0.5 * col3.r + 0.1 * y + o + 0.25 * h21(vec2(ff)), vec3(0,1,2)/3.);       
    if (length(col.rgb) < 0.05)
        col.rgb += g2 * col2.r * pal(-0.5 + 0.1 * y + o + 0.25 * h21(vec2(ff.x, ff.y - 1)), vec3(0,1,2)/3.);
    else
        col *= g;

    col.rgb = sqrt(col.rgb);
}

