#define KEYBOARD iChannel1
#define KEY_RESET 82

vec4 cell(in ivec2 p) {
    ivec2 r = ivec2(textureSize(iChannel0, 0));
    p = (p+r) % r;
    return texelFetch(iChannel0, p, 0 );
}

bool key_down(int key) {
    return int(texelFetch(KEYBOARD, ivec2(key, 0), 0).x) == 1;
}

void mainImage( out vec4 col, in vec2 f )
{
    col = vec4(0.0);
    ivec2 px = ivec2(f);
    int frame = iFrame % 120;

    ivec2 g = ivec2(f + vec2(100.,151.) * iTime * 60.) & 1023;
    float rand = texelFetch(iChannel2, g, 0).r;

    if (frame == 0 || key_down(KEY_RESET)) {
        col.g = rand;
        return;
    }

    // Center cell
    vec4 e = cell(px);
    col = e;

    // Neighbour cells
    vec4 b = cell(px - ivec2(0,1));
    vec4 t = cell(px + ivec2(0,1));
    vec4 l = cell(px - ivec2(1,0));
    vec4 r = cell(px + ivec2(1,0));   
    vec4 bl = cell(px + ivec2(-1,-1));
    vec4 br = cell(px + ivec2(1, -1));
    vec4 tl = cell(px + ivec2(-1, 1));
    vec4 tr = cell(px + ivec2(1,  1));   

    vec4 bb = cell(px - ivec2(0,2));
    vec4 tt = cell(px + ivec2(0,2));
    vec4 ll = cell(px - ivec2(2,0));
    vec4 rr = cell(px + ivec2(2,0));  

    vec4 sum = b + t + l + r;
    vec4 diag = bl + br + tl + tr;
    vec4 dif = abs(b-t) * abs(l-r);
    vec4 dif2 = (t + b) * (l + r);
    vec4 diDif = (tl + br) * (tr + bl);

    if (frame < 10) { // run CA
        col.g = 0.5 * diag.g + 0.2 * sum.g - e.g;
    } else if (frame == 10) { // invert and map to 0 or 1
        col.g = 1. - step(0.1, e.g);
    } else if (frame == 11 && diag.g == 0.) { // connect lines
       if (b.g+t.g==2. && l.g+r.g==0. && bb.g+tt.g>=1.)  
           col.g = 1.;
       else if (l.g+r.g==2. && b.g+t.g==0. && ll.g+rr.g>=1.)  
           col.g = 1.;         
    } else if (frame <= 13) { // remove corners
       if (e.g == 1. && dif2.g == 1.)
           col.g = 0.;
    } else if (frame == 14) { 
       if (f.x > dim.x + 1. || f.y > dim.y) // restrict to bounds (slightly wrong)
           col *= 0.;
       else if (f.x < 0.5 * dim.x + 1.) // make centre line red
           col.r = 1.;
    } else { // traverse greens when adjacent to reds
        if (e.g == 1. && (sum+diag).r > 0.) 
            col.r = 1.;
    }

}

