#define PI 3.1415926
#define PHASE 0.73

float HG(float aCosTheta, float aAnisotropy)
{
	/*
			1                  1 - g^2
		 ----- * -------------------------------------
		  4PI     (1 + g^2 - 2g * cos(theta)) ^ (3/2)
	*/
	float g = aAnisotropy;
	float numerator = (1.f / (4.0*PI)) * (1.f - g * g);

	float x = 1.f + g * g - 2.f * g * aCosTheta;
	float f = 1.0/sqrt(max(x, 0.00001)); // x^(-1/2)
	float denominator = f * f * f; // x^(-3/2)
	return numerator * denominator;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 screenSpace = uv * 2.0 - 1.0;

    vec3 o = vec3(0.0, 0.0, -0.5);

    vec3 f = normalize(-o);
    vec3 u = vec3(0,1,0);
    vec3 r = normalize(cross(f, u));

    vec3 direction = normalize(f * 2.0 + 
                         r * screenSpace.x * iResolution.x / iResolution.y +
                         u * screenSpace.y);

    vec3 bg = texture(iChannel0,direction).rgb;

    float transmittance = 1.0;
    vec3 scattering = vec3(0.0,0.0,0.0);

    vec3 indirectScattering = vec3(0.0, 0.0, 0.0);
    float ts = 2.0*PI/64.0;
    for(float t = 0.0; t < 2.0*PI; t += ts)
    {
        float ps = PI / 64.0;
        for(float p = 0.0; p < PI; p += ps)
        {
            vec3 l = vec3(cos(t)*sin(p), cos(p), sin(t)*sin(p));
            vec3 lc = texture(iChannel0,l).rgb * 2.0;

            float slice = ts * ps * sin(p);
            indirectScattering += lc * slice * HG(dot(l,direction), PHASE);
        }
    }

    float d = 0.0;
    for(; d < 1.;)
    {
        float ss = 0.001;
        vec3 pos = o + direction * d;

        if(abs(pos).x < 0.15 && abs(pos).y < 0.15 && abs(pos).z < 0.15)
        {
            float extinction = 1.0/0.1* ss;
            scattering += indirectScattering * transmittance * extinction;
            transmittance *= exp(-extinction);
        }

        d += ss;
    }

    fragColor.rgb = scattering + bg * transmittance;
}

#define MAX_STEPS 100
#define MAX_DISTANCE 100.
#define PERCISION .01

float sdSphere(vec3 p, vec3 q, float r){
    return length(p-q)-r;
}

float sdWall(vec3 p, float q){
    return max(q, p.z) - min(q, p.z + p.y);
}

float sdPlane(vec3 p, float q){
    return max(q, p.y) - min(q, p.y);
}

float sdDirCylinder(vec3 p, vec2 q, vec2 d, float r){
    float relativeDist = sqrt(pow(p.y * d.x, 2.) + pow(p.y * d.y, 2.)) - r;
    return relativeDist + sqrt(pow(q.x, 2.) + pow(q.y, 2.));
}

float sdScene(vec3 p){
    float sphere = sdSphere(p, vec3(1., 1.5, 5.), 1.);
    float wall  = sdWall(p, 10.);
    float ground  = sdPlane(p, 0.);

    return  min(sphere, min(wall, ground));
}

float rayMarch(vec3 ro, vec3 rd){
    float dO;

    for(int i=0;i<MAX_STEPS;i++){
        float cO     = sdScene( ro + rd * dO);
        dO += cO;
        if(dO > MAX_DISTANCE || cO < PERCISION)break;
    }
    return dO;
}

vec3 compute(float ray, vec3 ro, vec3 rd){
    vec3 position = ro + rd * ray;

    float lastSd = sdScene(position);
    vec2 error = vec2(.01 * (sin(iTime) + 1.5), 0);
    vec3 normal = lastSd + vec3(
        sdScene(position - error.xyy),
        sdScene(position - error.yxy),
        sdScene(position - error.yyx)
    );
    normal = normalize(normal);

    return normal;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy *.5) / iResolution.y;

    vec3 ro = vec3(1, 2, 1);
    vec3 rd = vec3(uv.x, uv.y, 0.8 + sin(iTime)/5.);

    float ray = rayMarch(ro, rd);

    vec3 col = compute(ray, ro, rd);

    fragColor = vec4(col,1.0);
}

