vec3 mix3ColorGradient(float ratio, vec3 start, vec3 mid, vec3 end){
    float h = .5;
    return
mix(mix(start, mid, ratio/h), mix(mid, end, (ratio - h)/(1.0 - h)), step(h, ratio));
}



vec3 cell_shade(in vec2 uv, in vec2 uv_floor, 
                vec3 col_int_orig, vec3 col_int_mid, vec3 col_int_target, 
                vec3 col_ext_orig, vec3 col_ext_mid, vec3 col_ext_target,
                vec3 col_ring_orig, vec3 col_ring_mid, vec3 col_ring_target, vec2 st) {
    vec3 res = vec3(.0);

    // Activation/Step things
    //float radius = length(uv); // Change size of the ring

    float N = 10.*(1.-abs(cos(iTime*.2)))+2.; // Norm 2 to norm 12 (circle to square) if using norm 1 -> losange

    float absX = pow(abs(uv.x), N);
    float absY = pow(abs(uv.y), N);

    float radius = pow(absX+absY, 1./N); 


    float ring_dist = (1.-length(st))/2.;
    float ring_thickness = 0.15;
    float activation_ext = smoothstep(ring_dist+ring_thickness - 0.04, ring_dist+ring_thickness, radius);
    float activation_int = (1.-smoothstep(ring_dist-0.04, ring_dist, radius));
    float activation_ring = (1.-activation_int)*(1.-activation_ext);

    // Color things

    vec3 col_interior = mix3ColorGradient(length(uv_floor)/N_tile*2.5, col_int_orig, col_int_mid, col_int_target);
    vec3 col_exterior = mix3ColorGradient(length(uv_floor)/N_tile*2.5, col_ext_orig, col_ext_mid, col_ext_target);
    vec3 col_ring = mix3ColorGradient(length(uv_floor)/N_tile*2.5, col_ring_orig, col_ring_mid, col_ring_target);

    //Render
    res += col_interior*activation_int;
    res += col_exterior*activation_ext;
    res += col_ring*activation_ring;

    return res;
}

vec3 sphere_warp(in vec2 uv) {

//uv += floor(uv);
    vec2 st = (uv+.5) * 2.;

    vec2 corner = floor(st);
    vec2 sphere_coord = (fract(st) - corner)/2.;
    //return sphere_coord;
    sphere_coord *= N_tile/(N_tile/2.-.5);
    vec2 sphere_uv = normalize(sphere_coord)*(asin(length(sphere_coord)) / 3.1415926);
    float mask = max(0., 1. - length(sphere_uv));


    // Moving Sphere

    return vec3(sphere_uv, mask);
}

