#define N_tile 21.
vec3 cell_shade(in vec2 uv, in vec2 uv_floor) {
    vec3 res = vec3(.0);

    // Activation/Step things
    float radius = length(uv);
    float ring_dist = 0.7;
    float ring_thickness = 0.15;
    float activation_ext = smoothstep(ring_dist+ring_thickness - 0.04, ring_dist+ring_thickness, radius);
    float activation_int = (1.-smoothstep(ring_dist-0.04, ring_dist, radius));
    float activation_ring = (1.-activation_int)*(1.-activation_ext);

    // Color things
    float uv_floor_x_value = 1.;
    float uv_floor_y_value = 1.;

    vec3 col_interior = vec3(uv_floor_x_value*uv_floor_y_value, 0., 0.);
    vec3 col_exterior = vec3(0., uv_floor_x_value*uv_floor_y_value, 0.);
    vec3 col_ring = vec3(0., 0., uv_floor_x_value*uv_floor_y_value);    

    //Render
    res += col_interior*activation_int;
    res += col_exterior*activation_ext;
    res += col_ring*activation_ring;

    return res;
}

vec2 shape_interpolation(vec2 shape1, vec2 shape2, float ratio){
    return mix(shape1, shape2,  smoothstep(0.0,1.0, ratio));
}


vec3 Sphere(in vec2 st, float radius) {
    //return sphere_coord;
    vec2 uv = (st/radius);
    vec2 sphere_uv = normalize(uv)*(asin(length(uv)) / 3.1415926);
    float mask = 1.-step(radius, length(st));
    return vec3(sphere_uv, mask);
}

vec3 Parabole(vec2 uv, float radius){
    vec3 res = vec3(.0, .0, 0.);
    vec2 uv_parabol = (uv)*(pow(length(uv), -.5))*1./pow(radius, -.5);// Change le pow pour du turfu
    float mask =(1.-step(radius, length(uv)));
    return vec3(uv_parabol, mask);
}


vec3 Gaussian2D(vec2 uv, float radius){
    vec3 res = vec3(.0, .0, 0.);

    float sigma = 5.1;
    vec2 uv_Gaussian = uv * exp(dot(uv, uv)*sigma)/(2.*sqrt(sigma));    
    float mask =(1.-step(radius, length(uv)));
    return vec3(uv_Gaussian, mask);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
