    vec2 parabole_uv_floor = floor(parabole.xy*N_tile);// *-1. move the center on the corner
    vec3 col_parabole = cell_shade((fract(parabole.xy*N_tile-.5)-.5)*2., parabole_uv_floor)*(parabole.z);

    //Lumire add *pow(sphere_uv.z*1.5, 2.)    
    vec3 sphere = Sphere(uv, radius);    
    vec2 sphere_uv_floor = floor(sphere.xy*(N_tile));// *-1. move the center on the corner
    vec3 col_sphere = cell_shade((fract(sphere.xy*N_tile*radius*2.-.5)-.5)*2., sphere_uv_floor)*(sphere.z); 


    vec3 gaussian_uv = Gaussian2D(uv, radius);    
    vec2 gaussian_uv_floor = floor(gaussian_uv.xy*N_tile);// *-1. move the center on the corner
    vec3 col_gaussian = cell_shade((fract(gaussian_uv.xy*N_tile*2.-.5)-.5)*2., gaussian_uv_floor)*(gaussian_uv.z); 



    vec2 sphere2 = sphere.xy*N_tile*radius*2.-.5;
    vec2 gaussian2 = gaussian_uv.xy*N_tile*2.-.5;
    vec2 parabole2 = parabole.xy*N_tile-.5;

    vec2 interp = shape_interpolation(parabole2, gaussian2, .5*cos(iTime)+.5);
    vec3 col_interp = cell_shade((fract(interp.xy)-.5)*2., sphere_uv_floor)*(sphere.z); 


    // Plane texture
    vec2 cell_uv = (fract(uv*(N_tile)+vec2(0.5,0.5))-.5)*2.;
    vec2 cell_uv_floor = floor(uv*N_tile+.5);
    vec3 col_shade = cell_shade(cell_uv, cell_uv_floor)*(1.-parabole.z);


    // Output to screen
    fragColor = vec4(col_shade+col_interp,1.0);
}

