    vec2 t = vec2(iTime / 1.0);
    float sound = texture(iChannel0, vec2(iTime)).x - 0.5;
    float ring_dist = 0.5 + hash(id).x / 5.0 +  abs(hash(vec2(t) + id).y) * pow(abs(sound), 1.5) * .5;
    float ring_thickness = 0.15;
    vec3 col_interior = vec3(.15, .2, 0.3);

    vec3 col_exterior =vec3( hash(vec2(id.y , 0.0)).x / 20.0, 0.0, 0.0)
    + vec3( hash(vec2(id.x , 0.0)).x / 20.0, 0.0, 0.0);
    col_exterior.x += 0.05;
    //0.6 * pal( hash(id).x, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(2.0,1.0,0.0),vec3(0.5,0.20,0.25));
    vec3 col_ring = vec3(0.15, 0., .4);

    float activation_ext = smoothstep(ring_dist+ring_thickness - 0.14, ring_dist+ring_thickness, radius);
    float activation_int = (1.-smoothstep(ring_dist-0.14, ring_dist, radius));
    float activation_ring = (1.-activation_int)*(1.-activation_ext);


    res += col_interior*activation_int;
    res += col_exterior*activation_ext;
    res += col_ring*activation_ring;


    return res;
}



vec3 sphere_warp(in vec2 uv) {

