    vec2 corner = floor(st);
    vec2 sphere_coord = (fract(st) - corner)/2.;
    //return sphere_coord;
    sphere_coord *= N_tile/(N_tile/2.-.5);
    vec2 sphere_uv = normalize(sphere_coord)* ((asin(length(sphere_coord)) / 3.1415926));

    float mask = max(0., 1. - length(sphere_uv));
    corner -= 0.5;

    sphere_uv.xy += vec2(iTime / 20.0) * normalize(-corner);
    return vec3(sphere_uv, mask);
}

float mask(inout vec2  uv) {

    if (uv.x > 2.0 * 9.3 / 21.0) {
        return 0.0;
    }
    if (uv.x < -0.11) {
        return 0.0;
    }
    uv.x -= 8.15 / 21.0;
    return 1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.yy-.5;
    if (mask(uv) < 1.0) {
        fragColor = vec4(0.1);
        return ;
    }
    float center_scale = 1.0 + sin(iTime/2.0) / 2.0;
    float angle = iTime / 2.0;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 cell_uv = (fract(rot*center_scale*uv*(N_tile)+vec2(0.5,0.5))-.5)*2.;
    vec2 cell_id = (floor(rot*center_scale*uv*(N_tile)+vec2(0.5,0.5))-.5)*2.;
    vec3 sphere_uv = sphere_warp(uv);

    vec2 sphere_cell_uv = (fract(sphere_uv.xy*N_tile)-.5)*2.;
    vec2 sphere_cell_id = (floor(sphere_uv.xy*N_tile)-.5)*2.;
    // Sphere texture
    vec3 col = max(vec3(.0), cell_shade(sphere_cell_uv, sphere_cell_id / 21.)* pow(sphere_uv.z*1.5, 4.)); //Lumire

    // Plane texture
    col += 0.7 * cell_shade(cell_uv, cell_id)*step(0., -sphere_uv.z);

    vec2 plane_center = vec2(0.0);

    col +=  vec3(0.21) * pow(1.0 / (.5 + length(uv * 2.0 * center_scale)), 1.5);
    fragColor = vec4(pow(col, vec3(1.1)) , 1.0);
}

