// "Hilbertian Saltation" by dr2 - 2022
// License: Creative Commons Attribution-NonCommercial-ShareAlike 4.0

#define AA  1   // (= 0/1) optional antialiasing

#if 0
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

float PrBoxDf (vec3 p, vec3 b);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
float PrSphDf (vec3 p, float r);
vec3 HsvToRgb (vec3 c);
float Minv2 (vec2 p);
float Maxv2 (vec2 p);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float ShowIntPZ (vec2 q, vec2 cBox, float mxChar, float val);

const int nBall = 8;
vec3 bPos[nBall], ltDir, qHit;
vec2 gId;
float tCur, dstFar, gSize, bSize, hInd, vDirI, vDirO, cHt;
int idObj;
const float pi = 3.1415927;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

// Hilbert indexing - without bit ops (from "Hilbert's Path")

int LoBit (int x)
{
  return x - 2 * (x / 2);
}

int FlipLoBit (int x)
{
  return 4 * (x / 2) - x + 1;
}

const int maxBits = 7;

int HilXy2D (ivec2 p, int n)
{
  ivec2 r;
  int j, td, d;
  d = 0;
  td = 1;
  j = n / 2;
  for (int b = 0; b < maxBits - 1; b ++) {
    td *= 2;
    j /= 2;
    if (j == 1) break;
  }
  j = n / 2;
  for (int b = 0; b < maxBits - 1; b ++) {
    r = ivec2 (LoBit (p.x / td), LoBit (p.y / td));
    d += j * j * ((r.y == 0) ? 3 * r.x : FlipLoBit (3 * r.x));
    if (r.y == 0) {
      if (r.x == 1) p = n - 1 - p;
      p = p.yx;
    }
    td /= 2;
    j /= 2;
    if (j == 0) break;
  }
  return d;
}

ivec2 HilD2Xy (int d, int n)
{
  ivec2 p, r;
  int j, td;
  td = d;
  p = ivec2 (0);
  j = 1;
  for (int b = 0; b < maxBits; b ++) {
    r.x = LoBit (td / 2);
    r.y = LoBit ((r.x == 0) ? td : FlipLoBit (td));
    if (r.y == 0) {
      if (r.x == 1) p = j - 1 - p;
      p = p.yx;
    }
    p += j * r;
    td /= 4;
    j *= 2;
    if (j == n) break;
  }
  return p;
}

#define HT(x) (1. - 0.8 * (x) / (bSize * bSize))

void SetGParm ()
{
  vec2 dq;
  int ic, ib;
  ib = int (bSize);
  ic = HilXy2D (ivec2 (gId), ib);
  hInd = float (ic);
  cHt = HT (hInd);
  vDirI = -1.;
  vDirO = -1.;
  if (ic > 0) {
    dq = gId - vec2 (HilD2Xy (ic - 1, ib));
    vDirI = (dq.y == 0.) ? ((dq.x > 0.) ? 0. : 2.) : ((dq.y > 0.) ? 1. : 3.);
  }
  if (ic < ib * ib - 1) {
    dq = vec2 (HilD2Xy (ic + 1, ib)) - gId;
    vDirO = (dq.y == 0.) ? ((dq.x > 0.) ? 0. : 2.) : ((dq.y > 0.) ? 1. : 3.);
  }
}

float GObjDf (vec3 p)
{
  vec3 q, qq;
  float dMin, d, r;
  dMin = dstFar;
  r = 0.12;
  if (Minv2 (gId) >= 0. && Maxv2 (gId) < bSize) {
    q = p;
    q.xz -= gSize * (gId + 0.5);
    qq = q;
    qq.y -= r + 0.08;
    q.y -= cHt + 0.02;
    d = PrRoundCylDf (q.xzy, 0.3, 0.02, cHt);
    DMINQ (1);
    if (vDirI >= 0.) {
      q = qq;
      if (vDirI > 0.) q.xz = vec2 (-1., 1.) * ((vDirI == 2.) ? q.xz : q.zx *
         sign (vDirI - 2.));
      d = max (length (q.yz) - r, q.x);
      DMINQ (2);
    }
    if (vDirO >= 0.) {
      q = qq;
      if (vDirO > 0.) q.xz = vec2 (-1., 1.) * ((vDirO == 2.) ? q.xz : q.zx *
         sign (vDirO - 2.));
      d = max (length (q.yz) - r, - q.x);
      DMINQ (2);
    }
  }
  return dMin;
}

float GObjRay (vec3 ro, vec3 rd)
{
  vec3 p, rdi;
  vec2 gIdP;
  float dHit, d, eps;
  if (rd.x == 0.) rd.x = 0.0001;
  if (rd.z == 0.) rd.z = 0.0001;
  rdi.xz = 1. / rd.xz;
  gIdP = vec2 (-999.);
  eps = 0.001;
  dHit = eps;
  for (int j = VAR_ZERO; j < 220; j ++) {
    p = ro + dHit * rd;
    gId = floor (p.xz / gSize);
    if (gId != gIdP) {
      gIdP = gId;
      SetGParm ();
    }
    d = GObjDf (p);
    dHit += min (d, eps + max (0., Minv2 ((gSize * (gId + step (0., rd.xz)) - p.xz) * rdi.xz)));
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 GObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = GObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

void BPos ()
{
  vec3 bp1, bp2;
  float t, bb;
  int ib1, ib2;
  bb = bSize * bSize;
  for (int k = VAR_ZERO; k < nBall; k ++) {
    t = tCur + (float (k) / float (nBall)) * (bb + 1.);
    ib1 = int (mod (t, bb));
    ib2 = int (mod (t + 1., bb));
    bp1 = vec3 (HilD2Xy (ib1, int (bSize)), 2. * HT (float (ib1))).xzy;
    bp2 = vec3 (HilD2Xy (ib2, int (bSize)), 2. * HT (float (ib2))).xzy;
    t = clamp (1.3 * fract (t) - 0.15, 0., 1.);
    if (ib1 < int (bb) - 1) {
      bPos[k] = mix (bp1, bp2, t);
      bPos[k].y += 3. * t * (1. - t);
    } else {
      bPos[k] = mix (bp1, bp2, step (0.5, t));
      bPos[k].y -= mix (t, 1. - t, step (0.5, t));
    }
    bPos[k].xz += 0.5;
    bPos[k].y += 0.2;
  }
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  q = p;
  q.xz -= 0.5 * bSize;
  q.y -= -0.1;
  d = PrBoxDf (q, vec3 (0.5 * bSize, 0.1, 0.5 * bSize));
  DMINQ (3);
  for (int k = VAR_ZERO; k < nBall; k ++) {
    q = p - bPos[k];
    d = PrSphDf (q, 0.2);
    DMINQ (4 + k);
  }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, qHitG;
  float dstObj, dstObjG, s;
  int idObjG;
  dstObjG = GObjRay (ro, rd);
  idObjG = idObj;
  qHitG = qHit;
  BPos ();
  dstObj = ObjRay (ro, rd);
  if (dstObjG < min (dstObj, dstFar)) {
    dstObj = dstObjG;
    idObj = idObjG;
    qHit = qHitG;
  }
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = (idObj == idObjG) ? GObjNf (ro) : ObjNf (ro);
    if (idObj == 1) {
      col4 = vec4 (0.9, 0.9, 0.95, 0.2);
      if (vn.y > 0.99) {
        for (int k = VAR_ZERO; k < nBall; k ++) {
          s = length (ro.xz - bPos[k].xz);
          if (s < 0.3) {
            col4.rgb = mix (HsvToRgb (vec3 (float (k) / float (nBall), 0.8, 1.)), col4.rgb, 
               smoothstep (0.1, 0.3, s));
            break;
          }
        }
        col4 *= 1. - 0.4 * smoothstep (0.05, 0.1,
           ShowIntPZ (qHit.xz - vec2 (0.2, -0.1), 0.9 * vec2 (0.5, 0.2), 3., hInd + 1.));
      } else col4 *= 0.9 + 0.1 * cos (32. * pi * qHit.y);
    } else if (idObj == 2) {
      col4 = vec4 (0.7, 0.75, 0.7, 0.2) * (0.7 + 0.3 * step (0., sin (8. * pi * (qHit.x - tCur))));
    } else if (idObj == 3) {
      col4 = vec4 (0.4, 0.2, 0.1, 0.2) * (0.5 + 0.5 * smoothstep (0., 0.1,
         length (fract (qHit.xz) - 0.5) - 0.32));
    } else if (idObj >= 4) {
      col4 = vec4 (HsvToRgb (vec3 (float (idObj - 4) / float (nBall), 0.8, 1.)), -1.);
    }
    if (col4.a >= 0.) col = col4.rgb * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
       col4.a * pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.);
    else col = col4.rgb * (0.6 - 0.4 * dot (rd, vn));
  } else {
    col = vec3 (0.6, 0.6, 1.) * (0.2 + 0.2 * (rd.y + 1.) * (rd.y + 1.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = -0.05 * pi;
  el = -0.25 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  }
  el = clamp (el, -0.4 * pi, -0.01 * pi);
  vuMat = StdVuMat (el, az);
  gSize = 1.;
  bSize = 8.;
  ro = vuMat * vec3 (0., 0.2, -2.5 * bSize);
  ro.xz += 0.5 * bSize;
  zmFac = 4.5;
  dstFar = 100.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1.,
     0., 1.), c.y);
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float DigSeg (vec2 q)
{
  return (1. - smoothstep (0.13, 0.17, abs (q.x))) *
     (1. - smoothstep (0.5, 0.57, abs (q.y)));
}

#define DSG(q) k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q)

float ShowDig (vec2 q, int iv)
{
  float d;
  int k, kk;
  const vec2 vp = vec2 (0.5, 0.5), vm = vec2 (-0.5, 0.5), vo = vec2 (1., 0.);
  if (iv == -1) k = 8;
  else if (iv < 2) k = (iv == 0) ? 119 : 36;
  else if (iv < 4) k = (iv == 2) ? 93 : 109;
  else if (iv < 6) k = (iv == 4) ? 46 : 107;
  else if (iv < 8) k = (iv == 6) ? 122 : 37;
  else             k = (iv == 8) ? 127 : 47;
  q = (q - 0.5) * vec2 (1.8, 2.3);
  d = 0.;
  kk = k;
  DSG (q.yx - vo);  DSG (q.xy - vp);  DSG (q.xy - vm);  DSG (q.yx);
  DSG (q.xy + vm);  DSG (q.xy + vp);  DSG (q.yx + vo);
  return d;
}

float ShowIntPZ (vec2 q, vec2 cBox, float mxChar, float val)
{
  float nDig, idChar, s, v;
  q = vec2 (- q.x, q.y) / cBox;
  s = 0.;
  if (Minv2 (q) >= 0. && Maxv2 (q) < 1.) {
    q.x *= mxChar;
    nDig = mxChar;
    idChar = mxChar - 1. - floor (q.x);
    q.x = fract (q.x);
    v = max (val, 0.) / pow (10., mxChar - idChar - 1.);
    if (idChar >= mxChar - nDig) s = ShowDig (q, int (mod (floor (v), 10.)));
  }
  return s;
}

// How does a ship wake form? Why are they 'universal'?
//
// A boat moving in deep water generates planar wave trains for
// a distribution of wavelength, each having their own propagation
// speed (/ Mach cone).
// Accumulating these waves (hit 'space'!) shows the typical ship-wake,
// called Kelvin wake.
// It is a physical mini-wonder that this shape (basically, a caustic,
// similar to your coffee mug's cardiod!) is relatively universal, independant
// of shape and speed.
// Changing the wavelength distribution (that is, taking the actual
// shape of the boat, propeller, etc.) doesn't change the final
// result much.
//
// See https://en.wikipedia.org/wiki/Wake_(physics)#Kelvin_wake_pattern
//
//  Some results:
//    . wake angle is 2.asin(1/3) ~= 38.9
//    . diverging wave angle is ~55
//    . transverse stern waves have a maximal wavelength 2 V^2 / g. 
//      This is used to evaluate the boat's speed V.
//    
//   Hope the vizualisation is telling!
//   Use the 'space' key to see the full wave train accumulated, and
//   mouse to change the scale.
//
//  See also: 
//    https://www.shadertoy.com/view/slKfWR <= Why do we approximate with planar waves?
//    https://www.shadertoy.com/view/7ltfDS <= Inaccurate but fun visu
//    https://www.youtube.com/watch?v=95sQcSulRFM  <= 5mins physics
//    https://www.youtube.com/watch?v=0cr6vZKe6HI  <= bow waves can cancel stern waves!
//    http://www.scullen.com.au/DSc/Publications/tuck_scullen_lazauskas_00e.pdf <= some maths
//
// skal/ (Pascal Massimino)
// License: Creative Commons Attribution-NonCommercial-ShareAlike 4.0

// Some physics
const float V0 = 1.2;    // base boat speed [m/s]
const float g  = 9.81;   // gravity
const float k_min = g / (V0 * V0);   // the only physical constant that matters
// Wavelength spectrum (basic)
const float L0 = 0.00;   // min wavelength [m]
const float NL = 30.;    // number of samples
// Boat position
const vec2 boat = vec2(3.20, 0.00);
// Colors
const vec3 ocean_col = vec3(.3, .5, .9);   // ocean background color
const vec3 boat_col  = vec3(.3, .7, .2);   // boat color (~green)
const vec3 shock_col = vec3(.6, .6, .1);   // wake-angle color (~yellow)

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  bool show_waves = (texelFetch(iChannel0, ivec2(32, 0),0 ).x > 0.5);
  vec2 uv = 2. * fragCoord / iResolution.yy - vec2(0.0, 1.0);  // y-screen = [-1, 1]

  float nL = NL;
  if (show_waves) nL *= 100.;

  float km = k_min * (1. + 3.7 * iMouse.x / iResolution.x);
  float Lmax = 6.2831 / km;

  // position relative to boat
  vec2 r = boat - uv;
  r = vec2(r.x, -abs(r.y));  // fold

  // color mixing factors
  float shock_mix = 0.;
  float boat_mix = 1. - smoothstep(0.18, 0.19, length(r - vec2(0., 0.15)));  // boat

  // Accumulate per-wavelength amplitude.
  float acc = 0.5;
  float progress = min(mod(iTime, 10.) * 0.3, 1.);
  for (float i = 1.; i <= nL; ++i) {
    float frac = i / nL;
    if (frac > progress) break;
    // sigmoid: sample more densely around 0. and 1.
    // This sampling is really where the actual shape of the boat/propeller would matter!
    frac = frac * (3. - 2. * frac); 
    float k0 = 6.2831 / mix(L0, Lmax, frac);

    float c2_phi = km / k0;
    float sin_th = sqrt(c2_phi);
    float cos_th = sqrt(1. - c2_phi);
    float phi = k0 * dot(r, vec2(sin_th, cos_th));
    float wave = (phi >= 0.) ? cos(phi) : 0.;
    if (show_waves) {
      acc += 2. * wave / nL;  // accumulate wave trains
    } else {
      // represent wave crests with lines:
      acc = max(acc, smoothstep(0.98, 1.0, wave));
    }
  }

  {  // Draw the limiting cone
    float c_Mach = 1. / 3.;  // critical angle
    // get the distance to the  cone
    vec2 shock_n = vec2(c_Mach, sqrt(1. - c_Mach * c_Mach));
    float d = abs(dot(r, shock_n));
    shock_mix = 1. - smoothstep(0.001, .005, d);
  }

  // final mix
  vec3 col = acc * ocean_col;
  col = mix(col, shock_col, shock_mix);
  col = mix(col, boat_col, boat_mix);

  fragColor = vec4(col, 1.0);
}

// License CC0: Dark chocolate FBM
//  Working on a cake related shader and created kind of dark chocolate
//  background. Nothing unique but different colors than what I usually 
//  do so sharing.

#define TIME        iTime
#define RESOLUTION  iResolution

#define PI          3.141592654
#define TAU         (2.0*PI)
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))
#define TTIME       (TAU*TIME)
#define DOT2(p)     dot(p, p)

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}
// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
//  Macro version of above to enable compile-time constants
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
vec3 sRGB(vec3 t) {
  return mix(1.055*pow(t, vec3(1./2.4)) - 0.055, 12.92*t, step(t, vec3(0.0031308)));
}

// License: Unknown, author: Matt Taylor (https://github.com/64), found: https://64.github.io/tonemapping/
vec3 aces_approx(vec3 v) {
  v = max(v, 0.0);
  v *= 0.6f;
  float a = 2.51f;
  float b = 0.03f;
  float c = 2.43f;
  float d = 0.59f;
  float e = 0.14f;
  return clamp((v*(a*v+b))/(v*(c*v+d)+e), 0.0f, 1.0f);
}

// License: Unknown, author: Unknown, found: don't remember
float tanh_approx(float x) {
//  return tanh(x);
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}

// License: MIT, author: Inigo Quilez, found: https://www.iquilezles.org/www/articles/smin/smin.htm
float pmin(float a, float b, float k) {
  float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

// License: CC0, author: Mrten Rnge, found: https://github.com/mrange/glsl-snippets
float pabs(float a, float k) {
  return -pmin(a, -a, k);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float heart(vec2 p) {
  p.y -= -0.6;
  p.x = pabs(p.x, 0.125);

  if( p.y+p.x>1.0 )
      return sqrt(DOT2(p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
  return sqrt(min(DOT2(p-vec2(0.00,1.00)),
                  DOT2(p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}

vec2 mod2_1(inout vec2 p) {
  vec2 n = floor(p + 0.5);
  p = fract(p+0.5)-0.5;
  return n;
}

float hf(vec2 p) {  
  p *= 0.25;
  vec2 p0 = p;
  vec2 n0 = mod2_1(p0);
  vec2 p1 = p*vec2(1.0, -1.0)+vec2(0.5, 0.66);
  vec2 n1 = mod2_1(p1);
  const float ss = 0.60;
  float d0 = heart(p0/ss)*ss;
  float d1 = heart(p1/ss)*ss;
  float d = min(d0, d1);
  return tanh_approx(smoothstep(0.0, -0.1,d)*exp(8.0*-d));
}

float height(vec2 p) {
  const mat2 rot1 = ROT(1.0);
  float tm = 123.0+TTIME/240.0;
  p += 5.0*vec2(cos(tm), sin(tm*sqrt(0.5)));
  const float aa = -0.45;
  const mat2  pp = (1.0/aa)*rot1;
  float h = 0.0;
  float a = 1.0;
  float d = 0.0;
  for (int i = 0; i < 4; ++i) {
    h += a*hf(p);
    d += a;
    a *= aa;
    p *= pp;
  }  
  const float hf = -0.125;
  return hf*(h/d)+hf;
}

vec3 normal(vec2 p) {
  vec2 v;
  vec2 w;
  vec2 e = vec2(4.0/RESOLUTION.y, 0);

  vec3 n;
  n.x = height(p + e.xy) - height(p - e.xy);
  n.y = 2.0*e.x;
  n.z = height(p + e.yx) - height(p - e.yx);

  return normalize(n);
}

vec3 effect(vec2 p, vec2 q) {
  vec2 ppp = p;
  const float s     = 1.0;
  const vec3 lp1    = vec3(1.0, 1.25, 1.0)*vec3(s, 1.0, s);
  const vec3 lp2    = vec3(-1.0, 1.25, 1.0)*vec3(s, 1.0, s);
  const vec3 lcol1  = HSV2RGB(vec3(0.06, 0.9 , .5));
  const vec3 lcol2  = HSV2RGB(vec3(0.05, 0.25, 1.0));
  const vec3 mcol   = HSV2RGB(vec3(0.1 , 0.95, 0.2));
  const float spe1  = 20.0;
  const float spe2  = 40.0;
  float aa = 2.0/RESOLUTION.y;

  float h = height(p);
  vec3  n = normal(p);

  vec3 ro = vec3(0.0, -10.0, 0.0);
  vec3 pp = vec3(p.x, 0.0, p.y);

  vec3 po = vec3(p.x, h, p.y);
  vec3 rd = normalize(ro - po);

  vec3 ld1 = normalize(lp1 - po);
  vec3 ld2 = normalize(lp2 - po);

  float diff1 = max(dot(n, ld1), 0.0);
  float diff2 = max(dot(n, ld2), 0.0);

  vec3  rn    = n;
  vec3  ref   = reflect(rd, rn);
  float ref1  = max(dot(ref, ld1), 0.0);
  float ref2  = max(dot(ref, ld2), 0.0);

  vec3 lpow1 = 0.15*lcol1/DOT2(ld1);
  vec3 lpow2 = 0.25*lcol2/DOT2(ld2);
  vec3 dm = mcol*tanh_approx(-h*5.0+0.125);
  vec3 col = vec3(0.0);
  col += dm*diff1*lpow1;
  col += dm*diff2*lpow2;
  vec3 rm = vec3(1.0)*mix(0.25, 1.0, tanh_approx(-h*1000.0));
  col += rm*pow(ref1, spe1)*lcol1;
  col += rm*pow(ref2, spe2)*lcol2;

  const float top = 10.0;

  col = aces_approx(col);
  col = sRGB(col);

  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 q = fragCoord/RESOLUTION.xy;;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec3 col = effect(p, q);  

  fragColor = vec4(col, 1.0);
}

// ---------------------------------------------------------------------------------------
//	Created by fenix in 2022
//	License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
//  Attempting to simulate the recrystalization of cooling metal. I am primarily trying
//  to demonstrate the effect of cooling rate on grain size. You should see smaller grains
//  where the material cools quickly.
//
//  I am not any kind of expert on this, but from my Googling and reading Wikipedia, I
//  believe that the two direction scenes could be called Ising (or Ising-Lenz) models, but
//  that the more general term is a Classical XY Model.
//
//  https://en.wikipedia.org/wiki/Ising_model
//  https://en.wikipedia.org/wiki/Classical_XY_model
//  https://en.wikipedia.org/wiki/Recrystallization_(metallurgy)
//
//  When above the melting point, the directions become randomized. As the material cools,
//  it becomes solid, but the atoms are still free to reorient themselves to match their
//  neighbors. Once the material cools below its recrystalization temperature, the grain
//  pattern becomes frozen, and the pattern it is frozen in has significant effects on the
//  material properties such has hardness.
//
//  In this shader there is a discrete set of directions the crystals can align to, controlled
//  by the w component of the state vector (stored in 0, 0 of buffer A). This is randomized
//  each time the scene resets or when you press the space bar. Lower counts are more
//  likely so that low (2- and 3-) color scenes have higher chances, just because I think they
//  look interesting. You can increase MAX_DIRECTIONS but at some point you can't see
//  much difference.
//
//  Clicking interrupts the attract mode and gives you mouse control over the heat. Click
//  to add heat, shift-click to remove heat. Space resets and resumes the attract mode.
//
//  You can see multi-colored micrographs similar to this in real life using a polarized
//  light and/or various colorizing etchants:
//
//  https://vacaero.com/information-resources/metallography-with-george-vander-voort/991-color-metallography.html
//
//  Buffer A computes the temperature (mostly just diffusion)
//  Buffer B computes the grain directions
//
// ---------------------------------------------------------------------------------------


const float TWO_PI = 3.141592653589793 * 2.;

vec2 circ(float a)
{
    return vec2(sin(a * TWO_PI), cos(a * TWO_PI));
}

mat2 rot2(float a)
{
    vec2 sc = vec2(sin(a), cos(a));
    return mat2(sc.x, sc.y, sc.y, -sc.x);
}

float computeSpecular(
    in float specularCoefficient,
    in float specularExponent,
    in vec3 nvNormal,
    in vec3 nvFragToLight,
    in vec3 nvFragToCam)
{
    vec3 blinnH = normalize(nvFragToLight + nvFragToCam);
    float valSpecular = pow(max(0.0, dot(nvNormal, blinnH)), specularExponent);
    valSpecular *= specularCoefficient;

    return valSpecular;
}

const float MAX_TEMP = 1.0;

vec3 fxBlackBody(float _t)
{
    _t = max(0., _t);
    vec3 temp = vec3(min(1.0, _t / MAX_TEMP), min(1.0, _t / (2.0 * MAX_TEMP)), min(1.0, _t / (3.0 * MAX_TEMP)));
    return temp * temp * temp * temp;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float dir = texelFetch(iChannel1, ivec2(fragCoord), 0).x;
    vec4 state = texelFetch(iChannel0, ivec2(0), 0);
    float directions = abs(state.w);

    vec2 steelCoord = rot2((dir + 0.5) * TWO_PI / (directions*2.)) * fragCoord * vec2(2.0, 0.004);
    vec3 steelNorm = texture(iChannel2, steelCoord/iResolution.xy).xyz;

  	vec2 p = fragCoord/iResolution.xy;
    steelNorm = normalize(steelNorm);
    vec2 c = circ(iTime*0.3)*0.4;
    vec3 lightDir = normalize(vec3(c.x + 1., 1., c.y + 1.));
    float steelSpec = computeSpecular(0.8, 15.0, steelNorm, lightDir, vec3(0.0, p.x - 0.5, p.y - 0.5));
    vec3 steel = vec3(steelSpec) * 0.9 + 0.5;

    vec3 color = sin((dir + 1.) * vec3(5, 11, 14) / directions * 0.5 + iTime*0.25) * 0.3 + 0.7;
    fragColor.xyz = steel * color;

    // Add heat radiative light
    fragColor.xyz += fxBlackBody(texelFetch(iChannel0, ivec2(fragCoord), 0).x) * 0.5;
    fragColor.a = 1.;
}

