// -----------------------------------------------------------------------------------------------
// Computes the temperature, which is mostly just diffusion cooling inwards from the edge,
// but there is a constant sapping of heat simulating the heat being lost in the Z direction also.
// -----------------------------------------------------------------------------------------------

const float HEAT_LOSS = 0.99;

float initHeat(vec2 fc)
{
    vec3 res = iResolution;
    vec2 coord = (fc/iResolution.y)*2. - vec2(iResolution.x / iResolution.y, 1.);
    switch(iFrame % 7)
    {
        case 0:
            // spiral
            return atan(coord.y, coord.x) + 4.;

        case 1:
            // phoenix
            coord.x *= 0.6;
            float sdp = sdPhoenix(coord);
            if (sdp < 0.) return 2.5;
            return 0.9;

        case 2:
            // diagonal
            return abs(coord.x + coord.y) * 1.6 + 0.9;

        case 3:
            // checker       
            vec2 fl = floor(coord * 5.);
            if ((int(fl.x) + int(fl.y)) % 2 == 0) return 2.5;
            return 0.83;

        case 4:
            // multi-spiral
            return mod((atan(coord.y, coord.x) + 4.) * 3.0, 4.0) + 0.82;

        case 5:
            // vertical fade
            return square(coord.y - 1.0) + .9;

        case 6:
            // stripes
            return step(0.5, mod(coord.x * 3.0, 1.0)) * 1.6 + 0.84;

    }
}

void handleState(inout vec4 fragColor, ivec2 ifc)
{
    vec3 h = hash(uvec3(ifc.y, ifc.x, iFrame * 984 + int(iDate.w)));

    if (iFrame == 0) fragColor = vec4(0, 0, -1, 3);
    if (iMouse.z > 0.)
    {
        // Remember last mouse position so we can draw a line
        fragColor.xy = iMouse.xy;
    }

    if (keyClick(KEY_SPACE) ||
        abs(fragColor.z) != iResolution.x * iResolution.y ||
        (fragColor.xy == vec2(0) && (iFrame % 331 == 0))) 
    {
        if (fragColor.z >= 0.)
        {
            // Compute how many directions to allow
            fragColor.w = max(2., floor(pow(h.x, 2.) * float(MAX_DIRECTIONS)));
        }
        fragColor.xy = vec2(0);
        fragColor.z = -iResolution.x * iResolution.y;
    }
    else
    {
        // Reset the reset
        fragColor.z = abs(fragColor.z);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 ifc = ivec2(fragCoord);

    fragColor = texelFetch(iChannel0, ifc, 0);

    if (ifc == ivec2(0))
    {
        handleState(fragColor, ifc);
        return;
    }

    vec4 state = texelFetch(iChannel0, ivec2(0), 0);
    if (iFrame == 0 || state.z < 0.)
    {
        fragColor.x = initHeat(fragCoord);
        fragColor.yzw = vec3(0);
    }
    else
    {
        // Diffuse heat
        float avgTemp = 0.;
        const int RANGE = 3;
        for (int x = -RANGE; x <= RANGE; ++x)
        {
            for (int y = -RANGE; y <= RANGE; ++y)
            {
                ivec2 ni = ifc + ivec2(x, y);
                if (ni == ivec2(0)) continue;
                vec4 n = texelFetch(iChannel0, ni, 0);
                avgTemp += n.x;
            }
        }

        fragColor.r = HEAT_LOSS * avgTemp / square(float(RANGE + RANGE + 1));

        // Handle mouse input
        if (iMouse.z > 0. && iMouse.w < 0. && state.xy != vec2(0))
        {
            float dist = sqrt(linePointDist2(iMouse.xy, state.xy, fragCoord));

            if (keyDown(KEY_SHIFT))
            {
                fragColor.r -= 20. / dist;
            }
            else
            {
                fragColor.r += 10. / dist;
            }

            fragColor.r = clamp(fragColor.r, 0., 2.5);
        }
    }
}

