// --------------------------------------------------------------------------------------------
// Computes the grain directions by attempting to match nearby pixels under correct conditions.
// --------------------------------------------------------------------------------------------

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 ifc = ivec2(fragCoord);

    vec3 h = hash(uvec3(ifc.y, ifc.x, iFrame + int(iDate.w)));

    fragColor = texelFetch(iChannel1, ifc, 0);

    vec4 state = texelFetch(iChannel0, ivec2(0), 0);
    float directions = abs(state.w);

    if (iFrame == 0 || state.z < 0.)
    {
        fragColor.x = floor(h.x * float(directions));
        fragColor.zw = vec2(0);
    }
    else
    {
        float t = texelFetch(iChannel0, ifc, 0).x;
        if (t > 0.99 + 0.9 * h.x)
        {
            fragColor.x = floor(h.y * float(directions));            
        }
        else if (t > 0.8 && h.x < 0.99)
        {
            // Count how many neighbors in range have each possible direction
            int counts[MAX_DIRECTIONS];
            for (int i = 0; i < int(directions); ++i)
            {
                counts[i] = 0;
            }

            const int RANGE = 3;
            for (int x = -RANGE; x <= RANGE; ++x)
            {
                for (int y = -RANGE; y <= RANGE; ++y)
                {
                    ivec2 ni = ifc + ivec2(x, y);
                    if (any(lessThan(ni, ivec2(0)))) continue;
                    if (any(greaterThanEqual(ni, ivec2(iResolution)))) continue;
                    vec4 n = texelFetch(iChannel1, ni, 0);
                    counts[int(n.x)]++;
                }
            }

            // Find the direction most popular among neighbors
            int bestCount = -1;
            int bestDir;
            bool unique;

            for (int d = 0; d < int(directions); ++d)
            {
                if (counts[d] > bestCount)
                {
                    bestCount = counts[d];
                    bestDir = d;
                    unique = true;
                }
                else if (counts[d] == bestCount)
                {
                    unique = false;
                }
            }

            if (unique)
            {
                fragColor.r = float(bestDir);
            }
        }
    }
}

