// Mach cone visualisation
//
// boat is moving left to right at constant speed (mouse-y)
// She drops 'stones' at regular interval (mouse-x), each stone
// producing a wave of wavelength L0, decaying in amplitude (Phi0).
// The accumulated result is either a move-ahead planar wave in
// the sub-sonic case, or a lagging-behind Mach cone for supersonic.
//
//   Hope the vizualisation is telling!
//   See also:  https://www.shadertoy.com/view/stGBWh
//
// skal/ (Pascal Massimino)
// License: Creative Commons Attribution-NonCommercial-ShareAlike 4.0

// Some physics
const float WS = 10.0;   // [m/px] world scale (px -> m)
const float V0 = 0.10;   // min boat speed [m/s]
const float V1 = 1.00;   // max boat speed [m/s]
const float g  = 9.81;   // gravity
const float dE = 0.5;    // max distance between emitters [m]
const float L0 = 30.;    // wavelength of emitter [m]
const float A0 = 0.5;    // global wave amplitude factor (mostly esthetic)
const float Phi0 = 9.0;  // phase-based decay length for emitter's amp

// Boat
const vec2 boat_pos0 = vec2(0.00, -0.08);        // initial boat position [m]
const vec2 boat_dir  = normalize(vec2(1., 0.0));   // direction of the boat [m/s]

// Color
const vec3 ocean_col = vec3(.3, .5, .9);   // ocean background color
const vec3 boat_col  = vec3(.3, .7, .2);   // boat color (~green)
const vec3 emit_col  = vec3(.5, .9, .2);   // emitter color
const vec3 shock_col = vec3(.6, .6, .1);   // shock-wave color (~yellow)
const vec3 mach1_col = vec3(.8, .3, .3);   // faint line materializing Mach=1

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  bool show_waves = (texelFetch(iChannel0, ivec2(32, 0),0 ).x > 0.5);
  vec2 uv = 2. * fragCoord / iResolution.yy - vec2(0.0, 1.0);  // y-screen = [-1, 1]
  vec2 M = iMouse.xy / iResolution.xy;
  if (length(iMouse.xy) < 4.) M = vec2(0.2, 0.3);

  // boat speed
  float V = mix(V0, V1, M.y);
  // emitter interval
  float dt = (dE / V) * mix(0.01, 1.12, M.x);

  // boat position and speed
  float screen_period = 2. * iResolution.x / iResolution.y;
  float cur_t = mod(iTime, 1.5 * screen_period / V);
  vec2 boat_v = V * boat_dir;
  vec2 boat_pos = boat_pos0 + cur_t * boat_v;

  // monochromatic emitter with wavelength L0
  float L = L0 / WS;
  float k = 6.2831 / L;
  float w = sqrt(g * k);

  // color mixing factors
  float emitter_mix = 0.;
  float shock_mix = 0.;
  float mach1_mix = 0.;
  float boat_mix = 1. - smoothstep(0.02, 0.03, length(uv - boat_pos));

  // draw a faint line at the Mach1 transition, for the mouse-y
  float dy = abs(2. * (sqrt(g / k) / WS - V0) / (V1 - V0) - 1. - uv.y);
  mach1_mix = 1. - step(0.005, dy);
  mach1_mix *= .4 * step(3., mod(fragCoord.x, 10.));  // dotted

  // Accumulate emitter's amplitude
  float acc = 0.;
  for (float t = -10. * dt; t <= cur_t; t += dt) {
    vec2 emitter = boat_pos0 + t * boat_v;
    float r = length(emitter - uv) * WS;  // distance to emitter
    float phi = w * (cur_t - t) - k * r;
    float base_wave = (phi >= 0.) ? cos(phi) : 0.;
    float amp = exp(-phi / Phi0);  // emitter strength decay
    float wave = amp * base_wave;
    if (show_waves) {
      acc += A0 * wave;  // accumulate wave trains
    } else {
      // represent waves with circles:
      wave = smoothstep(0.98, 1.00, base_wave) * amp;
      acc = max(acc, wave);
      // and draw spots at emitters' location:
      float emitter_strength = amp * (1. - smoothstep(0.15, 0.5, r));
      emitter_mix = max(emitter_mix, emitter_strength);
    }
  }

  // Draw the shock-wave for Mach >= 1
  float c_Mach = sqrt(g / k) / (V * WS);
  if (c_Mach <= 1.) {
     vec2 r = uv - boat_pos;
     // rotate along the boat's direction
     r = mat2(boat_dir.x, -boat_dir.y, boat_dir.y, boat_dir.x) * r;
     // fold
     r = vec2(r.x, abs(r.y));
     // and get the distance to the Mach cone
     vec2 shock_n = vec2(c_Mach, sqrt(1. - c_Mach * c_Mach));
     float d = abs(dot(r, shock_n));
     shock_mix = 1. - smoothstep(0.001, .005, d);
  }


  // final mix
  acc = 0.5 + 0.5 * acc;

  vec3 col = acc * ocean_col;
  col = mix(col, mach1_col, mach1_mix);
  col = mix(col, emit_col, emitter_mix);
  col = mix(col, shock_col, shock_mix);
  col = mix(col, boat_col, boat_mix);

  fragColor = vec4(col, 1.0);
}

