const float SHAPE_SIZE = .618;
const float CHROMATIC_ABBERATION = .02;
const float ITERATIONS = 10.;
const float INITIAL_LUMA = .4;

const float PI = 3.14159265359;
const float TWO_PI = 6.28318530718;

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float sdPolygon(in float angle, in float distance) {
  float segment = TWO_PI / 4.0;
  return cos(floor(.5 + angle / segment) * segment - angle) * distance;
}

float getColorComponent(in vec2 st, in float modScale, in float blur) {
    vec2 modSt = mod(st, 1. / modScale) * modScale * 2. - 1.;
    float dist = length(modSt);
    float angle = atan(modSt.x, modSt.y) + sin(iTime * .08) * 9.0;
    dist = sdPolygon(angle, dist);
    float shapeMap = smoothstep(SHAPE_SIZE + blur, SHAPE_SIZE - blur, dist);
    return shapeMap;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st =
        (2.* fragCoord - iResolution.xy)
        / min(iResolution.x, iResolution.y);
    vec2 origSt = st;
    st *= rotate2d(sin(iTime * .14) * .3);
    st *= (sin(iTime * .15) + 2.) * .3;
    st *= log(length(st * .28)) * .8;

    float modScale = 1.;

    vec3 color = vec3(0);
    float luma = INITIAL_LUMA;
    float blur = .5 + sin(iTime * .5) * .3;
    for (float i = 0.; i < ITERATIONS; i++) {
        vec2 center = st + vec2(sin(iTime * .12), cos(iTime * .13));
        //center += pow(length(center), 1.);
        vec3 shapeColor = vec3(
            getColorComponent(center - st * CHROMATIC_ABBERATION, modScale, blur),
            getColorComponent(center, modScale, blur),
            getColorComponent(center + st * CHROMATIC_ABBERATION, modScale, blur)        
        ) * luma;
        st *= 1.1 + getColorComponent(center, modScale, .04) * 1.;
        st *= rotate2d(sin(iTime  * .05) * .3);
        color += shapeColor;
        color = clamp(color, 0., 1.);
        if (color == vec3(1)) break;
        luma *= .6;
        blur *= .63;
    }
    const float GRADING_INTENSITY = .4;
    vec3 topGrading = vec3(
        1. - sin(iTime * 1.1 * .3) * GRADING_INTENSITY,
        1. - sin(iTime * 1.2 * .3) * GRADING_INTENSITY,
        1. - sin(iTime * 1.3 * .3) * GRADING_INTENSITY
    );
    vec3 bottomGrading = vec3(
        1. + cos(iTime * 1.4 * .3) * GRADING_INTENSITY,
        1. - cos(iTime * 1.5 * .3) * GRADING_INTENSITY,
        1. - cos(iTime * 1.6 * .3) * GRADING_INTENSITY
    );
    vec3 colorGrading = mix(topGrading, bottomGrading, length(origSt));
    fragColor = vec4(pow(color.rgb, colorGrading), 1.);
}

// License: MIT
// This shader was ported to shadertoy from my GLSL experiments on PC for a video: https://www.youtube.com/watch?v=PBI_yvz25aI
// There is very many roughs

#define PI 3.1415926535897932384626433832795
#define PI2 (PI*2.)

#define radians(x) ((x/360.0*PI2))

#define FOV 1.309

float intersectSDF(float distA,float distB) {
	return max(distA,distB);
}

float unionSDF(float distA,float distB) {
	return min(distA,distB);
}

float differenceSDF(float distA,float distB) {
	return max(distA,-distB);
}

float smoothIntersectSDF(float distA, float distB, float k) {
	float h = clamp(0.5 - 0.5*(distA - distB)/k,0.0,1.0);
	return mix(distA,distB,h) + k*h*(1.0 - h); 
}

float smoothUnionSDF(float distA,float distB,float k) {
	float h = clamp(0.5 + 0.5*(distA - distB)/k,0.0,1.0);
 	return mix(distA,distB,h) - k*h*(1.0 - h); 
}

float smoothDifferenceSDF(float distA, float distB, float k) {
	float h = clamp(0.5 - 0.5*(distB + distA)/k,0.0,1.0);
	return mix(distA,-distB,h) + k*h*(1.0 - h); 
}



mat4 TransMatrix(float x,float y,float z) {
	mat4 m = mat4(1);

	m[3][0] = x;
	m[3][1] = y;
	m[3][2] = z;

	return m;
}

mat4 RotMatrixX(float angle) {
	mat4 m = mat4(1);

	m[1][1] = cos(angle);
	m[2][1] = -sin(angle);
	m[1][2] = sin(angle);
	m[2][2] = cos(angle);

	return m;
}


mat4 RotMatrixY(float angle) {
	mat4 m = mat4(1);

	m[0][0] = cos(angle);
	m[0][2] = -sin(angle);
	m[2][0] = sin(angle);
	m[2][2] = cos(angle);

	return m;
}

mat4 RotMatrixZ(float angle) {
	mat4 m = mat4(1);

	m[0][0] = cos(angle);
	m[0][1] = sin(angle);
	m[1][0] = -sin(angle);
	m[1][1] = cos(angle);

	return m;
}



struct MarchRet_t {
	float d;
	int i;
};


struct CastRet_t {
	vec3 intersect;
	vec3 normal;
	bool casted;

	MarchRet_t march;
};

#define SMALLADD 0.004

#define DISTMAX 3000.0


MarchRet_t marchUnionSDF(MarchRet_t a,MarchRet_t b) {
	if (a.d < b.d) {
		return a;
	}
	else {
		return b;
	}
}



#define HEX_SEMISIZE 2.1
#define HEX_SIZE (HEX_SEMISIZE*2.)
#define HEX_MAXRADIUS (HEX_SEMISIZE/0.866)

#define HEXAMAP_HEIGHT 10.0



MarchRet_t HexagonSDF(vec3 ori) {
	if (ori.y > HEXAMAP_HEIGHT) {
		return MarchRet_t(ori.y - (HEXAMAP_HEIGHT - 1.),0);
	}

	ivec2 coord;

	vec3 o = ori;
	int zv = int(ori.z/5.0);
	if (ori.z < 0.) {
		zv++;
	}
	if (zv%2 == 1) {
		o.x += HEX_MAXRADIUS;
	}
	coord.x = int(o.x/5.0);
	coord.y = int(o.z/5.0);
	o.x = mod(o.x,5.0) - 2.5;
	o.z = mod(o.z,5.0) - 2.5;


	vec2 norm0 = vec2(cos(radians(30.)),sin(radians(30.)));
	vec2 norm1 = vec2(cos(radians(90.)),sin(radians(90.)));
	vec2 norm2 = vec2(cos(radians(150.)),sin(radians(150.)));

	vec2 dir = normalize(o.xz);

	float v0 = length(o.xz)*sin(acos(dot(dir,norm0)));
	float v1 = length(o.xz)*sin(acos(dot(dir,norm1)));
	float v2 = length(o.xz)*sin(acos(dot(dir,norm2)));

	float d = max(max(max(abs(v0) - HEX_SEMISIZE,abs(v1) - HEX_SEMISIZE),abs(v2) - HEX_SEMISIZE),o.y + cos(float(coord.x)*3.0) + sin(float(coord.y)*1.5) - 1.0);

	d /= 1.2;

	return MarchRet_t(d,0);
}


MarchRet_t WallsSDF(vec3 o,vec3 dir) {
	float minuscube = max(-length(o.xz) + 30.,-o.y + 40.);

	float d = differenceSDF(length(o.xz) - 50.,minuscube);

	return MarchRet_t(d,1);
}



#define CHAIN_WIDTH 800.0

MarchRet_t ChainSDF(vec3 o) {
	o = (RotMatrixZ(radians(239.98 - 30.0*(o.y/CHAIN_WIDTH)))*vec4(o,1.0)).xyz; // There is a bug, because this is a ported version

	float yv = o.y/1.5;

	float my = mod(yv,PI2);

	float av = sin(yv)*1.2;

	float d0 = length(vec2(o.x + av*float(my < PI2/2.),o.z + av*float(my >= PI2/2.))) - 0.5;
	float d1 = length(vec2(o.x + -av*float(my < PI2/2.),o.z + -av*float(my >= PI2/2.))) - 0.5;

	float d = smoothUnionSDF(d0,d1,0.25);

	d = intersectSDF(d,max(o.y - CHAIN_WIDTH,-o.y));

	return MarchRet_t(d,2);
}

MarchRet_t MainCloudSDF(vec3 o) {
	return MarchRet_t(length(o) - 50.,3);
}


MarchRet_t SceneSDF(vec3 ori,vec3 dir) {
	MarchRet_t ret;
	ret.i = 0;

	ret = HexagonSDF(ori);

	ret = marchUnionSDF(ret,WallsSDF(ori,dir));

	ret = marchUnionSDF(ret,ChainSDF(ori + vec3(0,-20,0)));

	ret = marchUnionSDF(ret,MainCloudSDF(ori + vec3(-600,-500,0)));

	return ret;
}

#define NORM_EPSILON 0.0001

vec3 SceneNormal(vec3 ori) {
	return normalize(vec3(SceneSDF(vec3(ori.x + NORM_EPSILON,ori.y,ori.z),vec3(0,1,0)).d - SceneSDF(vec3(ori.x - NORM_EPSILON,ori.y,ori.z),vec3(0,1,0)).d,
						  SceneSDF(vec3(ori.x,ori.y + NORM_EPSILON,ori.z),vec3(0,1,0)).d - SceneSDF(vec3(ori.x,ori.y - NORM_EPSILON,ori.z),vec3(0,1,0)).d,
						  SceneSDF(vec3(ori.x,ori.y,ori.z + NORM_EPSILON),vec3(0,1,0)).d - SceneSDF(vec3(ori.x,ori.y,ori.z - NORM_EPSILON),vec3(0,1,0)).d));
}



#define ITERMAX 200

#define EPSILON 0.008

CastRet_t TraceObjects(vec3 pos,vec3 dir,float outmax) {
	CastRet_t maincast;

	maincast.intersect = pos;
	maincast.normal = vec3(0.0,0.0,0.0);
	maincast.casted = false;



	vec3 ori = pos;
	float l = 0.;

	for (int i = 0; i < ITERMAX; i++) {
		MarchRet_t march = SceneSDF(ori,dir);

		float d = march.d/1.5;


		if (l > outmax) {
			break;
		}

		l += d;


		if (d < EPSILON) {
			vec3 epspos = ori - dir*EPSILON;

			maincast.casted = true;
			maincast.intersect = epspos;
			maincast.normal = SceneNormal(epspos);

			maincast.march = march;

			break;
		}

		if (d > DISTMAX) {
			break;
		}

		ori += dir*d;
	}

	return maincast;
}



vec3 ColorByMaterial(int index) {
	if (index == 0) {
        return vec3(1.0,1.0,0.95);
    }
    else if (index == 1) {
        return vec3(1.0,0.9,0.0);
    }
    else if (index == 2) {
        return vec3(0.1,0.8,0.95);
    }
    else if (index == 3) {
        return vec3(0.0,0.7,0.8);
    }
    return vec3(0);
}



bool mat_traceshadow(int index) {
    return index == 1;
}

float mat_specular(int index) {
    if (index == 0) {
        return 900.0;
    }
    else if (index == 1) {
        return 30.0;
    }
    else if (index == 2) {
        return 80.0;
    }
    else if (index == 3) {
        return 75.0;
    }
    return 0.0;
}



#define MINCOLOR 0.04

vec3 RaytraceObjects(vec3 pos,vec3 dir,vec3 camera_pos) {
	vec3 color = vec3(0.0,0.0,0.0);

	CastRet_t maincast = TraceObjects(pos,dir,DISTMAX);

	if (maincast.casted) {
		color = ColorByMaterial(maincast.march.i);

		vec3 lightpos = camera_pos + vec3(1.0,0,0);
		vec3 lightvecfull = lightpos - maincast.intersect;
		vec3 lightvec = normalize(lightvecfull);

		float cval = dot(maincast.normal,lightvec);

		bool shadowcasted = false;

		if (mat_traceshadow(maincast.march.i)) {
			vec3 shadow_pos = maincast.intersect + lightvec*SMALLADD;


			CastRet_t shadowcast = TraceObjects(shadow_pos,lightvec,length(lightvecfull));
			shadowcasted = shadowcast.casted;


			float spec = mat_specular(maincast.march.i);

			vec3 refl = normalize(maincast.normal*2.*dot(maincast.normal,lightvec) - lightvec);

			vec3 tocam = normalize(camera_pos - maincast.intersect);

			float cosangle = clamp(dot(tocam,refl),0.,1.);
			cval += pow(cosangle,spec);
		}

		if (cval < 0.0) {
			color = color*MINCOLOR;
		}
		else {
			if (shadowcasted) {
				color = color*MINCOLOR;
			}
			else {
				color = color*max(MINCOLOR,cval);
			}
		}
	}

	return color;
}





void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    float x = fragCoord.x;
	float y = fragCoord.y;

    mat4 camera = RotMatrixY(iTime*2.)*TransMatrix(150.0,300.0,1000.0 - 600.0*sin(iTime*0.97));

    mat4 camera_notrans = camera;
    camera_notrans[3][0] = 0.0;
    camera_notrans[3][1] = 0.0;
    camera_notrans[3][2] = 0.0;

	vec3 pos = (camera*vec4(0.0,0.0,0.0,1.0)).xyz;
	vec3 dir = (camera_notrans*normalize(vec4((x - iResolution.x/2.0)/iResolution.x*tan(FOV/2.0)*iResolution.x/iResolution.y,(y - iResolution.y/2.0)/iResolution.y*tan(FOV/2.0),-1.0,1.0))).xyz;

	fragColor = vec4(RaytraceObjects(pos,dir,vec3(camera[3][0],camera[3][1],camera[3][2])),1.0);
}

//	Simplex 3D Noise 
//	by Ian McEwan, Ashima Arts
//
vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}
vec4 taylorInvSqrt(vec4 r){return 1.79284291400159 - 0.85373472095314 * r;}

float snoise(vec3 v){ 
    const vec2  C = vec2(1.0/6.0, 1.0/3.0) ;
    const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);

// First corner
    vec3 i  = floor(v + dot(v, C.yyy) );
    vec3 x0 =   v - i + dot(i, C.xxx) ;

// Other corners
    vec3 g = step(x0.yzx, x0.xyz);
    vec3 l = 1.0 - g;
    vec3 i1 = min( g.xyz, l.zxy );
    vec3 i2 = max( g.xyz, l.zxy );

    //  x0 = x0 - 0. + 0.0 * C 
    vec3 x1 = x0 - i1 + 1.0 * C.xxx;
    vec3 x2 = x0 - i2 + 2.0 * C.xxx;
    vec3 x3 = x0 - 1. + 3.0 * C.xxx;

// Permutations
    i = mod(i, 289.0 ); 
    vec4 p = permute( permute( permute( 
                         i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
                     + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) 
                     + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

// Gradients
// ( N*N points uniformly over a square, mapped onto an octahedron.)
    float n_ = 1.0/7.0; // N=7
    vec3  ns = n_ * D.wyz - D.xzx;

    vec4 j = p - 49.0 * floor(p * ns.z *ns.z);  //  mod(p,N*N)

    vec4 x_ = floor(j * ns.z);
    vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)

    vec4 x = x_ *ns.x + ns.yyyy;
    vec4 y = y_ *ns.x + ns.yyyy;
    vec4 h = 1.0 - abs(x) - abs(y);

    vec4 b0 = vec4( x.xy, y.xy );
    vec4 b1 = vec4( x.zw, y.zw );

    vec4 s0 = floor(b0)*2.0 + 1.0;
    vec4 s1 = floor(b1)*2.0 + 1.0;
    vec4 sh = -step(h, vec4(0.0));

    vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
    vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

    vec3 p0 = vec3(a0.xy,h.x);
    vec3 p1 = vec3(a0.zw,h.y);
    vec3 p2 = vec3(a1.xy,h.z);
    vec3 p3 = vec3(a1.zw,h.w);

//Normalise gradients
    vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
    p0 *= norm.x;
    p1 *= norm.y;
    p2 *= norm.z;
    p3 *= norm.w;

// Mix final noise value
    vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
    m = m * m;
    return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), 
                                                                dot(p2,x2), dot(p3,x3) ) );
}

float fbm( vec3 p ) {
        float f = 0.0;
        f += 0.5000*snoise( p ); p = p*2.02;
        f += 0.2500*snoise( p ); p = p*2.03;
        f += 0.1250*snoise( p ); p = p*2.01;
        f += 0.0625*snoise( p );
        return f/0.9375;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy;
    float mouseRatio = smoothstep(100.0, 0.0, length(iMouse.xy - fragCoord.xy));
    float noise = 0.25 + fbm(vec3(uv * 12.0 + (iMouse.xy - fragCoord.xy) * mouseRatio * 0.05, iTime * 0.18 + 0.5 * mouseRatio));
    noise *= 0.25 + snoise(vec3(uv * 4.0 + 1.5, iTime * 0.15));
    fragColor = vec4(1.0, 1.0, 1.0, noise);
}

