//scene setup

const Material myMat = Material(
    vec3(1,0.5,0), //base color
    vec3(1,0.5,0), //ambient color
    vec3(1,0.5,0), //diffuse color
    vec3(1,1,1), //specular color
    0.5, //ambient amount
    1.0, //diffuse amount
    0.1, //specular amount
    1.0  //smoothness
);

Fog myFog = Fog(10.0,20.0,vec3(0.6));

PointLight myLight = PointLight(vec3(0,20,0),vec3(1));

Sphere mySphere = Sphere(vec3(0,0,10), vec3(0), vec3(0,0,10), 2.0, myMat);

Torus myTorus = Torus(vec3(0,0,0), vec3(PI/2.0,0,0), vec3(0,0,0), 2.0, 0.8, myMat);

//main drawing
void mainImage(out vec4 fragColor, in vec2 fragCoord){
    float time = iTime*0.1;
    Camera cam = createCamera(fragCoord.xy, iResolution.xy, vec3(-sin(time)*5.0,3,-cos(time)*5.0), vec3(sin(time)/10.0 + 0.5,time,0), 1.0);
    vec4 col = vec4(0,0,0,-1);

    //set depth
    col.w = torIntersect(cam.ray, myTorus);
    //col.w = sphIntersect(cam.ray, mySphere);
    if (col.w >= 0.0){ //draw the hit object
        vec3 drawnPoint = col.w*cam.ray.direction + cam.ray.origin;
        vec3 normal = torNormal(drawnPoint, myTorus);
        //vec3 normal = normalize(drawnPoint - mySphere.origin);
        vec3 lightDirection = myLight.origin - drawnPoint;
        Material mat = mySphere.mat;

        vec3 icingColor = vec3(0.7,0.7,0.6);
        vec3 baseColor = vec3(0.5,0.25,0) - 0.15*voronoi3d(rotate3d(drawnPoint, -myTorus.rotation, myTorus.origin));

        float icingInterpolant = voronoi3d(rotate3d(drawnPoint, -myTorus.rotation, myTorus.origin)*2.0) + 2.0*rotate3d(drawnPoint, -myTorus.rotation, myTorus.origin).z + 0.5;
        vec3 b = mix(icingColor, baseColor, smoothstep(0.0, 0.01, icingInterpolant));
        normal = mix(roundNormal(0.6, icingInterpolant, normal), normal, smoothstep(0.0, 0.2, icingInterpolant));

        float cracks = voronoi3dedges(rotate3d(drawnPoint, -myTorus.rotation, myTorus.origin)*10.0)*0.1;

        float d = clamp(dot(normal,normalize(lightDirection))*mat.diffuse,0.0,1.0);
        float s;
        if (icingInterpolant < 0.02){ //icing
            s = pow(max(dot(reflect(cam.ray.direction,normal),normalize(lightDirection)),0.0),mat.smoothness + cracks*10.0)*(mat.specular + cracks*10.0);
        } else { //dough
            s = pow(max(dot(reflect(cam.ray.direction,normal),normalize(lightDirection)),0.0),mat.smoothness)*mat.specular;
        }
        float a = mat.ambient;
        col.rgb = b*a + b*d + mat.specularColor*s;
        //col.rgb = normal;
        col.rgb = mix(col.rgb,myFog.color,smoothstep(myFog.start, myFog.end, col.w));

    } else { //draw the sky

        float interpolant = dot(cam.ray.direction,vec3(0,1,0));
        vec3 water = vec3(0.3,0.4,0.8);
        vec3 sky = vec3(0,0.6,1);
        vec3 haze = vec3(0.9);
        if (interpolant < 0.0) {
            col.rgb = mix(haze, water, -interpolant);
        } else {
            col.rgb = mix(haze, sky, interpolant);
        }
    }
    fragColor = col;
}

