const float PI = 3.14;

struct Material {
    vec3 baseColor;
    vec3 ambientColor;
    vec3 diffuseColor;
    vec3 specularColor;
    float ambient;
    float diffuse;
    float specular;
    float smoothness;
};

struct Ray {
    vec3 origin;
    vec3 direction;
};

struct Fog {
    float start;
    float end;
    vec3 color;
};

struct Sphere {
    vec3 origin;
    vec3 rotation;
    vec3 pivot;
    float radius;
    Material mat;
};

struct Torus {
    vec3 origin;
    vec3 rotation;
    vec3 pivot;
    float width;
    float radius;
    Material mat;
};

struct PointLight {
    vec3 origin;
    vec3 color;
};

struct Camera {
    vec3 position;
    Ray ray;
};

//returns a point rotated in 3d (radians)
vec3 rotate3d(vec3 point, vec3 angle, vec3 origin){ 
    point -= origin; //subtract pivot
    //calculate consts
    float cx = cos(angle.x);
    float sx = sin(angle.x);
    float cy = cos(angle.y);
    float sy = sin(angle.y);
    float cz = cos(angle.z);
    float sz = sin(angle.z);
    //rotate point
    point *= mat3(
        cy*cz, sx*sy*cz - cx*sz, cx*sy*cz + sx*sz,
        cy*sz, sx*sy*sz + cx*cz, cx*sy*sz - sx*cz,
        -sy, sx*cy, cx*cy
    );
    point += origin; //add pivot
    return point;
}

//camera constructor
Camera createCamera(vec2 fragCoord, vec2 resolution, vec3 cameraPos, vec3 camRot, float frustumLength){
    vec3 uv = vec3((fragCoord - resolution*0.5)/resolution.y * 2.0, frustumLength);
    Ray ray = Ray(rotate3d(uv,camRot,vec3(0)) + cameraPos, rotate3d(normalize(uv), camRot, vec3(0)));

    return Camera(cameraPos, ray);
}

// https://www.shadertoy.com/view/4d2XWV
// sphere of size ra centered at point ce
// returns x = first intersect, y = second intersect
float sphIntersect(in Ray ray, in Sphere sph){
    vec3 oc = ray.origin - sph.origin;
    float b = dot( oc, ray.direction );
    float c = dot( oc, oc ) - sph.radius*sph.radius;
    float h = b*b - c;
    if( h<0.0 ) return -1.0; // no intersection
    h = sqrt( h );
    return -b-h;
}

vec3 sphNormal(in vec3 pos, in Sphere sph){
    pos -= sph.origin; 
    return normalize(pos);
}

// https://www.shadertoy.com/view/4sBGDy
// input: ray origin, ray direction, torus origin, torus width, torus radius
float torIntersect(in Ray ray, in Torus tor){
    vec3 ro = ray.origin - tor.origin;
    float po = 1.0;

    vec3 rd = ray.direction = rotate3d(ray.direction,-tor.rotation,vec3(0));
    ro = rotate3d(ray.origin,-tor.rotation,tor.pivot);

    float Ra2 = tor.width*tor.width;
    float ra2 = tor.radius*tor.radius;

    float m = dot(ro,ro);
    float n = dot(ro,rd);
    float k = (m + Ra2 - ra2)/2.0;
    float k3 = n;
    float k2 = n*n - Ra2*dot(rd.xy,rd.xy) + k;
    float k1 = n*k - Ra2*dot(rd.xy,ro.xy);
    float k0 = k*k - Ra2*dot(ro.xy,ro.xy);

    if( abs(k3*(k3*k3-k2)+k1) < 0.01 )
    {
        po = -1.0;
        float tmp=k1; k1=k3; k3=tmp;
        k0 = 1.0/k0;
        k1 = k1*k0;
        k2 = k2*k0;
        k3 = k3*k0;
    }

    float c2 = k2*2.0 - 3.0*k3*k3;
    float c1 = k3*(k3*k3-k2)+k1;
    float c0 = k3*(k3*(c2+2.0*k2)-8.0*k1)+4.0*k0;
    c2 /= 3.0;
    c1 *= 2.0;
    c0 /= 3.0;
    float Q = c2*c2 + c0;
    float R = c2*c2*c2 - 3.0*c2*c0 + c1*c1;
    float h = R*R - Q*Q*Q;

    if( h>=0.0 )  
    {
        h = sqrt(h);
        float v = sign(R+h)*pow(abs(R+h),1.0/3.0); // cube root
        float u = sign(R-h)*pow(abs(R-h),1.0/3.0); // cube root
        vec2 s = vec2( (v+u)+4.0*c2, (v-u)*sqrt(3.0));
        float y = sqrt(0.5*(length(s)+s.x));
        float x = 0.5*s.y/y;
        float r = 2.0*c1/(x*x+y*y);
        float t1 =  x - r - k3; t1 = (po<0.0)?2.0/t1:t1;
        float t2 = -x - r - k3; t2 = (po<0.0)?2.0/t2:t2;
        float t = 1e20;
        if( t1>0.0 ) t=t1;
        if( t2>0.0 ) t=min(t,t2);
        return t;
    }

    float sQ = sqrt(Q);
    float w = sQ*cos( acos(-R/(sQ*Q)) / 3.0 );
    float d2 = -(w+c2); if( d2<0.0 ) return -1.0;
    float d1 = sqrt(d2);
    float h1 = sqrt(w - 2.0*c2 + c1/d1);
    float h2 = sqrt(w - 2.0*c2 - c1/d1);
    float t1 = -d1 - h1 - k3; t1 = (po<0.0)?2.0/t1:t1;
    float t2 = -d1 + h1 - k3; t2 = (po<0.0)?2.0/t2:t2;
    float t3 =  d1 - h2 - k3; t3 = (po<0.0)?2.0/t3:t3;
    float t4 =  d1 + h2 - k3; t4 = (po<0.0)?2.0/t4:t4;
    float t = 1e20;
    if( t1>0.0 ) t=t1;
    if( t2>0.0 ) t=min(t,t2);
    if( t3>0.0 ) t=min(t,t3);
    if( t4>0.0 ) t=min(t,t4);
    if (t == 1e20) t = -1.0;
    return t;
}

vec3 torNormal( in vec3 pos, in Torus tor){
    pos -= tor.origin; 
    pos = rotate3d(pos,-tor.rotation,tor.pivot);
    vec3 normal = normalize(pos*(dot(pos,pos) - tor.radius*tor.radius - tor.width*tor.width*vec3(1,1,-1)));
    return rotate3d(normal,tor.rotation,tor.pivot);
}

//given a radius, distance, and normal, bends the normal to add a circular bevel
vec3 roundNormal(float r, float d, vec3 normal){
    if (d < -r) return vec3(0,1,0);
    float b = d*d + 2.0*d*r;
    float m = (d+r)/sqrt(abs(b));
    float l = sqrt(d*d*(m*m + 1.0));
    vec2 v = sign(d)*vec2(-m*d,d)/l;
    vec2 a = normalize(normal.xz)*v.x;
    vec3 x = vec3(-a.y,v.y,-a.x);
    return x/length(x);
}

//  3 out, 3 in...
vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);

}

float voronoi3d(vec3 uv){
    float n = 999.0;
    for (int x = -1; x < 2; x++){
        for (int y = -1; y < 2; y++){
            for (int z = -1; z < 2; z++){
                n = min(n,distance(floor(uv) + vec3(x,y,z) + hash33(floor(uv) + vec3(x,y,z)),uv));
            }
        }
    }
    return n;
}

float powDist(vec3 a, vec3 b, float p){
    vec3 d = abs(a-b);
    return pow(d.x,p) + pow(d.y,p) + pow(d.z,p);
}

float voronoi3dedges(vec3 uv){
    float d1 = 999.0;
    float d2 = 999.0;
    for (int x = -1; x < 2; x++){
        for (int y = -1; y < 2; y++){
            for (int z = -1; z < 2; z++){
                float d = powDist(floor(uv) + vec3(x,y,z) + hash33(floor(uv) + vec3(x,y,z)),uv,3.0);
                if (d < d1) {
                    d2 = d1;
                    d1 = d;
                } else if (d < d2){
                    d2 = d;
                }
            }
        }
    }
    return abs(d1-d2);
}

