// based on a bunch of math I found on the internet, put into code
// http://large.stanford.edu/courses/2007/ph210/pelc2/

// maximum number of harmonics to compute
// fiddle with this number to see what it's like with less harmonics
const uint MAX_HARMONICS = 50u;

// fundamental frequencies of each string
// changing these will "tune" the guitar
const float FUNDAMENTAL[6] = float[6](
    329.63, // E4
    246.94, // B3
    196.00, // G3
    146.83, // D3
    110.00, // A2
    082.41  // E2
);

// length of string (approx 25 inches, standard guitar string length)
const float L = 0.635;
// height of pluck (12.5 cm, just a random number to make it clearly audible)
const float h = 0.125;
// position of pluck along string (5 inches from lower bridge)
const float d = 0.15;

// Damping coefficient (bigger = shorter)
const float GAMMA = 2.5;

// String stiffness coefficient
const float b = 0.008;

// The stuff below is code that isn't really worth fiddling with
// =============================================================

const float TAU = 6.283185307179586476925286766559;
const float PI = TAU / 2.0;

vec2 mainSound(int samp, float time) {
    float sig = 0.0;
    // for each string
    for (uint s = 0u; s < 6u; s++) {
        // repeat at a different offset
        // I fixed some floating point errors by using the sample number directly and using integers
        //float t = mod(time + (float(s) / 6.), 8./6.);
        float t = float((6 * samp + int(iSampleRate) * int(s)) % (8 * int(iSampleRate))) / (6. * iSampleRate);
        // for each harmonic
        for (uint n = 0u; n < MAX_HARMONICS; n++) {
            // amplitude for each harmonic
            float a_n = ((2. * h * L * L)/(PI * PI * d * (L - d) * float(n+1u) * float(n+1u))) * sin((float(n+1u) * PI * d)/L);
            // frequency for each harmonic
            float f_n = float(n+1u) * FUNDAMENTAL[s] * sqrt(1. + b * b * float(n+1u) * float(n+1u));
            // add value to total sound signal, with exponential falloff
            sig += a_n * sin(TAU * f_n * t) * exp(-float(n+1u) * GAMMA * t);
        }
    }

    return vec2(sig);
}

