// Note that MIPmap works in direction xy, but not z. ( to be implemented ).
// Only 2 faces are used over 6: Other volumes could be used to store velocity or shadowing data.
// For efficiency we rely on hardware texture interpolation, but you could implement degree 3 to improve quality.

#define keyToggle(a)  ( texelFetch(iChannel3,ivec2(a,2),0).x > 0.)

#define H(p) fract( sin((p+17.1)*mat3(127.1,311.7, 74.7,  269.5,183.3,246.1,  113.5,271.9,124.6))*3e4 )

float _z = .3;
vec3 hash( vec3 x ) 
{
 // float s = 0.;                          // standard Perlin noise
    float s = mod(x.x+x.y+x.z,2.)*2.-1.;   // flow noise checkered rotation direction
 // float s = mod(floor(x.x)+floor(x.y),2.)*2.-1.;   // should be this but x is integer
 // float s = 1.;                         // flow noise universal rotation direction
 // s *= iTime;                           // same rotation speed at all scales
    s *= iTime/ _z;                       // rotation speed increase with small scale    

    x = 2.*H(x) - 1.;
    x.xy *= mat2(cos( s + vec4(0,33,11,0))); // rotating gradients. rot: https://www.shadertoy.com/view/XlsyWX
    return x;
}

float noise( vec3 p )
{
    vec3 i = floor( p ),
         f = fract( p ),
	     u = f*f*(3.-2.*f);

#define P(x,y,z) dot( hash( i + vec3(x,y,z) ) , f - vec3(x,y,z) )
    return mix( mix( mix( P(0,0,0), P(1,0,0), u.x),
                     mix( P(0,1,0), P(1,1,0), u.x), u.y),
                mix( mix( P(0,0,1), P(1,0,1), u.x),
                     mix( P(0,1,1), P(1,1,1), u.x), u.y) , u.z );
}

float perlin( vec3 p )  //fractal noise
{	
    mat3 m =  mat3(2); // mat2( 1.6,  1.2, -1.2,  1.6 );
           // mat3( 1.6,  1.2, 1.4,    1.3, -1.2,  1.6, -1.4, -1.6, 1.2 ) / 2.;
    float v  = 0.,s = 1.;
    for( int i=0; i < 5; i++, s /= 2. ) {  _z = s; // for flownoise
        v += s*noise( p ); p *= m; p-=.5;
    }
    return v/2.;
}

vec4 getVoxel(vec3 q) {  // ----------- should really be in Common, but iChannel0 forbiden there... :-( 
// ( used only for shadowing )
    if ( q.x<0. || q.y < 0. || q.z < 0. || q.x > 1. || q.y > 1. || q.z > 1. ) return vec4(0);
    float t = 64.* fract(2.*q.z); 
    int   n = q.z<.5 ? 0 : 1,
          i = int(t);
    vec4 O =       T( ( q.xy + vec2( i%8, i/8 ) )/8., n ); return O;
    i++; if (i>63) n++, i -= 64; // if (n>1) return O;
    return mix( O, T( ( q.xy + vec2( i%8, i/8 ) )/8., n ), fract(t) ); // +.05;
 }


vec4 setVoxel( vec3 P ) {  // ---------- set your volume definition here. 

 // P = floor(P*128.); return vec4(P.z==float(iFrame%128));    // debug

    P = 2.*P-1.;                                               // centering
    vec4 O;
 // O = vec4( length( P ) < .8 );
 // O = vec4( smoothstep( 4.* 2./128., 0., abs( length(P) -.8 ) ) );
 // O = vec4(max(0., noise( 8.*P + 171.) ));
 // O = vec4(max(0., perlin( 4.*P + 171.) ));
 // O = vec4( .3* smoothstep( .1, 0., abs( length( P+ perlin(4.*P) ) -.8) ) );
    float v = .1 * smoothstep( .1, 0., abs( length( P+ perlin(4.*P) ) -.8) ); O = v* vec4(exp(-v/vec3(8,.25,.12)),1);
 // O = vec4( smoothstep( .3, 0., abs( length(P) -.75 ) ) * max(0., perlin(4.*P) ) );
 // float v = smoothstep( .3, 0., abs( length(P) -.75 ) ) * max(0., perlin(4.*P) ); O = v* vec4(exp(v*vec3(1,.1,.01)),1);
 // float v = smoothstep( .3, 0., abs( length(P) -.75 ) ) * .4*smoothstep(.05,.2, perlin(4.*P) ); O = v* vec4(exp(v*vec3(1,.1,.01)),1);

    // ---------------------------------- computing shadows -------------------
    // ( maybe would be cleaner to stores light in a separate volume.
    //   e.g. it would allows to have still density with animated shadowing ).
    float t = 1.; P = .5+.5*P;
    for (float z = P.z; z < 1.; z += 1./128. )
        t *= 1.- getVoxel( vec3(P.xy,z) ).a;
    O.rgb *= .1 + 2.*exp(-3.*t);
    return O;
}

void mainCubemap( out vec4 O, vec2 U, vec3 o, vec3 D )
{
    vec3 A = abs(D);
    int n = A.x > A.y ? A.x > A.z ? 0 : 2 : A.y > A.z ? 1 : 2; // faceID
    if (D[n]<0.) n += 3;

    if ( n > 1 ) { O*=0.; return; }                            // we use only 2 faces.
    if ( keyToggle(32) && iFrame>0 ) { O = texture(iChannel0,D); return; }        // uncomment if data not evolving.

    U = ( 8.*floor(U) + .5 ) / 1024.;
    vec2 I = floor(U);                                         // tile ID = (I,n)
    vec3 P = vec3( fract(U), ( I.x +8.*I.y + 64.*float(n) +.5 ) /128. ); // 3D pos in normalized volume [0,1] of resolution 128
    O = setVoxel(P);
}

