// License: MIT
// This shader was ported to shadertoy from my GLSL experiments on PC for a video: https://www.youtube.com/watch?v=PBI_yvz25aI
// There is many roughs

#define PI 3.1415926535897932384626433832795
#define PI2 (PI*2.)

#define CirclePart(angle) (angle*PI2)

#define FOV 1.309

float intersectSDF(float distA,float distB) {
	return max(distA,distB);
}

float unionSDF(float distA,float distB) {
	return min(distA,distB);
}

float differenceSDF(float distA,float distB) {
	return max(distA,-distB);
}

float smoothIntersectSDF(float distA, float distB, float k) {
	float h = clamp(0.5 - 0.5*(distA - distB)/k,0.0,1.0);
	return mix(distA,distB,h) + k*h*(1.0 - h); 
}

float smoothUnionSDF(float distA,float distB,float k) {
	float h = clamp(0.5 + 0.5*(distA - distB)/k,0.0,1.0);
 	return mix(distA,distB,h) - k*h*(1.0 - h); 
}

float smoothDifferenceSDF(float distA, float distB, float k) {
	float h = clamp(0.5 - 0.5*(distB + distA)/k,0.0,1.0);
	return mix(distA,-distB,h) + k*h*(1.0 - h); 
}



mat4 TransMatrix(float x,float y,float z) {
	mat4 m = mat4(1);

	m[3][0] = x;
	m[3][1] = y;
	m[3][2] = z;

	return m;
}

mat4 RotMatrixY(float angle) {
	mat4 m = mat4(1);

	m[0][0] = cos(angle);
	m[0][2] = -sin(angle);
	m[2][0] = sin(angle);
	m[2][2] = cos(angle);

	return m;
}

mat4 RotMatrixX(float angle) {
	mat4 m = mat4(1);

	m[0][0] = cos(angle);
	m[0][1] = sin(angle);
	m[1][0] = -sin(angle);
	m[1][1] = cos(angle);

	return m;
}



struct MarchRet_t {
	float d;
	int i;
};


struct CastRet_t {
	vec3 intersect;
	vec3 normal;
	bool casted;

	MarchRet_t march;
};

#define SMALLADD 0.004

#define DISTMAX 3000.0





MarchRet_t marchSmoothUnionSDF(MarchRet_t a,MarchRet_t b,float k) {
	int i = 0;
	if (a.d < b.d) {
		i = a.i;
	}
	else {
		i = b.i;
	}

	return MarchRet_t(smoothUnionSDF(a.d,b.d,k),i);
}


MarchRet_t marchIntersectSDF(MarchRet_t a,MarchRet_t b) {
	return MarchRet_t(max(a.d,b.d),a.i);
}

MarchRet_t marchUnionSDF(MarchRet_t a,MarchRet_t b) {
	if (a.d < b.d) {
		return a;
	}
	else {
		return b;
	}
}

MarchRet_t marchDifferenceSDF(MarchRet_t a,MarchRet_t b) {
	int i = 0;
	if (a.d > 0.) {
		i = a.i;
	}
	else {
		i = b.i;
	}

	return MarchRet_t(max(a.d,-b.d),i);
}




MarchRet_t RoomSDF(vec3 o) {
	float cube0 = max(max(abs(o.x) - 5.,abs(o.z) - 8.),abs(o.y) - 5.);
	o.x -= 8.9;

	float cube1 = max(max(abs(o.x) - 4.,abs(o.z) - 4.),abs(o.y) - 2.);

	float d = smoothUnionSDF(cube0,cube1,(sin(CirclePart(float(iFrame%80)/80.0)) + 1.0)/8.0 + 0.25);

	return MarchRet_t(-d,0);
}



vec3 obj0_pos() {
    return vec3(8,1.0 + cos(iTime*2.3)*1.2,1.0 + sin(iTime*3.0)*2.0);
}
vec3 obj1_pos() {
    return vec3(8,-1.0 + cos(iTime*4.3)*1.5,2.0 + sin(iTime*2.1)*2.0);
}
vec3 obj2_pos() {
    return vec3(8,-1.2 + cos(iTime*7.3)*1.1,-3.0 + sin(iTime*1.3)*1.5);
}
vec3 obj3_pos() {
    return vec3(8,0.3 + cos(iTime*1.5)*0.7,-2.5 + sin(iTime*2.2)*2.2);
}
vec3 obj4_pos() {
    return vec3(8,0.0 + cos(iTime*2.1)*1.1,1.2 + sin(iTime*1.3)*3.0);
}

MarchRet_t shapesSDF(vec3 o) {
	float t = CirclePart(float(iFrame%200)/200.0);

	float sphere0 = length(o - obj0_pos()) - 0.7;

	float sphere1 = length(o - obj1_pos()) - 0.5;

	float sphere2 = length(o - obj2_pos()) - 0.25;

	vec3 cbo = o - obj3_pos();
	float sphere3 = max(max(abs(cbo.x) - 0.7,abs(cbo.z) - 0.7),abs(cbo.y) - 0.5);

	float sphere4 = length(o - obj4_pos()) - 0.8;

	float d = smoothUnionSDF(sphere0,sphere1,1.5);
	d = smoothUnionSDF(d,sphere2,1.5);
	d = smoothUnionSDF(d,sphere3,1.5);
	d = smoothUnionSDF(d,sphere4,1.5);

	return MarchRet_t(d,1);
}


MarchRet_t effectSDF(vec3 o) {
	float tangle = CirclePart(float(iFrame%30)/30.0);

	float powbase = cos(CirclePart(float(iFrame%70)/70.0))/2.0 + 2.8;

	float d = intersectSDF(length(o.yz) - 1.2,abs(o.x) - ((cos(length(o.yz*(pow(powbase,length(o.yz) + 1.5))) - tangle) + 1.2)*0.05));

	return MarchRet_t(d,2);
}


MarchRet_t SceneSDF(vec3 ori,vec3 dir) {
	MarchRet_t ret;
	ret.i = 0;

	ret = RoomSDF(ori);

	ret = marchUnionSDF(ret,shapesSDF(ori));

	ret = marchUnionSDF(ret,effectSDF(ori));

	return ret;
}

#define NORM_EPSILON 0.0001

vec3 SceneNormal(vec3 ori) {
	return normalize(vec3(SceneSDF(vec3(ori.x + NORM_EPSILON,ori.y,ori.z),vec3(0,1,0)).d - SceneSDF(vec3(ori.x - NORM_EPSILON,ori.y,ori.z),vec3(0,1,0)).d,
						  SceneSDF(vec3(ori.x,ori.y + NORM_EPSILON,ori.z),vec3(0,1,0)).d - SceneSDF(vec3(ori.x,ori.y - NORM_EPSILON,ori.z),vec3(0,1,0)).d,
						  SceneSDF(vec3(ori.x,ori.y,ori.z + NORM_EPSILON),vec3(0,1,0)).d - SceneSDF(vec3(ori.x,ori.y,ori.z - NORM_EPSILON),vec3(0,1,0)).d));
}



#define ITERMAX 300

#define EPSILON 0.008

CastRet_t TraceObjects(vec3 pos,vec3 dir,float outmax) {
	CastRet_t maincast;

	maincast.intersect = pos;
	maincast.normal = vec3(0.0,0.0,0.0);
	maincast.casted = false;





	vec3 ori = pos;
	float l = 0.;

	for (int i = 0; i < ITERMAX; i++) {
		MarchRet_t march = SceneSDF(ori,dir);

		float d = march.d/1.5;


		if (l > outmax) {
			break;
		}

		l += d;


		if (d < EPSILON) {
			vec3 epspos = ori - dir*EPSILON;

			maincast.casted = true;
			maincast.intersect = epspos;
			maincast.normal = SceneNormal(epspos);

			maincast.march = march;

			break;
		}

		if (d > DISTMAX) {
			break;
		}

		ori += dir*d;
	}

	return maincast;
}

vec3 ColorByMaterial(int index) {
	if (index == 0) {
        return vec3(1.0,1.0,0.95);
    }
    else if (index == 1) {
        return vec3(1.0,0.9,0.0);
    }
    else if (index == 2) {
        return vec3(0.1,0.8,0.95);
    }
    else if (index == 3) {
        return vec3(0.0,0.7,0.8);
    }
    return vec3(0);
}



bool mat_traceshadow(int index) {
    return index == 1;
}

float mat_specular(int index) {
    if (index == 0) {
        return 900.0;
    }
    else if (index == 1) {
        return 30.0;
    }
    else if (index == 2) {
        return 80.0;
    }
    else if (index == 3) {
        return 75.0;
    }
    return 0.0;
}



#define MINCOLOR 0.04

vec3 RaytraceObjects(vec3 pos,vec3 dir,vec3 camera_pos) {
	vec3 color = vec3(0.0,0.0,0.0);

	CastRet_t maincast = TraceObjects(pos,dir,DISTMAX);

	if (maincast.casted) {
		color = ColorByMaterial(maincast.march.i);

		vec3 lightpos = vec3(-2.7,0,-2.0);
		vec3 lightvecfull = lightpos - maincast.intersect;
		vec3 lightvec = normalize(lightvecfull);

		float cval = dot(maincast.normal,lightvec);

		bool shadowcasted = false;

		if (mat_traceshadow(maincast.march.i)) {
			vec3 shadow_pos = maincast.intersect + lightvec*SMALLADD;


			CastRet_t shadowcast = TraceObjects(shadow_pos,lightvec,length(lightvecfull));
			shadowcasted = shadowcast.casted;


			float spec = mat_specular(maincast.march.i);

			vec3 refl = normalize(maincast.normal*2.*dot(maincast.normal,lightvec) - lightvec);

			vec3 tocam = normalize(camera_pos - maincast.intersect);

			float cosangle = clamp(dot(tocam,refl),0.,1.);
			cval += pow(cosangle,spec);
		}

		if (cval < 0.0) {
			color = color*MINCOLOR;
		}
		else {
			if (shadowcasted) {
				color = color*MINCOLOR;
			}
			else {
				color = color*max(MINCOLOR,cval);
			}
		}
	}

	return color;
}





void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    float x = fragCoord.x;
	float y = fragCoord.y;

    mat4 camera = RotMatrixY(-PI/2. + iMouse.x/iResolution.x - 0.3)*TransMatrix(-3.0 + iMouse.y/iResolution.y*3.0,0.0,4.0);

    mat4 camera_notrans = camera;
    camera_notrans[3][0] = 0.0;
    camera_notrans[3][1] = 0.0;
    camera_notrans[3][2] = 0.0;

	vec3 pos = (camera*vec4(0.0,0.0,0.0,1.0)).xyz;
	vec3 dir = (camera_notrans*normalize(vec4((x - iResolution.x/2.0)/iResolution.x*tan(FOV/2.0)*iResolution.x/iResolution.y,(y - iResolution.y/2.0)/iResolution.y*tan(FOV/2.0),-1.0,1.0))).xyz;

	fragColor = vec4(RaytraceObjects(pos,dir,vec3(camera[3][0],camera[3][1],camera[3][2])),1.0);
}

