float GeoSphereSDF(vec3 o,float r) {
	vec3 onorm = normalize(o);

	float value = -max(abs(onorm.x),abs(onorm.z));
	if (value < -0.25) {
		value = -0.25;
	}

	return length(o) - (r + 1.2*value);
}

float CubeSDF(vec3 o,float size) {
	vec3 d = abs(o) - vec3(size/2.,size/2.,size/2.);

	float distin = min(max(d.x, max(d.y, d.z)), 0.0);
	float distout = length(max(d, 0.0));

	return distin + distout;
}



float MultiSphereCubeSDF(vec3 o,float r,float size,float l) {
	float ly = l*2.;

	float mx = mod(o.x,l);
	float my = mod(o.y,ly);
	float mz = mod(o.z,l);

	bool xodd = (mod(o.x,l*2.0) > 0. && mod(o.x,l*2.0) < l);
	bool zodd = (mod(o.z,l*2.0) > 0. && mod(o.z,l*2.0) > l);

	o.x = mx - l/2.;
	o.y = my - ly/2.;
	o.z = mz - l/2.;

