#define M_PI 3.1415926535897932384626433832795
#define M_PI2 (M_PI*2.0)

#define FOV 1.309

#define SMALLADD 0.004

float intersectSDF(float distA,float distB) {
	return max(distA,distB);
}

float unionSDF(float distA,float distB) {
	return min(distA,distB);
}

float differenceSDF(float distA,float distB) {
	return max(distA,-distB);
}



mat4 TransMatrix(float x,float y,float z) {
	mat4 m = mat4(1);

	m[3][0] = x;
	m[3][1] = y;
	m[3][2] = z;

	return m;
}

mat4 RotMatrixY(float angle) {
	mat4 m = mat4(1);

	m[0][0] = cos(angle);
	m[0][2] = -sin(angle);
	m[2][0] = sin(angle);
	m[2][2] = cos(angle);

	return m;
}



float SphereSDF(vec3 o,vec3 pos,float r) {
	return length(o - pos) - r*r;
}

float CubeSDF(vec3 o,vec3 pos,float size) {
	vec3 d = abs(o - pos) - vec3(size,size,size);

	float distin = min(max(d.x, max(d.y, d.z)), 0.0);
	float distout = length(max(d, 0.0));

	return distin + distout;
}



float OneObjSDF(vec3 ori,vec3 pos) {
	return differenceSDF(unionSDF(CubeSDF(ori,pos + vec3(0,0,0),1.0),SphereSDF(ori,pos + vec3(0,0,0),1.15)),SphereSDF(ori,pos + vec3(-0.8 - 0.4*sin(float(iFrame%45)/45.0*M_PI2),1.0*sin(float(iFrame%300)/300.0*M_PI2),1.0*sin(float(iFrame%500)/500.0*M_PI2)),0.8));
}


float SceneSDF(vec3 ori) {
	float angle = float(iFrame%100)/100.0*M_PI2;

	return unionSDF(unionSDF(unionSDF(unionSDF(OneObjSDF(ori,vec3(2.0*cos(angle),2.0*cos(angle + 0.65) + cos(float(iFrame%20)/20.0*M_PI2),30.0*cos(angle + 1.23))),
	                                           OneObjSDF(ori,vec3(-5.0*cos(angle + 0.3),23.0*cos(angle + 0.3),23.0*cos(angle + 1.3)))),
	                                           OneObjSDF(ori,vec3(-12.3*cos(angle + 1.6),-4.0*cos(angle + 1.12),12.0*cos(angle + 0.54)))),
	                                           OneObjSDF(ori,vec3(0,0,0))),
	                                           OneObjSDF(ori,vec3(30.0*cos(angle),30.0*sin(angle),30.0*tan(angle))));
}

