float fullCycle = 10.0;
float timeToVisible = 1.0;
float timeToInvisible = 1.0;
float timeVisible = 2.0;

float hash(vec2 p)  // replace this by something better
{
    p  = 50.0*fract( p*0.3183099 + vec2(0.71,0.113));
    return -1.0+2.0*fract( p.x*p.y*(p.x+p.y) );
}

float getDistance(vec2 p) {
    return sqrt((p.x - 0.5) * (p.x - 0.5) + (p.y - 0.5) * (p.y - 0.5));
}

float easeInOutBack(float x) {
float c1 = 1.70158;
float c2 = c1 * 1.525;

return x < 0.5
  ? (pow(2.0 * x, 2.0) * ((c2 + 1.0) * 2.0 * x - c2)) / 2.0
  : (pow(2.0 * x - 2.0, 2.0) * ((c2 + 1.0) * (x * 2.0 - 2.0) + c2) + 2.0) / 2.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvMask = fract(fragCoord/iResolution.xy * gridSize);
