    vec2 uvID = round(uv * gridSize) / gridSize;

    float id = hash(uvID);


    float normalTime = mod(iTime + id * 10.0, fullCycle);

    float fullDuration = timeToVisible + timeToInvisible + timeVisible;
    float timeStartToInvisible = timeToVisible + timeVisible;

    float baseMaskLevel = normalTime > fullDuration ? 0.0 : easeInOutBack(clamp(normalTime / timeToVisible, 0.0, 1.0));
    float otherMaskLevel = normalTime < timeToVisible + timeVisible || normalTime > fullDuration ? 0.0 : easeInOutBack(clamp((normalTime - timeToVisible - timeVisible) / timeToInvisible, 0.0, 1.0));

    float mask = getDistance(uvMask) < maskSize * baseMaskLevel ? 1.0 : 0.0; 
    float mask2 = getDistance(uvMask) < maskSize * otherMaskLevel ? 0.0 : 1.0;



    fragColor = vec4(mask * mask2, 0.0, 0.0, 1.0);

}

