// Code from https://www.jcgt.org/published/0011/03/05/
// Also: https://80.lv/articles/implementing-morten-mikkelsen-s-method-of-working-with-hex-tiles/

#define M_PI 3.14159

void TriangleGrid(out float w1, out float w2, out float w3,
	out ivec2 vertex1, out ivec2 vertex2, out ivec2 vertex3,
	vec2 st) {
	// Scaling of the input
	st *= 2.0 * sqrt(3.0);
	// Skew input space into simplex triangle grid.
	mat2 gridToSkewedGrid =
		mat2(1.0, -0.57735027, 0.0, 1.15470054);
	vec2 skewedCoord = (st * gridToSkewedGrid);
	ivec2 baseId = ivec2(floor(skewedCoord));
	vec3 temp = vec3(fract(skewedCoord), 0.0);
	temp.z = 1.0 - temp.x - temp.y;
	float s = step(0.0, -temp.z);
	float s2 = 2.0 * s - 1.0;
	w1 = -temp.z * s2;
	w2 = s - temp.y * s2;
	w3 = s - temp.x * s2;
	vertex1 = baseId + ivec2(s, s);
	vertex2 = baseId + ivec2(s, 1.0 - s);
	vertex3 = baseId + ivec2(1.0 - s, s);
}

vec2 hash(ivec2 p) {
	vec2 r = vec2(p) * mat2(127.1, 311.7, 269.5, 183.3);
	return fract(sin(r) * 43758.5453);
}

