#define AA 2

const float pi = 3.1415925;

/*
My changes to iq's original function:

* Return all four roots

* Apply a halley iteration step to each root
to fix uncovered precision issues

*/
// f(x) = (|x| + R - r) - 4R|xy| = 0
vec4 iTorus(vec3 ro, vec3 rd, vec2 tor)
{
    float po = 1.0;

    float Ra2 = tor.x*tor.x;
    float ra2 = tor.y*tor.y;

    float m = dot(ro,ro);
    float n = dot(ro,rd);

    // bounding sphere
    {
	float h = n*n - m + (tor.x+tor.y)*(tor.x+tor.y);
	if( h<0.0 ) return vec4(-1.0);
	//float t = -n-sqrt(h); // could use this to compute intersections from ro+t*rd
    }

	// find quartic equation
    float k = (m - ra2 - Ra2)/2.0;
    float k3 = n;
    float k2 = n*n + Ra2*rd.z*rd.z + k;
    float k1 = k*n + Ra2*ro.z*rd.z;
    float k0 = k*k + Ra2*ro.z*ro.z - Ra2*ra2;

    // prevent |c1| from being too close to zero
    if( abs(k3*(k3*k3 - k2) + k1) < 0.01 )
    {
        po = -1.0;
        float tmp=k1; k1=k3; k3=tmp;
        k0 = 1.0/k0;
        k1 = k1*k0;
        k2 = k2*k0;
        k3 = k3*k0;
    }

    float c2 = 2.0*k2 - 3.0*k3*k3;
    float c1 = k3*(k3*k3 - k2) + k1;
    float c0 = k3*(k3*(-3.0*k3*k3 + 4.0*k2) - 8.0*k1) + 4.0*k0;

    c2 /= 3.0;
    c1 *= 2.0;
    c0 /= 3.0;

    float Q = c2*c2 + c0;
    float R = 3.0*c0*c2 - c2*c2*c2 - c1*c1;

    float h = R*R - Q*Q*Q;
    float z = 0.0;
    if( h < 0.0 )
    {
    	// 4 intersections
        float sQ = sqrt(Q);
        z = 2.0*sQ*cos( acos(R/(sQ*Q)) / 3.0 );
    }
    else
    {
        // 2 intersections
        float sQ = pow( sqrt(h) + abs(R), 1.0/3.0 );
        z = sign(R)*abs( sQ + Q/sQ );
    }		
    z = c2 - z;

    float d1 = z   - 3.0*c2;
    float d2 = z*z - 3.0*c0;
    if( abs(d1) < 1.0e-4 )
    {
        if( d2 < 0.0 ) return vec4(-1.0);
        d2 = sqrt(d2);
    }
    else
    {
        if( d1 < 0.0 ) return vec4(-1.0);
        d1 = sqrt( d1/2.0 );
        d2 = c1/d1;
    }

    //----------------------------------

    vec4 intersections = vec4(1e38);
	int i = 0;

    h = d1*d1 - z + d2;
    if( h > 0.0 )
    {
        h = sqrt(h);

        float t1 = -d1 - h - k3;

        float f = (((t1/4.0 + k3) * t1 + k2) * t1 + 2.0*k1) * t1 + k0;
        float f1 = ((t1 + 3.0 * k3) * t1 + 2.0 * k2) * t1 + 2.0*k1;
        float f2 = (3.0 * t1 + 6.0 * k3) * t1 + 2.0 * k2;
        //t1 -= f / f1; /* Newton iteration */
        t1 -= (2.0*f*f1) / (2.0*f1*f1 - f*f2); /* Halley iteration */

        t1 = (po<0.0)?2.0/t1:t1;

        float t2 = -d1 + h - k3;

        f = (((t2/4.0 + k3) * t2 + k2) * t2 + 2.0*k1) * t2 + k0;
        f1 = ((t2 + 3.0 * k3) * t2 + 2.0 * k2) * t2 + 2.0*k1;
        f2 = (3.0 * t1 + 6.0 * k3) * t1 + 2.0 * k2;
        //t2 -= f / f1; /* Newton iteration */
        t2 -= (2.0*f*f1) / (2.0*f1*f1 - f*f2); /* Halley iteration */

        t2 = (po<0.0)?2.0/t2:t2;

        if( t1 > 0.0 ) intersections[i++] = t1; 
        if( t2 > 0.0 ) intersections[i++] = t2;
    }

    h = d1*d1 - z - d2;
    if( h > 0.0 )
    {
        h = sqrt(h);
        float t1 = d1 - h - k3;

        float f = (((t1/4.0 + k3) * t1 + k2) * t1 + 2.0*k1) * t1 + k0;
        float f1 = ((t1 + 3.0 * k3) * t1 + 2.0 * k2) * t1 + 2.0*k1;
        float f2 = (3.0 * t1 + 6.0 * k3) * t1 + 2.0 * k2;
        //t1 -= f / f1; /* Newton iteration */
        t1 -= (2.0*f*f1) / (2.0*f1*f1 - f*f2); /* Halley iteration */

        t1 = (po<0.0)?2.0/t1:t1;

        float t2 = d1 + h - k3;

        f = (((t2/4.0 + k3) * t2 + k2) * t2 + 2.0*k1) * t2 + k0;
        f1 = ((t2 + 3.0 * k3) * t2 + 2.0 * k2) * t2 + 2.0*k1;
        f2 = (3.0 * t1 + 6.0 * k3) * t1 + 2.0 * k2;
        //t2 -= f / f1; /* Newton iteration */
        t2 -= (2.0*f*f1) / (2.0*f1*f1 - f*f2); /* Halley iteration */

        t2 = (po<0.0)?2.0/t2:t2;

        if( t1 > 0.0 ) intersections[i++] = t1;
        if( t2 > 0.0 ) intersections[i++] = t2;
    }

    return intersections;
}

// df(x)/dx
vec3 nTorus( in vec3 pos, vec2 tor )
{
	return normalize( pos*(dot(pos,pos)- tor.y*tor.y - tor.x*tor.x*vec3(1.0,1.0,-1.0)));
}

// This function just clips the torus to an arc
float iTorusArc(vec3 ro, vec3 rd, vec3 p0, vec3 p1, vec2 tor, bool long_arc){

	float nearest_intersection = 1e38;

	vec4 torus_intersections = iTorus(ro, rd, tor);

	for(int i=0; i<4; i++){
		float t = torus_intersections[i];
		vec3 p = ro + t * rd;

		vec2 center = normalize((p0.xy+p1.xy) / 2.0);
		if(dot(center, normalize(p0.xy)) < dot(center, normalize(p.xy)) ^^ long_arc){
			nearest_intersection = min(nearest_intersection, t);
		}
	}

	if(nearest_intersection > 1e37){
		return -1.0;
	}

	return nearest_intersection;
}

