}

// Ray Domain:
const float tMin = 0.001;
const float tMax = 100.0;

// Precision:
// The amount of times to recursively subdivide the ray. 
const int maxDepth = 20;
// The depth of the quadtree used to search for grid lines.
const int maxGridLineDepth = 10;

// Discontinuity Tolerance:
// If the interval returned by the implicit function over a section of the ray
// has a width bigger than or equal to discontinuityTolerance, that section is
// counted as containing a discontinuity and is not counted as an intersection.
// It can be set to INFINITY if the surface is known to be continous.
const float discontinuityTolerance = 1000.0 * (tMax - tMin) / pow(2.0, float(maxDepth - 1));

// Display Settings:
const float fovY = 50.0;
const bool doVignette = true;
const bool useCubemapAsBackgroundForPBR = true;

const bool renderGridLines = true;
const float gridLineSpacing = 0.4;
const float gridLineWidth = 0.02;

// Rendering Mode:
const int regular = 1;
const int pbr = 2;
const int normalMap = 3;

const int renderingMode = pbr;



//============================================================================//



struct Ray {
    vec3 o;
    vec3 d;
};

// Main Intersection Function:
// Performs a depth-first search of the ray's t domain, descending if the
// current segment possibly contains an intersection. Once the max depth is
// reached, if there's still an intersection in the segment and it is also 
// considered continous, then it is counted as an intersection and returned.
float intersect(Ray r) {
    if (maxDepth < 1) {
        return -1.0;
    }

    float tRange = tMax - tMin;
    int depth = 0;
    int pos = 0;

    while (true) {
        // Calculate the current t domain at this location in the tree
        float size = float(1 << depth);
        iFloat ti = iFloat(
            tMin + tRange * float(pos) / size,
            tMin + tRange * float(pos + 1) / size
        );
        // Find the bounding box of the ray segment and use it to calculate an
        // interval of possible values for f
        iFloat fi = f(
            iAdd(r.o.x, iMul(r.d.x, ti)),
            iAdd(r.o.y, iMul(r.d.y, ti)),
            iAdd(r.o.z, iMul(r.d.z, ti))
        );

        if (fi.u < 0.0 || 0.0 <= fi.l || depth == maxDepth - 1) {
            if (fi.l <= 0.0 && 0.0 <= fi.u && fi.u - fi.l < discontinuityTolerance) {
                // We are at a leaf of the tree and there appears to be an
                // intersection, so return it
                return (ti.l + ti.u) / 2.0;
            }

            // The segment of the ray this node of the tree represents
            // definitely does not contain any intersections, so don't descend
            // into its children and instead move onto the next node in the DFS
            while (pos % 2 == 1) {
                --depth;
                pos /= 2;
            }

            ++pos;

            if (depth == 0 && pos == 1) {
                // We are back at the root of the tree after having searched the
                // entire tree, so break out of the loop
                break;
            }

            continue;
        }

        // Descend the tree to look for possible intersections
        ++depth;
        pos *= 2;
    }

    // There were no intersections
    return -1.0;
}

// Grid Line Intersection Function: 
// too lazy to explain ;)
// why does it have to be so complicated to get constant width grid lines!?!?

const int xAxis = 0;
const int yAxis = 1;
const int zAxis = 2;

bool intersectsGridLine(vec3 p, int axis) {
    if (maxGridLineDepth < 1) {
        return false;
    }

    float t = p.x;
    float u = p.y;
    float v = p.z;
    float w = gridLineWidth;
    float s = gridLineSpacing;
    float uMin = u - w / 2.0;
    float vMin = v - w / 2.0;
    int depth = 0;
    int uPos = 0;
    int vPos = 0;

    while (true) {
        float size = float(1 << depth);
        iFloat ui = iFloat(uMin + w * float(uPos) / size, uMin + w * float(uPos + 1) / size);
        iFloat vi = iFloat(vMin + w * float(vPos) / size, vMin + w * float(vPos + 1) / size);
        iFloat ti = ifloat(s * round(t / s));
        iFloat di = iAdd(iAdd(iSquare(iSub(u, ui)), iSquare(iSub(v, vi))), (ti.l - t) * (ti.l - t));
        iFloat fi;

        switch (axis) {
            case xAxis: fi = f(ti, ui, vi); break;
            case yAxis: fi = f(vi, ti, ui); break;
            case zAxis: fi = f(ui, vi, ti); break;
        }

        if (depth == maxGridLineDepth - 1 && di.u <= w * w / 4.0 && fi.l <= 0.0 && 0.0 <= fi.u) {
            return true;
        }

        if (di.l > w * w / 4.0 || 0.0 < fi.l || fi.u < 0.0 || depth == maxGridLineDepth - 1) {
            while (uPos % 2 == 1 && vPos % 2 == 1) {
                --depth;
                uPos /= 2;
                vPos /= 2;
            }

            if (vPos % 2 == 0) {
                ++vPos;
            } else {
                ++uPos;
                --vPos;
            }

            if (depth == 0 && vPos == 1) {
                break;
            }

            continue;
        }

        ++depth;
        uPos *= 2;
        vPos *= 2;
    }

    return false;
}

bool isPointInGridLine(vec3 p) {
    return renderGridLines && (
        intersectsGridLine(p.xyz, xAxis) ||
        intersectsGridLine(p.yzx, yAxis) || 
        intersectsGridLine(p.zxy, zAxis)
    );
}

vec3 normal(vec3 p) {
    const float e = 1e-3;
    return normalize(vec3(
        f(ifloat(p.x + e), ifloat(p.y), ifloat(p.z)).l - f(ifloat(p.x - e), ifloat(p.y), ifloat(p.z)).l,
        f(ifloat(p.x), ifloat(p.y + e), ifloat(p.z)).l - f(ifloat(p.x), ifloat(p.y - e), ifloat(p.z)).l,
        f(ifloat(p.x), ifloat(p.y), ifloat(p.z + e)).l - f(ifloat(p.x), ifloat(p.y), ifloat(p.z - e)).l
    ));
}

vec3 normal(vec3 rd, vec3 p) {
    vec3 n = normal(p);
    return faceforward(n, rd, n);
    return n;
}



