//-----------------------Keyboard & Mouse Input Handler-----------------------//

// Settings: 
const float MOUSE_SENSITIVITY = 5.0;  
const float MOVEMENT_SPEED = 4.0; // Units per second

// Controls:
const float KEY_W = 87.0;     // forwards
const float KEY_A = 65.0;     // left
const float KEY_S = 83.0;     // backwards
const float KEY_D = 68.0;     // right
const float KEY_UP = 38.0;    // forwards
const float KEY_LEFT = 37.0;  // left
const float KEY_DOWN = 40.0;  // backwards
const float KEY_RIGHT = 39.0; // right
const float KEY_SPACE = 32.0; // up
const float KEY_SHIFT = 16.0; // down
const float KEY_J = 74.0;     // up
const float KEY_N = 78.0;     // down
const float KEY_K = 75.0;     // decrease orbit radius
const float KEY_M = 77.0;     // increase orbit radius



//============================================================================//



bool keyPressed(float keyCode) {
    return texture(iChannel0, vec2((keyCode + 0.5) / 256.0, 0.5 / 3.0)).r > 0.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(0.0, 0.0, 1.0, 1.0);

    if (fragCoord == vec2(0.5, 0.5)) {
        if (iMouse.z > 0.0) {
            vec2 oldCamRot = texelFetch(iChannel1, ivec2(0, 0), 0).zw;
            float mouseSens = -MOUSE_SENSITIVITY / iResolution.x;
            fragColor = vec4(clamp(oldCamRot.x - (iMouse.y - abs(iMouse.w)) * mouseSens, -PI * 0.5, PI * 0.5),
                                   oldCamRot.y + (iMouse.x - abs(iMouse.z)) * mouseSens,
                                   oldCamRot);
        } else {
            vec2 camRot = texelFetch(iChannel1, ivec2(0, 0), 0).xy;
            fragColor = camRot.xyxy;
        }

        if (iFrame == 0 || iMouse.x == 0.0 && iMouse.z == 0.0) {
            fragColor.x = mix(-0.3, 0.1, 0.5 - 0.5 * cos(iTime * 0.5));
            fragColor.y = -0.5 + iTime * 0.3;
        }
    } else if (fragCoord == vec2(1.5, 0.5)) {
        fragColor = texelFetch(iChannel1, ivec2(1, 0), 0);
        float camRotY = texelFetch(iChannel1, ivec2(0, 0), 0).y;
        float c = cos(camRotY);
        float s = sin(camRotY);
        mat3 rotMat = mat3(
                c,   0.0,  -s,
                0.0, 1.0,  0.0,
                s,   0.0,  c
            ) * MOVEMENT_SPEED * iTimeDelta;
        vec3 right = vec3(1.0, 0.0, 0.0);
        vec3 up = vec3(0.0, 1.0, 0.0);
        vec3 forward = vec3(0.0, 0.0, -1.0);

        if (keyPressed(KEY_W) || keyPressed(KEY_UP)) {
            fragColor.xyz += rotMat * forward;
        }
        if (keyPressed(KEY_S) || keyPressed(KEY_DOWN)) {
            fragColor.xyz -= rotMat * forward;
        }

        if (keyPressed(KEY_D) || keyPressed(KEY_RIGHT)) {
            fragColor.xyz += rotMat * right;
        }
        if (keyPressed(KEY_A) || keyPressed(KEY_LEFT)) {
            fragColor.xyz -= rotMat * right;
        }

        if (keyPressed(KEY_SPACE) || keyPressed(KEY_J)) {
            fragColor.xyz += rotMat * up;
        }
        if (keyPressed(KEY_SHIFT) || keyPressed(KEY_N)) {
            fragColor.xyz -= rotMat * up;
        }

        if (keyPressed(KEY_K)) {
            fragColor.w   -= MOVEMENT_SPEED * iTimeDelta;
        }
        if (keyPressed(KEY_M)) {
            fragColor.w   += MOVEMENT_SPEED * iTimeDelta;
        }

        fragColor.w = iFrame == 0 ? 4.0 : max(fragColor.w, 0.0);
    } else if (fragCoord == vec2(0.5, 1.5)) {
        if (keyPressed(KEY_W)     || keyPressed(KEY_A)     || 
            keyPressed(KEY_S)     || keyPressed(KEY_D)     || 
            keyPressed(KEY_UP)    || keyPressed(KEY_LEFT)  || 
            keyPressed(KEY_DOWN)  || keyPressed(KEY_RIGHT) || 
            keyPressed(KEY_SPACE) || keyPressed(KEY_SHIFT) || 
            keyPressed(KEY_J)     || keyPressed(KEY_N)     || 
            keyPressed(KEY_K)     || keyPressed(KEY_M)     || 

            iMouse.z > 0.0) {
            fragColor.x = 1.0;
        } else {
            fragColor.x = texelFetch(iChannel1, ivec2(0, 1), 0).x + 1.0;
        }
    }
}

