    int index = int(floor(b));
    int size = bitSize.x * bitSize.y;

    return (n & ((1 << (size - 1)) >> index)) > 0 ? 1.0 : 0.0;
}

//Returns the pixel at uv in the given bit-packed sprite.
float Sprite(int spr, vec2 uv, vec2 bitSize)
{
    vec2 charSize = bitSize;
    uv = 1.0 - uv;
	uv *= charSize;
    uv = floor(uv);

    //Calculate the bit to extract (x + y * width) (flipped on x-axis)
    float bit = (charSize.x - uv.x - 1.0) + uv.y * charSize.x;

    //Clipping bound to remove garbage outside the sprite's boundaries.
    bool bounds = all(greaterThanEqual(uv,vec2(0)));
    bounds = bounds && all(lessThan(uv,charSize));

    return bounds ? Extract_bit(spr, ivec2(bitSize), bit) : 0.0;
}

//Prints a character and moves the print position forward by 1 character width.
float Char(int ch, vec2 uv, vec2 bitSize)
{
    float px = Sprite(ch, uv, bitSize);
    return px;
}

