// Draw Background : The Rain Of Numbers
// Created by Ernst

float SignTex(int index, vec2 uv)
{
    const float border = 0.2;
 	if (uv.x >= border && uv.x <= 1.0 - border && uv.y >= border && uv.y <= 1.0 - border)
    {
 		if (index == 0)
        {
        	if (abs(uv.x - 0.5) > 0.2 || abs(uv.y - 0.5) > 0.25)
            {
                return 1.0;
            }
        }
        else if (index == 1)
        {
            if (abs(uv.x - 0.5) < 0.08)
            {
                return 1.0;
            }
        }
    }
    return 0.0;
}


vec4 DrawBackground(vec2 fragCoord, float randSeed, float sizeScale)
{
    vec4 col = vec4(0);
    vec2 curSignSize = vec2(SIGN_WIDTH * sizeScale, SIGN_HEIGHT * sizeScale);

    vec2 curFragCoord = fragCoord;
    vec2 originIndex = vec2(floor(curFragCoord.x / curSignSize.x), floor(curFragCoord.y / curSignSize.y));

    vec2 newIndex = originIndex;
    //Rand A Speed
    float rand = Random(vec2(newIndex.x, randSeed), RANDOM_SEED) ;
    curFragCoord.y += SPEED * TIME * (rand + 0.3);

    //Rand Zero Or One
    newIndex.y = floor(curFragCoord.y / (curSignSize.y));
    float signRand = Random(vec2(newIndex.y,newIndex.x), RANDOM_SEED);

    vec2 uv = (curFragCoord - newIndex * curSignSize) / curSignSize;

    //Rand The Length
    int screenHeightCount = int(curFragCoord.y / SCREEN_HEIGHT);
    float relativeY = curFragCoord.y - float(screenHeightCount) * SCREEN_HEIGHT;
    float signLength = SCREEN_HEIGHT * (rand + 0.1);
    if (relativeY < signLength)
    {
        float change = (1.0 - relativeY / signLength);
        float res = SignTex(int(signRand * 10.0) % 2, uv);
        col.g = res * change * pow((change + 0.2), 2.0);
        if (res > 0.0)
        {
            float whiteColor = abs(sin(_Time.y * rand)) * pow(change, 4.0);
            col.rb = vec2(whiteColor);
        }
    }

    return col * float(int(originIndex.x) % 2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    //Draw Background
    vec4 backgroundColor = DrawBackground(fragCoord, 0.0, 0.5) * 0.2;
    backgroundColor += DrawBackground(fragCoord + vec2(SIGN_WIDTH * 0.9, 0.0), 10.0, 1.5);
    fragColor = backgroundColor;
}

