// GlitchWaveJitter For Background
// Draw Team Name : MATRIX
// Draw Company Name : TECH CENTER
// Created by Ernst

#define _WaveJitterSpeed 0.6
#define _WaveJitterDuration 1.0
#define _WaveJitterDensity 0.001
#define _WaveJitterRGBSplit 0.0
#define _WaveJitterFading 0.5
#define _GlitchEllipseData vec4(0.0, 1.0, 0.0, 1.0)
#define _IsUseGlitchEllipseData 0.0
#define _UseShake 1.0

#define UI0 1597334673U
#define UI1 3812015801U
#define UI2 ivec2(UI0, UI1)
#define UI3 ivec3(UI0, UI1, 2798796415U)
#define UIF (1. / float(0xffffffffU))
#define SS(a, b, x) (smoothstep(a, b, x) * smoothstep(b, a, x))

float GetEllipseValue(vec2 uv, vec4 ellipseData)
{
    vec2 center = vec2(0.5f, 0.5f);
    vec2 p2c = 2.0 * (uv - center);
    float distance = length(p2c);

    float insideValue = ellipseData.z;
    float outsideValue = ellipseData.w;
    float power = (distance - ellipseData.x) / (ellipseData.y - ellipseData.x);
    return mix(insideValue, outsideValue, clamp(power, 0.0, 1.0));
}

float RandomNoise(vec2 seed)
{
    return fract(sin(dot(seed, vec2(127.1, 311.7))) * 43758.5453123);
}

vec3 Hash33(vec3 p)
{
    ivec3 q = ivec3(ivec3(p)) * UI3;
    q = (q.x ^ q.y ^ q.z) * UI3;
    return -1. + 2. * vec3(q) * UIF;
}

float Gnoise(vec3 x)
{
    // grid
    vec3 p = floor(x);
    vec3 w = fract(x);

    // quintic interpolant
    vec3 u = w * w * w * (w * (w * 6. - 15.) + 10.);

    // gradients
    vec3 ga = Hash33(p + vec3(0., 0., 0.));
    vec3 gb = Hash33(p + vec3(1., 0., 0.));
    vec3 gc = Hash33(p + vec3(0., 1., 0.));
    vec3 gd = Hash33(p + vec3(1., 1., 0.));
    vec3 ge = Hash33(p + vec3(0., 0., 1.));
    vec3 gf = Hash33(p + vec3(1., 0., 1.));
    vec3 gg = Hash33(p + vec3(0., 1., 1.));
    vec3 gh = Hash33(p + vec3(1., 1., 1.));

    // projections
    float va = dot(ga, w - vec3(0., 0., 0.));
    float vb = dot(gb, w - vec3(1., 0., 0.));
    float vc = dot(gc, w - vec3(0., 1., 0.));
    float vd = dot(gd, w - vec3(1., 1., 0.));
    float ve = dot(ge, w - vec3(0., 0., 1.));
    float vf = dot(gf, w - vec3(1., 0., 1.));
    float vg = dot(gg, w - vec3(0., 1., 1.));
    float vh = dot(gh, w - vec3(1., 1., 1.));

    // interpolation
    float Gnoise = va + u.x * (vb - va) +
        u.y * (vc - va) +
        u.z * (ve - va) +
        u.x * u.y * (va - vb - vc + vd) +
        u.y * u.z * (va - vc - ve + vg) +
        u.z * u.x * (va - vb - ve + vf) +
        u.x * u.y * u.z * (-va + vb + vc - vd + ve - vf - vg + vh);

    return 2. * Gnoise;
}

vec2 uvR;
vec2 uvG;
vec2 uvB;
vec2 uvDefault;

vec4 CopyScreen(vec2 uv)
{
    uvDefault = uvR = uvB = uvG = uv;
    return vec4(texture(iChannel0, uv).xyz, 1.0);
}

vec4 GlitchWaveJitter(in vec4 color, in vec2 uv)
{
    float time = _Time.y * _WaveJitterSpeed;
    float distortionAmount = SS(_WaveJitterDuration * 0.001, _WaveJitterDuration * 0.5, mod(time, _WaveJitterDuration));

	float uvy = uvDefault.y * uv.y * _WaveJitterDensity;
    float distortion = Gnoise(vec3(0.0, uvy * 1.0, time * 500.00)) * (distortionAmount * 3.0 + 0.05);
    distortion *= Gnoise(vec3(0.0, uvy * 2.0, time * 250.0)) * (distortionAmount * 2.0 + 0.025) * _UseShake;
    distortion += smoothstep(0.999, 1.0, sin((uvDefault.y + time * 1.6) * 2.)) * 0.02;
    distortion -= smoothstep(0.999, 1.0, sin((uvDefault.y + time) * 2.0)) * 0.02;
    distortion += smoothstep(0.999, 1.0, sin((uvDefault.y + time * 1.2) * 2.)) * 0.02;
    distortion -= smoothstep(0.999, 1.0, sin((uvDefault.y + time) * 0.8)) * 0.02;

    vec2 uvShift = vec2(distortion, 0);

    uvR += uvShift * (0.5 + _WaveJitterRGBSplit * uvShift * RandomNoise(vec2(5, _Time.x)));
    uvG += uvShift * (0.5 + _WaveJitterRGBSplit * uvShift * RandomNoise(vec2(7, _Time.x)));
    uvB += uvShift * (0.5 + _WaveJitterRGBSplit * uvShift * RandomNoise(vec2(13, _Time.x)));

    vec4 colorR = vec4(texture(iChannel0, uvR).xyz, 1.0);
    vec4 colorG = vec4(texture(iChannel0, uvG).xyz, 1.0);
    vec4 colorB = vec4(texture(iChannel0, uvB).xyz, 1.0);

    vec4 finalColor = vec4(vec3(colorR.x, colorG.y, colorB.z), 1);
    float power = mix(_WaveJitterFading, _WaveJitterFading * GetEllipseValue(uv, _GlitchEllipseData), step(0.5, _IsUseGlitchEllipseData));

    finalColor = mix(color, finalColor, power);

    return finalColor;
}

