}

float fbm (vec3 seed)
{
    float result = 0.;
    float a = .5;
    for (int i = 0; i < 4; ++i) {
        seed += result * .5;
        result += gyroid(seed/a)*a;
        a /= 2.;
    }
    return result;
}

