// inspired by "Path with max sum" by Envy24. https://shadertoy.com/view/7lGfz1
// redone with buffer (and thus progressive )

#define n 10 // number of paths

void mainImage(out vec4 O, vec2 U)
{
    U = floor(N*U/R.y);
    vec2 P[n];
    for (int i=0; i<n; i++) P[i][i%2] += float(i*5);
    O-=O;
    for (float i = 0.; i < N+N*R.x/R.y -2. ; i++ ) {
        for (int i=0; i<n; i++) 
            P[i][ T(P[i]+vec2(1,0)).g < T(P[i]+vec2(0,1)).g ? 1 : 0 ] ++,
            P[i]==U ?  O += .6 + .6 * cos( 6.3*float(i)/float(n) + vec4(0,23,21,0) ) : O;
      }    
   if (O!=O-O) return;
   O = iMouse.z > 0. 
          ? .5+.5*sin(T(U).gggg) // T(U).gggg / (N*length(R)/R.y)
          : T(U).rrrr;

// O.b *= float( max(U.x,U.y) < float(iFrame%60) );
}

