// precompute max sum field array ( opposite of distance field )
// .r = randow value
// .g = local max sum field

void mainImage( out vec4 O, vec2 U )
{
    if ( U.y > N || U.x > N*R.x/R.y ) return;  // only compute at resolution N

    U = floor(U);
    if (iFrame%120 < 1 ) { O*=0.; O.r = fract(1e4*sin(dot(U,R-57.)-iTime)); return; } // init

    O = T(U);                        // previous state
    O.g = O.r;

#if 0                                // purely local computations
    float l = U.x>0. ? T(U-vec2(1,0)).g : 0., 
          b = U.y>0. ? T(U-vec2(0,1)).g : 0.;
    O.g += U.x==0. ? b
         : U.y==0. ? l
         :           max(l,b);   
#else                                // boost(?) progression by directly computing borders
         if (U.x==0.) for (float y=0.; y < U.y; y++ ) O.g += T(U+vec2(0,y)).r;
    else if (U.y==0.) for (float x=0.; x < U.x; x++ ) O.g += T(U+vec2(x,0)).r;
    else O.g += max( T(U-vec2(1,0)).g, T(U-vec2(0,1)).g ); // progressively computing the max sum field
#endif
}

