// Copyright  2022 IWBTShyGuy
// Attribution 4.0 International (CC BY 4.0)

// Hash without Sine https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p) {
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float noise12(vec2 p) {
    vec2 t = fract(p);
    p = floor(p) + vec2(1.365, -0.593);
    vec2 e = vec2(0, 1);
    return mix(
        mix(hash12(p + e.xx), hash12(p + e.yx), t.x),
        mix(hash12(p + e.yx), hash12(p + e.yy), t.x),
        t.y
    );
}

void mainImage(out vec4 O, in vec2 U) {
    O = vec4(0.95, 0.95, 0.9, 1);
    vec2 r = iResolution.xy, m = vec2(0), M = r, p = m, k, l, e = vec2(0, 1);
    float a;
    for (int i = 0; i < int(log2(length(r))/1.2); i++) {
        a = noise12(p + iTime * 0.1);
        if (a > 0.1 * dot(r, e) / dot(r, e.yx)) e = 1.0 - e;
        a = 0.35 + 0.3 * hash12(p + r * sign(U - p));
        p += (m * (1.0 - a) + M * a - p) * e;
        if (abs(dot(p - U, e)) < 2.0) {
            O.xyz *= 0.0;
            return;
        }
        k = clamp(sign(U - p), 0.0, 1.0);
        l = clamp(sign(p - U), 0.0, 1.0);
        m += (max(k * p, m) - m) * e;
        M += (min(k * r + l * p, M) - M) * e;
    }
    a = noise12(p + sign(U - p) * r + iTime * 0.25);
    if (0.3 <= a && a < 0.4) O = vec4(0.9, 0, 0.2, 1);
    else if (0.4 <= a && a < 0.5) O = vec4(0.9, 0.9, 0, 1);
    else if (0.5 <= a && a < 0.6) O = vec4(0, 0.2, 1, 1);
}

#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

// RayMarching from TheArtOfCode

// From BlackleMori
#define FK(k) floatBitsToInt(k*k/7.)^floatBitsToInt(k)
float hash(float a, float b) {
    int x = FK(a), y = FK(b);
    return float((x*x+y)*(y*y-x)-x)/2.14e9;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

vec3 face(vec3 p) {
     vec3 a = abs(p);
     return step(a.yzx, a.xyz)*step(a.zxy, a.xyz)*sign(p);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdBox(in vec2 p, in vec2 b){
    vec2 d = abs(p)-b;
    return length(max(d,0.))+min(max(d.x,d.y),0.);
}

vec3 GetRayOrigin() {
    vec2 m = iMouse.xy/iResolution.xy;
    float r = 4.;
    float a = 0.125 * iTime;
    vec3 ro = vec3(r * cos(a), 0, r * sin(a));
    //ro.yz *= rot(-m.y*3.14+1.);
    //ro.xz *= rot(-m.x*6.2831);
    return ro;
}

float GetDist(vec3 p) {
    float sd = length(p) - 0.5;
    for(int j=0; j<16; j++){
        float io = 2. * pi * float(j) / 16.;
        vec3 q =normalize(p.yzx);
        //p.xy *= rot(p.z + 0.25 * iTime);
        p.xz = abs(p.zx * rot(0.25 * iTime + io) - 0.25 * abs(p.z-p.x))-0.0;
        p.yz = abs(p.yz * rot(0.25 * iTime + io) + .25 * abs(p.z - p.y))-0.01;
      // p.y += 0.01;
      // p.x -= 0.01;
      // p.z -= 0.01;
    }
    return max(sd, 0.1 * abs(p.x + p.y + p.z) - 0.01);
   // return max(0., length(p.yxz)/10. - 0.01);
}

float RayMarch(vec3 ro, vec3 rd, float z) {

    float dO=0.;
    float s = sign(z);
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if (s != sign(dS)) { z *= 0.5; s = sign(dS); }
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = GetRayOrigin();

    vec3 rd = GetRayDir(uv, ro, vec3(0), 1. + 5. * tanh(0.25 * iTime));
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    vec3 p = ro + rd * d;
    float IOR = 1.05;
    if(d<MAX_DIST) {        
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        vec3 pIn = p - 4. * SURF_DIST * n;
        vec3 rdIn = refract(rd, n, 1./IOR);
        float dIn = RayMarch(pIn, rdIn, -1.);

        vec3 pExit = pIn + dIn * rdIn;
        vec3 nExit = -GetNormal(pExit); // *-1.; ?

        float dif = dot(n, normalize(p))*.5+.5;
        float dif2 = dot(n, normalize(abs(r-p)))*.5+.5;
        col = vec3(dif);
       // col *= clamp(0.25 + p.y, 0., 1.);
        float fres = pow(1. + dot(rd, n), 4.);
        float fog = 1.-exp(-length(p));

        float spec = pow(dif, 32.);
        float spec2 = pow(dif2, 1.);

        col *= 0.5 + 0.5 * n;
        col *= pal(n.y * 0.5 + 0.1 * iTime, vec3(0,1,2)/3.);
        col *= 1. + 0.75 * n.y;


        col += spec;
        //col = vec3(spec);
        col = pal(dif + p.y * 0.5 + 0.5 * iTime, 0.25 * vec3(2,1,1));
        col += spec;
        col += p.y -0.0;

        //col = sqrt(col);
        float th = .6 + .4 * thc(8., 12. * log(length(p)) - 1.5 * pi * iTime);

       col *= th *exp(-12. * abs(fract(log(length(p))-1. * iTime)-0.5)) * abs(n.x + n.y + n.z);
       //col *= exp(-4. * length(p)) * abs(n.y);
        col *= abs(n.x + n.y + n.z) ;
    }


    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

