}

#define pi 3.141592

void mainImage(out vec4 fragColor,in vec2 fragCoord){
  vec2 uv = (2.0 * fragCoord-iResolution.xy) / iResolution.y;
  vec3 col = vec3(0.0);

  float sz = 120.0;
  uv = floor(uv * sz) / sz;

  float m = sin(iTime*3.0)*0.05;
  vec2 tp = uv - vec2(sin(iTime*2.0)*0.05,cos(iTime*10.0)*0.025);
  tp *= mat2(cos(m),-sin(m),sin(m),cos(m));
  float s = 0.1;
  float sdf = max(abs(tp.x*2.0),abs(tp.y));
  vec2 p = vec2(atan(tp.y,tp.x)/pi,1.0/sdf);
  float fog = clamp(0.5/p.y,0.0,1.0);
  p.y += iTime;

  col += textureLod(iChannel1,p,0.0).rgb;
  if(p.x < 0.65 && p.x > 0.35)
    col += textureLod(iChannel2,p,0.0).rgb;
  if(p.x < -0.3 && p.x > -0.7)
    col += textureLod(iChannel0,p,0.0).rgb;

  col *= fog;

  {
  vec2 p = abs(tp);
  vec2 p1 = vec2(0.0);
  vec2 p2 = vec2(1.0,2.0);
  float a = length(p1 - p2);
  float b = length(p1 - p);
  float line = length(mix(p1,p2,clamp(b/a,0.0,1.0)) - p);
  col *= clamp(line*5.0+0.5,0.0,1.0);
  }

  {
  vec2 p = tp; p.x = abs(p.x);
  vec2 p1 = vec2(0.0);
  vec2 p2 = vec2(1.0,-1.45);
  float a = length(p1 - p2);
  float b = length(p1 - p);
  float line = length(mix(p1,p2,clamp(b/a,0.0,1.0)) - p);
  col *= clamp(line*5.0+0.5,0.0,1.0);
  }

  {
    vec2 p = abs(uv) * 2.5;
    p.xy = p.y>p.x ? p.yx : p.xy;
    float d = length(max(abs(p)-vec2(0.04,0.0),0.0));
    col += vec3(0,1,0) * (0.015 / d);
  }

  col += vec3(0,1,0) * 0.045;
  col *= 1.0 - length((fragCoord / iResolution.xy) - 0.5);

  col = floor(col * 32.0) / 32.0;

  fragColor = vec4(col,1.0);
