    //    iMul(0.6, iAdd(iMul(6.0, sinU), iMul(iMul(iMul(iMul(0.5, r), sinU), cosV), iExp(iNeg(iPow(iSub(u, 1.5 * PI), 2.0)))))),
    //    iMul(0.6, iMul(r, sinV))
    //);

    // Torus
    //return iVec3(
    //    iMul(iAdd(2.0, cosU), cosV),
    //    sinU,
    //    iMul(iAdd(2.0, cosU), sinV)
    //);

    // Sphere
    //return iVec3(
    //    iMul(3.0, iMul(sinU, cosV)),
    //    iMul(3.0, iMul(sinU, sinV)),
    //    iMul(3.0, cosU)
    //);
}

// Resolution:
// The amount of times to recursively subdivide the u-v domain. 
const int maxDepth = 16;

// Discontinuity Tolerance:
// If the bounding box for a leaf surface patch has a side length bigger than or
// equal to discontinuityTolerance, that surface patch is counted as containing
// a discontinuity and is not counted as an intersection. It can be set to
// INFINITY if the surface is known to be continous.
const float discontinuityTolerance = 1000.0 * max(vMax - vMin, uMax - uMin) / pow(2.0, float(maxDepth - 1));

// Display Settings:
const float fovY = 50.0;
#define VIGNETTE 1
#define USE_CUBEMAP_BACKGROUND_FOR_PBR 1

// Rendering Mode:
#define REGULAR 1
#define PBR 2
#define NORMAL_MAP 3

#define RENDERING_MODE PBR



//============================================================================//



struct Ray {
    vec3 o;
    vec3 d;
};

iFloat intersect(Ray r, iVec3 a) {
    // Basically a ray-AABB intersection test but returns both the min and max
    // intersection t-values.
    vec3  t0 = (vec3(a.x.l, a.y.l, a.z.l) - r.o) / r.d;
    vec3  t1 = (vec3(a.x.u, a.y.u, a.z.u) - r.o) / r.d;
    float t;

    if (t0.x > t1.x) {
        t    = t0.x;
        t0.x = t1.x;
        t1.x = t;
    }

    if (t0.y > t1.y) {
        t    = t0.y;
        t0.y = t1.y;
        t1.y = t;
    }

    if (t0.z > t1.z) {
        t    = t0.z;
        t0.z = t1.z;
        t1.z = t;
    }

    float h0 = max(t0.x, max(t0.y, t0.z));
    float h1 = min(t1.x, min(t1.y, t1.z));

    if (h0 > h1) {
        return EMPTY_SET;
    }

    return iFloat(h0, h1);
}

// Main Intersection Function:
// Performs a depth-first search of the u-v domain treating it as a quadtree,
// keeping track of the closest intersection found so far and only descending
// the quadtree if it possibly contains a closer intersection. Every time a leaf
// patch is reached and there might be an intersection within it, it is counted
// as an intersection.
bool intersect(Ray r, out float t, out float u, out float v) {
    if (maxDepth < 1) {
        return false;
    }

    float uRange = uMax - uMin;
    float vRange = vMax - vMin;
    int depth = 0;
    int uPos = 0;
    int vPos = 0;
    t = INFINITY;

    while (true) {
        // Calculate the current u-v domain at this location in the quadtree
        float size = float(1 << depth);
        iFloat ui = iFloat(uMin + uRange * float(uPos) / size, uMin + uRange * float(uPos + 1) / size);
        iFloat vi = iFloat(vMin + vRange * float(vPos) / size, vMin + vRange * float(vPos + 1) / size);
        // Calculate a bounding box for the surface patch
        iVec3 fi = f(ui, vi);
        // Intersect the bounding box
        iFloat ti = intersect(r, fi);

        // If the ray has intersected the bounding box and currently at a leaf
        // patch which doesn't contain a discontinuity, check if this
        // intersection value is closer than the current best intersection and
        // update it if so.
        if (depth == maxDepth - 1 && 0.0 < ti.l && ti.u < t
            && fi.x.u - fi.x.l < discontinuityTolerance
            && fi.y.u - fi.y.l < discontinuityTolerance
            && fi.z.u - fi.z.l < discontinuityTolerance) {
            t = ti.u;
            u = (ui.l + ui.u) / 2.0;
            v = (vi.l + vi.u) / 2.0;
        }

        // If the ray didn't intersect the patch or currently at a leaf patch,
        // don't descend the quadtree any further, instead go to the next patch 
        // in the depth-first search and continue on to the next iteration.
        if (ti.u <= 0.0 || t <= ti.l || depth == maxDepth - 1) {
            while (uPos % 2 == 1 && vPos % 2 == 1) {
                --depth;
                uPos /= 2;
                vPos /= 2;
            }

            if (vPos % 2 == 0) {
                ++vPos;
            } else {
                ++uPos;
                --vPos;
            }

            // Exit the loop if back at root node
            if (depth == 0 && vPos == 1) {
                break;
            }

            continue;
        }

        // There is a potential intersection in this surface patch, so descend
        // and check its children for intersections.
        ++depth;
        uPos *= 2;
        vPos *= 2;
    }

    return t != INFINITY;
}

vec3 normal(float u, float v) {
    const float e = 1e-3;
    iVec3 fup = f(ifloat(u + e), ifloat(v));
    iVec3 fun = f(ifloat(u - e), ifloat(v));
    iVec3 fvp = f(ifloat(u), ifloat(v + e));
    iVec3 fvn = f(ifloat(u), ifloat(v - e));

    return normalize(cross(
        vec3(fup.x.l - fun.x.l, fup.y.l - fun.y.l, fup.z.l - fun.z.l),
        vec3(fvp.x.l - fvn.x.l, fvp.y.l - fvn.y.l, fvp.z.l - fvn.z.l)
    ));
}

vec3 normal(Ray r, float u, float v) {
    vec3 n = normal(u, v);
    return faceforward(n, r.d, n);
    return n;
}



